/**
 * yotas - Location-based Review App
 * @format
 */

import React from 'react';
import { StatusBar, StyleSheet, useColorScheme, View } from 'react-native';
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import AppNavigator from './src/navigation/AppNavigator';

const queryClient = new QueryClient();

function App() {
  console.log('App: „É¨„É≥„ÉÄ„É™„É≥„Ç∞‰∏≠');
  const isDarkMode = useColorScheme() === 'dark';

  return (
    <QueryClientProvider client={queryClient}>
      <View style={styles.container}>
        <StatusBar barStyle={isDarkMode ? 'light-content' : 'dark-content'} />
        <AppNavigator />
      </View>
    </QueryClientProvider>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
});

export default App;

// React Native Testing Library setup
// Note: extend-expect is included by default in newer versions

// Firebase mocks„ÇíËøΩÂä†
jest.mock('@react-native-firebase/app', () => ({
  utils: () => ({
    FilePath: {
      PICTURES_DIRECTORY: '/tmp/pictures',
    },
  }),
}));

jest.mock('@react-native-firebase/firestore', () => ({
  __esModule: true,
  default: jest.fn(() => ({
    collection: jest.fn(() => ({
      doc: jest.fn(() => ({
        set: jest.fn(() => Promise.resolve()),
        get: jest.fn(() => Promise.resolve({ exists: false })),
        update: jest.fn(() => Promise.resolve()),
        delete: jest.fn(() => Promise.resolve()),
      })),
      add: jest.fn(() => Promise.resolve({ id: 'mock-id' })),
      where: jest.fn(() => ({
        get: jest.fn(() => Promise.resolve({ empty: true, docs: [] })),
        orderBy: jest.fn(() => ({
          limit: jest.fn(() => ({
            get: jest.fn(() => Promise.resolve({ empty: true, docs: [] })),
          })),
        })),
      })),
    })),
  })),
}));

jest.mock('@react-native-firebase/auth', () => ({
  __esModule: true,
  default: jest.fn(() => ({
    currentUser: null,
    signInWithEmailAndPassword: jest.fn(() => Promise.resolve()),
    createUserWithEmailAndPassword: jest.fn(() => Promise.resolve()),
    signOut: jest.fn(() => Promise.resolve()),
  })),
}));

jest.mock('@react-native-firebase/storage', () => ({
  __esModule: true,
  default: jest.fn(() => ({
    ref: jest.fn(() => ({
      putFile: jest.fn(() => Promise.resolve()),
      getDownloadURL: jest.fn(() => Promise.resolve('https://example.com/image.jpg')),
    })),
  })),
}));

// React Navigation mocks
jest.mock('@react-navigation/native', () => ({
  useNavigation: () => ({
    navigate: jest.fn(),
    goBack: jest.fn(),
    reset: jest.fn(),
  }),
  useRoute: () => ({
    params: {},
  }),
  NavigationContainer: ({ children }) => children,
}));

// SafeAreaProvider mock
jest.mock('react-native-safe-area-context', () => ({
  SafeAreaProvider: ({ children }) => children,
  useSafeAreaInsets: () => ({ top: 0, bottom: 0, left: 0, right: 0 }),
}));

// React Native Vector Icons mock
jest.mock('react-native-vector-icons/Ionicons', () => {
  const Icon = ({ name, ...props }) => {
    const React = require('react');
    const { Text } = require('react-native');
    return React.createElement(Text, { ...props }, `Icon-${name}`);
  };
  return Icon;
});

// React Native Async Storage mock
jest.mock('@react-native-async-storage/async-storage', () => ({
  setItem: jest.fn(() => Promise.resolve()),
  getItem: jest.fn(() => Promise.resolve(null)),
  removeItem: jest.fn(() => Promise.resolve()),
  clear: jest.fn(() => Promise.resolve()),
}));

// Alert mock - use a more targeted approach
jest.mock('react-native/Libraries/Alert/Alert', () => ({
  alert: jest.fn(),
  prompt: jest.fn(),
}));

// Modal mock - React Native Modal doesn't render children in tests by default
jest.mock('react-native/Libraries/Modal/Modal', () => {
  const mockModal = ({ children, visible }) => {
    if (visible) {
      return children;
    }
    return null;
  };
  return mockModal;
});

// Console warning suppression for tests
global.console = {
  ...console,
  warn: jest.fn(),
  error: jest.fn(),
};

const { getDefaultConfig, mergeConfig } = require('@react-native/metro-config');

/**
 * Metro configuration
 * https://reactnative.dev/docs/metro
 *
 * @type {import('@react-native/metro-config').MetroConfig}
 */
const config = {};

module.exports = mergeConfig(getDefaultConfig(__dirname), config);

import React from 'react';
import { Alert } from 'react-native';
import { render, fireEvent, waitFor, mockUser, mockToilet } from '../../test-utils';

// Mock Alert
jest.mock('react-native/Libraries/Alert/Alert', () => ({
  alert: jest.fn(),
  prompt: jest.fn(),
}));
import ReportModal from '../../components/report/ReportModal';
import ReportHistoryScreen from '../../screens/report/ReportHistoryScreen';
import { ReportTargetType, ReportReason } from '../../types/post';

// Mock functions defined first
const mockSubmitReport = jest.fn();
const mockGetUserReports = jest.fn();
const mockGetUserRestrictions = jest.fn();
const mockGetUserViolationPoints = jest.fn();

// Mock dependencies
jest.mock('../../stores/authStore', () => ({
  useAuth: () => ({ user: mockUser }),
}));

// Create a mock hook that can be updated per test
const mockUseReportHook: any = {
  submitReport: mockSubmitReport,
  reports: [],
  userRestrictions: [],
  violationPoints: 0,
  isLoading: false,
  isSubmitting: false,
  error: null,
  getReasonDisplayName: (reason: any) => {
    const names: Record<string, string> = {
      inappropriate_content: '‰∏çÈÅ©Âàá„Å™„Ç≥„É≥„ÉÜ„É≥„ÉÑ',
      spam: '„Çπ„Éë„É†',
      harassment: 'Â´å„Åå„Çâ„Åõ',
      fake_information: 'ËôöÂÅΩÊÉÖÂ†±',
      copyright_violation: 'Ëëó‰ΩúÊ®©‰æµÂÆ≥',
      privacy_violation: '„Éó„É©„Ç§„Éê„Ç∑„Éº‰æµÂÆ≥',
      commercial_spam: 'ÂïÜÊ•≠ÁöÑ„Çπ„Éë„É†',
      hate_speech: '„Éò„Ç§„Éà„Çπ„Éî„Éº„ÉÅ',
      other: '„Åù„ÅÆ‰ªñ',
    };
    return names[reason] || reason;
  },
  getStatusDisplayName: (status: any) => {
    const names: Record<string, string> = {
      pending: 'ÂØ©ÊüªÂæÖ„Å°',
      under_review: 'ÂØ©Êüª‰∏≠',
      resolved: 'Ëß£Ê±∫Ê∏à„Åø',
      dismissed: 'Âç¥‰∏ã',
      auto_resolved: 'Ëá™ÂãïËß£Ê±∫',
    };
    return names[status] || status;
  },
  getStatusColor: () => '#FF9800',
  hasActiveRestrictions: () => false,
  getActiveRestrictionTypes: () => [],
  isNearViolationLimit: () => false,
  refresh: jest.fn(),
  reportCount: 0,
  activeRestrictionCount: 0,
  isHighRiskUser: false,
};

jest.mock('../../hooks/useReport', () => ({
  useReport: () => mockUseReportHook,
}));

jest.mock('../../services/reportService', () => ({
  reportService: {
    submitReport: mockSubmitReport,
    getUserReports: mockGetUserReports,
    getUserRestrictions: mockGetUserRestrictions,
    getUserViolationPoints: mockGetUserViolationPoints,
    isUserRestricted: jest.fn().mockResolvedValue({ restricted: false }),
  },
}));

describe('Report Flow Integration', () => {
  beforeEach(() => {
    jest.clearAllMocks();
    mockSubmitReport.mockResolvedValue(undefined);
    mockGetUserReports.mockResolvedValue([]);
    mockGetUserRestrictions.mockResolvedValue([]);
    mockGetUserViolationPoints.mockResolvedValue(0);

    // Reset mock hook data
    mockUseReportHook.reports = [];
    mockUseReportHook.userRestrictions = [];
    mockUseReportHook.violationPoints = 0;
    mockUseReportHook.isLoading = false;
    mockUseReportHook.isSubmitting = false;
    mockUseReportHook.error = null;
    mockUseReportHook.hasActiveRestrictions = () => false;
    mockUseReportHook.isNearViolationLimit = () => false;
  });

  describe('Report Submission Flow', () => {
    it('should complete full report submission flow', async () => {
      const onClose = jest.fn();

      const { getByText, getByPlaceholderText } = render(
        <ReportModal
          visible={true}
          onClose={onClose}
          targetType={ReportTargetType.TOILET}
          targetId={mockToilet.id}
          targetTitle={mockToilet.title}
        />,
      );

      // Step 1: Select report reason
      const spamOption = getByText('„Çπ„Éë„É†');
      fireEvent.press(spamOption);

      // Should navigate to details step
      await waitFor(() => {
        expect(getByText('Ë©≥Á¥∞ÊÉÖÂ†±Ôºà‰ªªÊÑèÔºâ')).toBeTruthy();
      });

      // Step 2: Add description
      const descriptionInput = getByPlaceholderText('ÂïèÈ°å„ÅÆË©≥Á¥∞„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑÔºà‰ªªÊÑèÔºâ');
      fireEvent.changeText(descriptionInput, 'This is spam content');

      // Press next
      const nextButton = getByText('Ê¨°„Å∏');
      fireEvent.press(nextButton);

      // Step 3: Confirmation
      await waitFor(() => {
        expect(getByText('ÈÄöÂ†±ÂÜÖÂÆπ„ÅÆÁ¢∫Ë™ç')).toBeTruthy();
      });

      // Verify report details are shown
      expect(getByText('ÁêÜÁî±:')).toBeTruthy();
      expect(getByText('„Çπ„Éë„É†')).toBeTruthy();
      expect(getByText('Ë©≥Á¥∞:')).toBeTruthy();
      expect(getByText('This is spam content')).toBeTruthy();

      // Submit report
      const submitButton = getByText('ÈÄöÂ†±„ÇíÈÄÅ‰ø°');
      fireEvent.press(submitButton);

      await waitFor(() => {
        expect(mockSubmitReport).toHaveBeenCalledWith(
          mockUser.uid,
          ReportTargetType.TOILET,
          mockToilet.id,
          ReportReason.SPAM,
          'This is spam content',
          undefined,
        );
      });

      // Verify success alert
      expect(Alert.alert).toHaveBeenCalledWith(
        'ÈÄöÂ†±ÂÆå‰∫Ü',
        'ÈÄöÂ†±„ÇíÂèó„Åë‰ªò„Åë„Åæ„Åó„Åü„ÄÇÁ¢∫Ë™çÂæå„ÄÅÈÅ©Âàá„Å™ÂØæÂøú„ÇíË°å„ÅÑ„Åæ„Åô„ÄÇ',
        [{ text: 'OK' }],
      );

      // Modal should close
      expect(onClose).toHaveBeenCalled();
    });

    it('should handle report submission without description', async () => {
      const onClose = jest.fn();

      const { getByText } = render(
        <ReportModal
          visible={true}
          onClose={onClose}
          targetType={ReportTargetType.TOILET}
          targetId={mockToilet.id}
          targetTitle={mockToilet.title}
        />,
      );

      // Select reason
      const harassmentOption = getByText('Â´å„Åå„Çâ„Åõ');
      fireEvent.press(harassmentOption);

      // Skip description and go to next
      await waitFor(() => {
        const nextButton = getByText('Ê¨°„Å∏');
        fireEvent.press(nextButton);
      });

      // Submit without description
      await waitFor(() => {
        const submitButton = getByText('ÈÄöÂ†±„ÇíÈÄÅ‰ø°');
        fireEvent.press(submitButton);
      });

      await waitFor(() => {
        expect(mockSubmitReport).toHaveBeenCalledWith(
          mockUser.uid,
          ReportTargetType.TOILET,
          mockToilet.id,
          ReportReason.HARASSMENT,
          undefined,
          undefined,
        );
      });
    });

    it('should handle report submission error', async () => {
      mockSubmitReport.mockRejectedValueOnce(new Error('Network error'));
      const onClose = jest.fn();

      const { getByText } = render(
        <ReportModal
          visible={true}
          onClose={onClose}
          targetType={ReportTargetType.TOILET}
          targetId={mockToilet.id}
        />,
      );

      // Quick submission
      fireEvent.press(getByText('„Åù„ÅÆ‰ªñ'));
      await waitFor(() => fireEvent.press(getByText('Ê¨°„Å∏')));
      await waitFor(() => fireEvent.press(getByText('ÈÄöÂ†±„ÇíÈÄÅ‰ø°')));

      await waitFor(() => {
        expect(Alert.alert).toHaveBeenCalledWith('„Ç®„É©„Éº', 'ÈÄöÂ†±„ÅÆÈÄÅ‰ø°„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
      });

      // Modal should not close on error
      expect(onClose).not.toHaveBeenCalled();
    });
  });

  describe('Report History Display', () => {
    it('should display user reports in history', async () => {
      const mockReports = [
        {
          id: 'report-1',
          reporterId: mockUser.uid,
          targetType: ReportTargetType.TOILET,
          targetId: 'toilet-1',
          reason: ReportReason.SPAM,
          description: 'Spam content',
          status: 'pending' as const,
          createdAt: new Date('2024-01-01'),
        },
        {
          id: 'report-2',
          reporterId: mockUser.uid,
          targetType: ReportTargetType.USER,
          targetId: 'user-456',
          reason: ReportReason.HARASSMENT,
          status: 'resolved' as const,
          createdAt: new Date('2024-01-02'),
          resolution: 'Action taken against user',
        },
      ];

      // Update mock hook with test data
      mockUseReportHook.reports = mockReports;

      const { getByText } = render(<ReportHistoryScreen />);

      await waitFor(() => {
        expect(getByText('ÈÄöÂ†±Áµ±Ë®à')).toBeTruthy();
        expect(getByText('2')).toBeTruthy(); // Total reports
        expect(getByText('Á∑èÈÄöÂ†±Êï∞')).toBeTruthy();
      });

      // Check report items
      expect(getByText('ÁêÜÁî±: „Çπ„Éë„É†')).toBeTruthy();
      expect(getByText('ÁêÜÁî±: Â´å„Åå„Çâ„Åõ')).toBeTruthy();
      expect(getByText('ÂØ©ÊüªÂæÖ„Å°')).toBeTruthy();
      expect(getByText('Ëß£Ê±∫Ê∏à„Åø')).toBeTruthy();
    });

    it('should display user restrictions', async () => {
      const mockRestrictions = [
        {
          id: 'restriction-1',
          userId: mockUser.uid,
          type: 'post_restriction' as const,
          reason: 'Ë§áÊï∞„ÅÆÈÅïÂèç„Å´„Çà„ÇäÊäïÁ®ø„ÅåÂà∂Èôê„Åï„Çå„Å¶„ÅÑ„Åæ„Åô',
          startDate: new Date(),
          endDate: new Date(Date.now() + 86400000), // 1 day later
          isActive: true,
          createdBy: 'system' as const,
        },
      ];

      // Update mock hook with test data
      mockUseReportHook.userRestrictions = mockRestrictions;
      mockUseReportHook.violationPoints = 15;
      mockUseReportHook.hasActiveRestrictions = () => true;
      mockUseReportHook.isNearViolationLimit = () => true;

      const { getByText } = render(<ReportHistoryScreen />);

      await waitFor(() => {
        // Violation points
        expect(getByText('ÈÅïÂèç„Éù„Ç§„É≥„Éà')).toBeTruthy();
        expect(getByText('15 / 20')).toBeTruthy();

        // Warning message
        expect(getByText(/ÈÅïÂèç„Éù„Ç§„É≥„Éà„ÅåÈ´ò„Åè„Å™„Å£„Å¶„ÅÑ„Åæ„Åô/)).toBeTruthy();

        // Active restrictions
        expect(getByText('Âà©Áî®Âà∂Èôê')).toBeTruthy();
        expect(getByText('ÊäïÁ®øÂà∂Èôê')).toBeTruthy();
        expect(getByText('Ë§áÊï∞„ÅÆÈÅïÂèç„Å´„Çà„ÇäÊäïÁ®ø„ÅåÂà∂Èôê„Åï„Çå„Å¶„ÅÑ„Åæ„Åô')).toBeTruthy();
      });
    });

    it('should handle report detail press', async () => {
      const mockReports = [
        {
          id: 'report-1',
          reporterId: mockUser.uid,
          targetType: ReportTargetType.TOILET,
          targetId: 'toilet-1',
          reason: ReportReason.FAKE_INFORMATION,
          description: 'Contains false information',
          status: 'under_review' as const,
          createdAt: new Date('2024-01-01'),
        },
      ];

      // Update mock hook with test data
      mockUseReportHook.reports = mockReports;

      const { getByText } = render(<ReportHistoryScreen />);

      await waitFor(() => {
        const reportItem = getByText('ÁêÜÁî±: ËôöÂÅΩÊÉÖÂ†±');
        fireEvent.press(reportItem.parent?.parent as any);
      });

      // Should show alert with details
      expect(Alert.alert).toHaveBeenCalledWith(
        'ÈÄöÂ†±Ë©≥Á¥∞',
        expect.stringContaining('ID: report-1'),
        [{ text: 'OK' }],
      );
    });
  });

  describe('Report Flow with Restrictions', () => {
    it('should prevent report submission when user is restricted', async () => {
      // Mock user has report restriction
      const mockIsUserRestricted = jest.fn().mockResolvedValue({
        restricted: true,
        reason: '„ÅÇ„Å™„Åü„ÅØÈÄöÂ†±Ê©üËÉΩ„ÅÆ‰ΩøÁî®„ÅåÂà∂Èôê„Åï„Çå„Å¶„ÅÑ„Åæ„Åô',
        endDate: new Date(Date.now() + 86400000),
      });

      jest.doMock('../../services/reportService', () => ({
        reportService: {
          isUserRestricted: mockIsUserRestricted,
        },
      }));

      // This would typically be integrated with the actual flow
      // For now, we're testing the concept
      const restriction = await mockIsUserRestricted(mockUser.uid, 'report');

      expect(restriction.restricted).toBe(true);
      expect(restriction.reason).toBeTruthy();
    });
  });

  describe('Report Statistics Update', () => {
    it('should update statistics after successful report', async () => {
      const onClose = jest.fn();

      const { getByText, rerender } = render(
        <ReportModal
          visible={true}
          onClose={onClose}
          targetType={ReportTargetType.TOILET}
          targetId={mockToilet.id}
        />,
      );

      // Submit a report
      fireEvent.press(getByText('„Çπ„Éë„É†'));
      await waitFor(() => fireEvent.press(getByText('Ê¨°„Å∏')));
      await waitFor(() => fireEvent.press(getByText('ÈÄöÂ†±„ÇíÈÄÅ‰ø°')));

      // Update mock with new report after submission
      mockUseReportHook.reports = [
        {
          id: 'new-report',
          reporterId: mockUser.uid,
          targetType: ReportTargetType.TOILET,
          targetId: mockToilet.id,
          reason: ReportReason.SPAM,
          status: 'pending' as const,
          createdAt: new Date(),
        },
      ];

      // Switch to history screen
      rerender(<ReportHistoryScreen />);

      await waitFor(() => {
        expect(getByText('1')).toBeTruthy(); // Updated total count
      });
    });
  });
});

import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import { Badge, BadgeRarity } from '../../types/post';
import { RARITY_COLORS, CATEGORY_COLORS } from '../../data/badges';

interface BadgeItemProps {
  badge: Badge;
  unlocked: boolean;
  size?: 'small' | 'medium' | 'large';
  onPress?: () => void;
  showDetails?: boolean;
}

const BadgeItem: React.FC<BadgeItemProps> = ({
  badge,
  unlocked,
  size = 'medium',
  onPress,
  showDetails = false,
}) => {
  const sizeConfig = {
    small: {
      container: 60,
      icon: 24,
      fontSize: 10,
    },
    medium: {
      container: 80,
      icon: 32,
      fontSize: 12,
    },
    large: {
      container: 100,
      icon: 40,
      fontSize: 14,
    },
  };

  const config = sizeConfig[size];
  const rarityColor = RARITY_COLORS[badge.rarity];
  const categoryColor = CATEGORY_COLORS[badge.category];

  const containerStyle = [
    styles.container,
    {
      width: config.container,
      height: config.container,
      borderColor: unlocked ? rarityColor : '#e0e0e0',
      backgroundColor: unlocked ? `${rarityColor}15` : '#f5f5f5',
    },
    !unlocked && styles.lockedContainer,
  ];

  const Component = onPress ? TouchableOpacity : View;

  return (
    <Component style={containerStyle} onPress={onPress} activeOpacity={onPress ? 0.7 : 1}>
      {/* „Éê„ÉÉ„Ç∏„Ç¢„Ç§„Ç≥„É≥ */}
      <Text
        style={[
          styles.icon,
          {
            fontSize: config.icon,
            opacity: unlocked ? 1 : 0.3,
          },
        ]}
      >
        {badge.icon}
      </Text>

      {/* „É¨„Ç¢Â∫¶„Ç§„É≥„Ç∏„Ç±„Éº„Çø„Éº */}
      <View
        style={[
          styles.rarityIndicator,
          {
            backgroundColor: unlocked ? rarityColor : '#ccc',
          },
        ]}
      />

      {/* „Éê„ÉÉ„Ç∏Âêç */}
      <Text
        style={[
          styles.name,
          {
            fontSize: config.fontSize,
            color: unlocked ? '#333' : '#999',
          },
        ]}
        numberOfLines={2}
      >
        {badge.name}
      </Text>

      {/* „É≠„ÉÉ„ÇØÁä∂ÊÖã */}
      {!unlocked && (
        <View style={styles.lockOverlay}>
          <Text style={styles.lockIcon}>üîí</Text>
        </View>
      )}

      {/* Ë©≥Á¥∞ÊÉÖÂ†± */}
      {showDetails && (
        <View style={styles.detailsContainer}>
          <Text style={styles.description} numberOfLines={3}>
            {badge.description}
          </Text>

          <View style={styles.metaContainer}>
            <View style={[styles.categoryBadge, { backgroundColor: categoryColor }]}>
              <Text style={styles.categoryText}>{getCategoryDisplayName(badge.category)}</Text>
            </View>

            <Text style={styles.rarityText}>{getRarityDisplayName(badge.rarity)}</Text>
          </View>

          {badge.unlockedAt && (
            <Text style={styles.unlockedDate}>ÂèñÂæóÊó•: {badge.unlockedAt.toLocaleDateString()}</Text>
          )}
        </View>
      )}
    </Component>
  );
};

// „Ç´„ÉÜ„Ç¥„É™Ë°®Á§∫Âêç
const getCategoryDisplayName = (category: string): string => {
  const categoryNames = {
    posting: 'ÊäïÁ®ø',
    reviewing: '„É¨„Éì„É•„Éº',
    exploration: 'Êé¢Á¥¢',
    community: '„Ç≥„Éü„É•„Éã„ÉÜ„Ç£',
    special: 'ÁâπÂà•',
  };
  return categoryNames[category as keyof typeof categoryNames] || category;
};

// „É¨„Ç¢Â∫¶Ë°®Á§∫Âêç
const getRarityDisplayName = (rarity: BadgeRarity): string => {
  const rarityNames = {
    [BadgeRarity.COMMON]: '„Ç≥„É¢„É≥',
    [BadgeRarity.UNCOMMON]: '„Ç¢„É≥„Ç≥„É¢„É≥',
    [BadgeRarity.RARE]: '„É¨„Ç¢',
    [BadgeRarity.EPIC]: '„Ç®„Éî„ÉÉ„ÇØ',
