import React, { useCallback, useMemo } from 'react';
import { View, Text, TouchableOpacity, StyleSheet } from 'react-native';

interface StarRatingProps {
  rating: number;
  maxRating?: number;
  onRatingChange?: (rating: number) => void;
  size?: number;
  color?: string;
  disabled?: boolean;
  showValue?: boolean;
  required?: boolean;
  label?: string;
  description?: string;
}

const StarRating: React.FC<StarRatingProps> = ({
  rating,
  maxRating = 5,
  onRatingChange,
  size = 24,
  color = '#FFD700',
  disabled = false,
  showValue = true,
  required = false,
  label,
  description,
}) => {
  const handleStarPress = useCallback(
    (starRating: number) => {
      if (!disabled && onRatingChange) {
        onRatingChange(starRating);
      }
    },
    [disabled, onRatingChange],
  );

  // „É°„É¢Âåñ„Åï„Çå„ÅüË©ï‰æ°ÂÄ§Ë°®Á§∫
  const displayRating = useMemo(() => (rating > 0 ? `${rating.toFixed(1)}` : 'Êú™Ë©ï‰æ°'), [rating]);

  // „É°„É¢Âåñ„Åï„Çå„ÅüÊòü„ÅÆÈÖçÂàó
  const stars = useMemo(
    () =>
      Array.from({ length: maxRating }, (_, index) => {
        const starNumber = index + 1;
        const isFilled = starNumber <= rating;
        const isHalfFilled = starNumber - 0.5 === rating;

        return (
          <TouchableOpacity
            key={starNumber}
            style={[styles.star, { width: size, height: size }]}
            onPress={() => handleStarPress(starNumber)}
            disabled={disabled}
            activeOpacity={0.7}
          >
            <Text
              style={[
                styles.starText,
                {
                  fontSize: size,
                  color: isFilled || isHalfFilled ? color : '#E0E0E0',
                },
              ]}
            >
              {isFilled ? '‚òÖ' : isHalfFilled ? '‚òÜ' : '‚òÜ'}
            </Text>
          </TouchableOpacity>
        );
      }),
    [maxRating, rating, size, color, disabled, handleStarPress],
  );

  return (
    <View style={styles.container}>
      {label && (
        <View style={styles.labelContainer}>
          <Text style={styles.label}>
            {label}
            {required && <Text style={styles.required}> *</Text>}
          </Text>
          {description && <Text style={styles.description}>{description}</Text>}
        </View>
      )}

      <View style={styles.ratingContainer}>
        <View style={styles.starsContainer}>{stars}</View>

        {showValue && <Text style={styles.ratingValue}>{displayRating}</Text>}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    marginVertical: 8,
  },
  labelContainer: {
    marginBottom: 8,
  },
  label: {
    fontSize: 16,
    fontWeight: '600',
    color: '#333',
  },
  required: {
    color: '#ff4757',
  },
  description: {
    fontSize: 12,
    color: '#666',
    marginTop: 2,
  },
  ratingContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  starsContainer: {
    flexDirection: 'row',
    marginRight: 12,
  },
  star: {
    marginRight: 4,
    justifyContent: 'center',
    alignItems: 'center',
  },
  starText: {
    textAlign: 'center',
    lineHeight: undefined, // „Éá„Éï„Ç©„É´„Éà„Å´„É™„Çª„ÉÉ„Éà
  },
  ratingValue: {
    fontSize: 16,
    fontWeight: '600',
    color: '#333',
    minWidth: 45,
  },
});

// React.memo with custom comparison for performance optimization
export default React.memo(
  StarRating,
  (prevProps, nextProps) =>
    prevProps.rating === nextProps.rating &&
    prevProps.maxRating === nextProps.maxRating &&
    prevProps.size === nextProps.size &&
    prevProps.color === nextProps.color &&
    prevProps.disabled === nextProps.disabled &&
    prevProps.showValue === nextProps.showValue &&
    prevProps.required === nextProps.required &&
    prevProps.label === nextProps.label &&
    prevProps.description === nextProps.description &&
    prevProps.onRatingChange === nextProps.onRatingChange,
);

import React, { useRef, useState, useCallback, useEffect } from 'react';
import {
  View,
  StyleSheet,
  TouchableOpacity,
  Text,
  Alert,
  Dimensions,
} from 'react-native';
import MapView, { PROVIDER_GOOGLE, Region, Marker } from 'react-native-maps';
import Icon from 'react-native-vector-icons/Ionicons';
import { Coordinate, DEFAULT_MAP_REGION } from '../../types/maps';
import { useLocation } from '../../hooks/useLocation';

interface LocationPickerProps {
  selectedLocation: Coordinate | null;
  onLocationSelect: (location: Coordinate) => void;
  onCancel: () => void;
  onConfirm: () => void;
}

const { width, height } = Dimensions.get('window');

const LocationPicker: React.FC<LocationPickerProps> = ({
  selectedLocation,
  onLocationSelect,
  onCancel,
  onConfirm,
}) => {
  const mapRef = useRef<MapView>(null);
  const [currentRegion, setCurrentRegion] = useState<Region>(
    selectedLocation
      ? {
          latitude: selectedLocation.latitude,
          longitude: selectedLocation.longitude,
          latitudeDelta: 0.01,
          longitudeDelta: 0.01,
        }
      : DEFAULT_MAP_REGION
  );

  const {
    getCurrentLocation,
    isLoading: isLocationLoading,
    showLocationError,
  } = useLocation();

  // ÁèæÂú®‰ΩçÁΩÆ„Å´ÁßªÂãï
  const moveToCurrentLocation = useCallback(async () => {
    try {
      const userLocation = await getCurrentLocation();
      if (userLocation && mapRef.current) {
        const region: Region = {
          latitude: userLocation.latitude,
          longitude: userLocation.longitude,
          latitudeDelta: 0.01,
          longitudeDelta: 0.01,
        };

        mapRef.current.animateToRegion(region, 1000);
        setCurrentRegion(region);
        onLocationSelect(userLocation);
      }
    } catch (error) {
      showLocationError();
    }
  }, [getCurrentLocation, showLocationError, onLocationSelect]);

  // Âú∞Âõ≥„Çø„ÉÉ„ÉóÊôÇ„ÅÆÂá¶ÁêÜ
  const handleMapPress = useCallback((event: any) => {
    const { coordinate } = event.nativeEvent;
    onLocationSelect(coordinate);
  }, [onLocationSelect]);

  // Âú∞Âõ≥„ÅÆÈ†òÂüüÂ§âÊõ¥ÊôÇ„ÅÆÂá¶ÁêÜ
  const handleRegionChangeComplete = useCallback((region: Region) => {
    setCurrentRegion(region);
  }, []);

  // Á¢∫ÂÆöÂâç„ÅÆÁ¢∫Ë™ç
  const handleConfirm = useCallback(() => {
    if (!selectedLocation) {
      Alert.alert('„Ç®„É©„Éº', '‰ΩçÁΩÆ„ÇíÈÅ∏Êäû„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
      return;
    }

    Alert.alert(
      '‰ΩçÁΩÆ„ÅÆÁ¢∫Ë™ç',
      'ÈÅ∏Êäû„Åó„Åü‰ΩçÁΩÆ„Åß„Çà„Çç„Åó„ÅÑ„Åß„Åô„ÅãÔºü',
      [
        { text: '„Ç≠„É£„É≥„Çª„É´', style: 'cancel' },
        { text: 'Á¢∫ÂÆö', onPress: onConfirm },
      ]
    );
  }, [selectedLocation, onConfirm]);

  // ÂàùÊúü‰ΩçÁΩÆ„ÇíÁèæÂú®Âú∞„Å´Ë®≠ÂÆö
  useEffect(() => {
    if (!selectedLocation) {
      moveToCurrentLocation();
    }
  }, [selectedLocation, moveToCurrentLocation]);

  return (
    <View style={styles.container}>
      {/* „Éò„ÉÉ„ÉÄ„Éº */}
      <View style={styles.header}>
        <TouchableOpacity style={styles.headerButton} onPress={onCancel}>
          <Icon name="close" size={24} color="#333" />
        </TouchableOpacity>
        <Text style={styles.headerTitle}>‰ΩçÁΩÆ„ÇíÈÅ∏Êäû</Text>
        <TouchableOpacity 
          style={[styles.headerButton, styles.confirmButton]} 
          onPress={handleConfirm}
        >
          <Text style={styles.confirmButtonText}>Á¢∫ÂÆö</Text>
        </TouchableOpacity>
      </View>

      {/* Âú∞Âõ≥ */}
      <View style={styles.mapContainer}>
        <MapView
          ref={mapRef}
          provider={PROVIDER_GOOGLE}
          style={styles.map}
          region={currentRegion}
          onRegionChangeComplete={handleRegionChangeComplete}
          onPress={handleMapPress}
          showsUserLocation={true}
          showsMyLocationButton={false}
          showsCompass={true}
        >
          {selectedLocation && (
            <Marker
              coordinate={selectedLocation}
              draggable={true}
              onDragEnd={(event) => {
                onLocationSelect(event.nativeEvent.coordinate);
              }}
            >
              <View style={styles.markerContainer}>
                <Icon name="location" size={30} color="#ff4757" />
              </View>
            </Marker>
          )}
        </MapView>

        {/* ‰∏≠Â§Æ„ÅÆÂçÅÂ≠óÁ∑öÔºàÂú∞Âõ≥„Çø„ÉÉ„ÉóÁî®„ÅÆ„Ç¨„Ç§„ÉâÔºâ */}
        <View style={styles.crosshair}>
          <View style={styles.crosshairHorizontal} />
          <View style={styles.crosshairVertical} />
        </View>

        {/* ÁèæÂú®‰ΩçÁΩÆ„Éú„Çø„É≥ */}
        <TouchableOpacity
          style={[styles.locationButton, isLocationLoading && styles.loadingButton]}
          onPress={moveToCurrentLocation}
          disabled={isLocationLoading}
        >
          <Icon 
            name={isLocationLoading ? "refresh" : "locate"} 
            size={24} 
            color="#4285f4" 
          />
        </TouchableOpacity>
      </View>

      {/* Â∫ïÈÉ®„ÅÆË™¨Êòé */}
      <View style={styles.instructions}>
        <Text style={styles.instructionText}>
          üìç Âú∞Âõ≥„Çí„Çø„ÉÉ„Éó„Åæ„Åü„ÅØ„Éû„Éº„Ç´„Éº„Çí„Éâ„É©„ÉÉ„Ç∞„Åó„Å¶‰ΩçÁΩÆ„ÇíÈÅ∏Êäû„Åó„Å¶„Åè„Å†„Åï„ÅÑ
        </Text>
        {selectedLocation && (
          <View style={styles.coordinates}>
            <Text style={styles.coordinateText}>
              Á∑ØÂ∫¶: {selectedLocation.latitude.toFixed(6)}
            </Text>
            <Text style={styles.coordinateText}>
              ÁµåÂ∫¶: {selectedLocation.longitude.toFixed(6)}
            </Text>
          </View>
        )}
      </View>
    </View>
  );
};

