interface HelpfulVotingProps {
  toiletId: string;
  size?: 'small' | 'medium' | 'large';
  showStats?: boolean;
  showText?: boolean;
}

const HelpfulVoting: React.FC<HelpfulVotingProps> = ({
  toiletId,
  size = 'medium',
  showStats = true,
  showText = true,
}) => {
  const {
    stats,
    isLoading,
    isVoting,
    userVotedHelpful,
    userVotedNotHelpful,
    voteHelpful,
    voteNotHelpful,
    removeVote,
  } = useHelpful(toiletId);

  // „É°„É¢Âåñ„Åï„Çå„Åü„Çµ„Ç§„Ç∫Ë®≠ÂÆö
  const config = useMemo(() => {
    const sizeConfig = {
      small: {
        iconSize: 16,
        fontSize: 12,
        padding: 6,
        gap: 8,
      },
      medium: {
        iconSize: 20,
        fontSize: 14,
        padding: 8,
        gap: 12,
      },
      large: {
        iconSize: 24,
        fontSize: 16,
        padding: 10,
        gap: 16,
      },
    };
    return sizeConfig[size];
  }, [size]);

  // „É°„É¢Âåñ„Åï„Çå„Åü„Éú„Çø„É≥„Éè„É≥„Éâ„É©„Éº
  const handleHelpfulPress = useCallback(() => {
    if (userVotedHelpful) {
      removeVote();
    } else {
      voteHelpful();
    }
  }, [userVotedHelpful, removeVote, voteHelpful]);

  const handleNotHelpfulPress = useCallback(() => {
    if (userVotedNotHelpful) {
      removeVote();
    } else {
      voteNotHelpful();
    }
  }, [userVotedNotHelpful, removeVote, voteNotHelpful]);

  // „É°„É¢Âåñ„Åï„Çå„ÅüÁµ±Ë®àË®àÁÆó
  const helpfulPercentage = useMemo(
    () => (stats.totalVotes > 0 ? Math.round(stats.helpfulRatio * 100) : 0),
    [stats.helpfulRatio, stats.totalVotes],
  );

  if (isLoading) {
    return (
      <View style={[styles.container, { gap: config.gap }]}>
        <ActivityIndicator size="small" color="#666" />
        {showText && (
          <Text style={[styles.loadingText, { fontSize: config.fontSize }]}>Ë™≠„ÅøËæº„Åø‰∏≠...</Text>
        )}
      </View>
    );
  }

  return (
    <View style={[styles.container, { gap: config.gap }]}>
      {/* ÂΩπ„Å´Á´ã„Å£„Åü„Éú„Çø„É≥ */}
      <TouchableOpacity
        style={[
          styles.voteButton,
          {
            padding: config.padding,
            backgroundColor: userVotedHelpful ? '#4CAF50' : '#f5f5f5',
          },
          isVoting && styles.disabledButton,
        ]}
        onPress={handleHelpfulPress}
        disabled={isVoting}
        activeOpacity={0.7}
      >
        <Icon name="thumbs-up" size={config.iconSize} color={userVotedHelpful ? '#fff' : '#666'} />
        {showText && (
          <Text
            style={[
              styles.voteText,
              {
                fontSize: config.fontSize,
                color: userVotedHelpful ? '#fff' : '#666',
              },
            ]}
          >
            ÂΩπ„Å´Á´ã„Å£„Åü
          </Text>
        )}
        {showStats && (
          <Text
            style={[
              styles.countText,
              {
                fontSize: config.fontSize - 2,
                color: userVotedHelpful ? '#fff' : '#999',
              },
            ]}
          >
            {stats.helpfulCount}
          </Text>
        )}
      </TouchableOpacity>

      {/* ÂΩπ„Å´Á´ã„Åü„Å™„Åã„Å£„Åü„Éú„Çø„É≥ */}
      <TouchableOpacity
        style={[
          styles.voteButton,
          {
            padding: config.padding,
            backgroundColor: userVotedNotHelpful ? '#f44336' : '#f5f5f5',
          },
          isVoting && styles.disabledButton,
        ]}
        onPress={handleNotHelpfulPress}
        disabled={isVoting}
        activeOpacity={0.7}
      >
        <Icon
          name="thumbs-down"
          size={config.iconSize}
          color={userVotedNotHelpful ? '#fff' : '#666'}
        />
        {showText && (
          <Text
            style={[
              styles.voteText,
              {
                fontSize: config.fontSize,
                color: userVotedNotHelpful ? '#fff' : '#666',
              },
            ]}
          >
            ÂΩπ„Å´Á´ã„Åü„Å™„Åã„Å£„Åü
          </Text>
        )}
        {showStats && (
          <Text
            style={[
              styles.countText,
              {
                fontSize: config.fontSize - 2,
                color: userVotedNotHelpful ? '#fff' : '#999',
              },
            ]}
          >
            {stats.notHelpfulCount}
          </Text>
        )}
      </TouchableOpacity>

      {/* Áµ±Ë®àÊÉÖÂ†±ÔºàË©≥Á¥∞Ôºâ */}
      {showStats && stats.totalVotes > 0 && (
        <View style={styles.statsContainer}>
