  onRemoveImage: (index: number) => void;
  maxImages?: number;
}

const { width } = Dimensions.get('window');
const imageSize = (width - 48) / 3; // 3ÂàóË°®Á§∫„ÄÅ‰ΩôÁôΩ„ÇíËÄÉÊÖÆ

const ImagePickerComponent: React.FC<ImagePickerProps> = ({
  images,
  onAddImage,
  onRemoveImage,
  maxImages = 5,
}) => {
  const selectImageSource = useCallback(() => {
    Alert.alert('ÁîªÂÉè„ÇíÈÅ∏Êäû', 'ÁîªÂÉè„ÅÆÂèñÂæóÊñπÊ≥ï„ÇíÈÅ∏Êäû„Åó„Å¶„Åè„Å†„Åï„ÅÑ', [
      { text: '„Ç≠„É£„É≥„Çª„É´', style: 'cancel' },
      { text: '„Ç´„É°„É©„ÅßÊíÆÂΩ±', onPress: openCamera },
      { text: '„É©„Ç§„Éñ„É©„É™„Åã„ÇâÈÅ∏Êäû', onPress: openImageLibrary },
    ]);
  }, []);

  const openCamera = useCallback(() => {
    const options = {
      mediaType: 'photo' as MediaType,
      quality: 0.8 as PhotoQuality,
      maxWidth: 1024,
      maxHeight: 1024,
    };

    launchCamera(options, (response: ImagePickerResponse) => {
      if (response.didCancel || response.errorMessage) {
        return;
      }

      if (response.assets && response.assets[0]?.uri) {
        onAddImage(response.assets[0].uri);
      }
    });
  }, [onAddImage]);

  const openImageLibrary = useCallback(() => {
    const options = {
      mediaType: 'photo' as MediaType,
      quality: 0.8 as PhotoQuality,
      maxWidth: 1024,
      maxHeight: 1024,
      selectionLimit: maxImages - images.length,
    };

    launchImageLibrary(options, (response: ImagePickerResponse) => {
      if (response.didCancel || response.errorMessage) {
        return;
      }

      if (response.assets) {
        response.assets.forEach(asset => {
          if (asset.uri) {
            onAddImage(asset.uri);
          }
        });
      }
    });
  }, [onAddImage, maxImages, images.length]);

  const confirmRemoveImage = useCallback(
    (index: number) => {
      Alert.alert('ÁîªÂÉè„ÇíÂâäÈô§', '„Åì„ÅÆÁîªÂÉè„ÇíÂâäÈô§„Åó„Åæ„Åô„ÅãÔºü', [
        { text: '„Ç≠„É£„É≥„Çª„É´', style: 'cancel' },
        { text: 'ÂâäÈô§', onPress: () => onRemoveImage(index), style: 'destructive' },
      ]);
    },
    [onRemoveImage],
  );

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>
          ÂÜôÁúü ({images.length}/{maxImages})
        </Text>
        <Text style={styles.subtitle}>„Éà„Ç§„É¨„ÅÆÂÜôÁúü„ÇíËøΩÂä†„Åó„Å¶„Åè„Å†„Åï„ÅÑ</Text>
      </View>

      <ScrollView horizontal showsHorizontalScrollIndicator={false} style={styles.scrollView}>
        <View style={styles.imageGrid}>
          {/* ËøΩÂä†„Éú„Çø„É≥ */}
          {images.length < maxImages && (
            <TouchableOpacity style={styles.addButton} onPress={selectImageSource}>
              <Icon name="camera" size={32} color="#666" />
              <Text style={styles.addButtonText}>ÂÜôÁúü„ÇíËøΩÂä†</Text>
            </TouchableOpacity>
          )}

          {/* ÁîªÂÉè„É™„Çπ„Éà */}
          {images.map((image, index) => (
            <View key={index} style={styles.imageContainer}>
              <Image source={{ uri: image }} style={styles.image} />
              <TouchableOpacity
                style={styles.removeButton}
                onPress={() => confirmRemoveImage(index)}
              >
                <Icon name="close-circle" size={24} color="#ff4757" />
              </TouchableOpacity>
            </View>
          ))}
        </View>
      </ScrollView>

      {images.length === 0 && (
        <View style={styles.emptyState}>
          <Icon name="images-outline" size={48} color="#ccc" />
          <Text style={styles.emptyText}>„Åæ„Å†ÂÜôÁúü„Åå„ÅÇ„Çä„Åæ„Åõ„Çì</Text>
          <Text style={styles.emptySubtext}>
            „Éà„Ç§„É¨„ÅÆÂ§ñË¶≥„ÇÑÂÜÖÈÉ®„ÅÆÂÜôÁúü„ÇíËøΩÂä†„Åô„Çã„Å®„ÄÅ‰ªñ„ÅÆ„É¶„Éº„Ç∂„Éº„Å´ÂΩπÁ´ã„Å°„Åæ„Åô
          </Text>
        </View>
      )}

      {images.length > 0 && (
        <View style={styles.tips}>
          <Icon name="information-circle-outline" size={16} color="#666" />
          <Text style={styles.tipsText}>
            ÂÜôÁúü„ÅØ‰ªñ„ÅÆ„É¶„Éº„Ç∂„Éº„Åå„Éà„Ç§„É¨„ÇíË¶ã„Å§„Åë„ÇÑ„Åô„Åè„Åô„Çã„Åü„ÇÅ„Å´‰ΩøÁî®„Åï„Çå„Åæ„Åô
          </Text>
        </View>
      )}
    </View>
  );
};

