// Location hook
export const useLocation = () => {
  const [location, setLocation] = useState<{
    latitude: number;
    longitude: number;
  } | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    console.log('‰ΩçÁΩÆÊÉÖÂ†±„ÅÆÂèñÂæó„ÇíÈñãÂßã...');

    // „Çà„ÇäÂØõÂÆπ„Å™Ë®≠ÂÆö„Åß‰ΩçÁΩÆÊÉÖÂ†±„ÇíÂèñÂæó
    Geolocation.getCurrentPosition(
      position => {
        console.log('‰ΩçÁΩÆÊÉÖÂ†±ÂèñÂæóÊàêÂäü:', position.coords);
        setLocation({
          latitude: position.coords.latitude,
          longitude: position.coords.longitude,
        });
        setError(null);
        setLoading(false);
      },
      positionError => {
        console.error('‰ΩçÁΩÆÊÉÖÂ†±ÂèñÂæó„Ç®„É©„Éº:', positionError);
        console.error('„Ç®„É©„Éº„Ç≥„Éº„Éâ:', positionError.code);
        console.error('„Ç®„É©„Éº„É°„ÉÉ„Çª„Éº„Ç∏:', positionError.message);

        // „Éá„Éï„Ç©„É´„Éà„ÅÆ‰ΩçÁΩÆÊÉÖÂ†±ÔºàÊù±‰∫¨ÈßÖÔºâ„ÇíË®≠ÂÆö
        console.log('„Éá„Éï„Ç©„É´„Éà‰ΩçÁΩÆÊÉÖÂ†±„Çí‰ΩøÁî®: Êù±‰∫¨ÈßÖ');
        setLocation({
          latitude: 35.6812,
          longitude: 139.7671,
        });
        setError('‰ΩçÁΩÆÊÉÖÂ†±„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åü„Åü„ÇÅ„ÄÅ„Éá„Éï„Ç©„É´„Éà‰ΩçÁΩÆÔºàÊù±‰∫¨ÈßÖÔºâ„Çí‰ΩøÁî®„Åó„Å¶„ÅÑ„Åæ„Åô');
        setLoading(false);
      },
      { 
        enableHighAccuracy: false, // Á≤æÂ∫¶„Çí‰∏ã„Åí„Å¶ÈÄüÂ∫¶„Çí‰∏ä„Åí„Çã
        timeout: 30000, // „Çø„Ç§„É†„Ç¢„Ç¶„Éà„Çí30Áßí„Å´Âª∂Èï∑
        maximumAge: 300000 // „Ç≠„É£„ÉÉ„Ç∑„É•„Çí5ÂàÜÈñìÊúâÂäπ„Å´„Åô„Çã
      },
    );
  }, []);

  return { location, error, loading };
};

// Reviews hooks
export const useReviews = (params?: { limit?: number; offset?: number }) =>
  useQuery({
    queryKey: ['reviews', params],
    queryFn: () => apiService.getReviews(params),
  });

export const useReviewsByPlace = (placeId: string) =>
  useQuery({
    queryKey: ['reviews', 'place', placeId],
    queryFn: () => apiService.getReviewsByPlace(placeId),
    enabled: !!placeId,
  });

export const useCreateReview = () => {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: (review: { placeId: string; rating: number; comment: string; photos?: string[] }) =>
      apiService.createReview(review),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['reviews'] });
    },
  });
};

// Places hooks
export const useSearchPlaces = (query: string, location?: { lat: number; lng: number }) =>
  useQuery({
    queryKey: ['places', 'search', query, location],
    queryFn: () => apiService.searchPlaces(query, location),
    enabled: !!query,
  });

export const useNearbyPlaces = (location: { lat: number; lng: number }, radius = 1000) =>
  useQuery({
    queryKey: ['places', 'nearby', location, radius],
    queryFn: () => apiService.getNearbyPlaces(location, radius),
    enabled: !!location,
  });

import { useState, useCallback, useEffect } from 'react';
import { Alert } from 'react-native';
import { Badge, UserBadges } from '../types/post';
import { badgeService } from '../services/badgeService';
import { useAuth } from '../stores/authStore';

interface UseBadgesState {
  userBadges: UserBadges | null;
  unlockedBadgeDetails: Badge[];
  allBadges: Badge[];
  isLoading: boolean;
  isChecking: boolean;
}

export const useBadges = () => {
  const { user } = useAuth();
  const [state, setState] = useState<UseBadgesState>({
    userBadges: null,
    unlockedBadgeDetails: [],
    allBadges: badgeService.getAllBadges(),
    isLoading: false,
    isChecking: false,
  });

  // „É¶„Éº„Ç∂„Éº„ÅÆ„Éê„ÉÉ„Ç∏ÊÉÖÂ†±„ÇíË™≠„ÅøËæº„Åø
  const loadUserBadges = useCallback(async () => {
    if (!user) return;

    setState(prev => ({ ...prev, isLoading: true }));

    try {
      const [userBadges, badgeDetails] = await Promise.all([
        badgeService.getUserBadges(user.uid),
        badgeService.getUserBadgeDetails(user.uid),
      ]);

      setState(prev => ({
        ...prev,
        userBadges,
        unlockedBadgeDetails: badgeDetails,
        isLoading: false,
      }));
    } catch (error) {
      console.error('Failed to load user badges:', error);
      setState(prev => ({ ...prev, isLoading: false }));
    }
  }, [user]);

  // „Éê„ÉÉ„Ç∏Êù°‰ª∂„Çí„ÉÅ„Çß„ÉÉ„ÇØ
  const checkBadgeConditions = useCallback(async (): Promise<string[]> => {
    if (!user) return [];

    setState(prev => ({ ...prev, isChecking: true }));

    try {
      const newBadgeIds = await badgeService.checkBadgeConditions(user.uid);

      if (newBadgeIds.length > 0) {
        // Êñ∞„Åó„ÅÑ„Éê„ÉÉ„Ç∏„ÇíÂèñÂæó„Åó„ÅüÂ†¥Âêà„ÄÅ„Éá„Éº„Çø„ÇíÂÜçË™≠„ÅøËæº„Åø
        await loadUserBadges();

        // Êñ∞„Åó„ÅÑ„Éê„ÉÉ„Ç∏ÂèñÂæó„ÅÆÈÄöÁü•
        const newBadges = newBadgeIds
          .map(id => state.allBadges.find(badge => badge.id === id))
          .filter((badge): badge is Badge => badge !== undefined);

        if (newBadges.length === 1) {
          Alert.alert(
            'üéâ Êñ∞„Åó„ÅÑ„Éê„ÉÉ„Ç∏„ÇíÁç≤ÂæóÔºÅ',
            `„Äå${newBadges[0].name}„Äç\n${newBadges[0].description}`,
            [{ text: 'OK' }],
          );
        } else if (newBadges.length > 1) {
          Alert.alert(
            'üéâ Ë§áÊï∞„ÅÆ„Éê„ÉÉ„Ç∏„ÇíÁç≤ÂæóÔºÅ',
            `${newBadges.length}ÂÄã„ÅÆÊñ∞„Åó„ÅÑ„Éê„ÉÉ„Ç∏„ÇíÁç≤Âæó„Åó„Åæ„Åó„Åü`,
            [{ text: 'OK' }],
          );
        }
      }

      setState(prev => ({ ...prev, isChecking: false }));
      return newBadgeIds;
    } catch (error) {
      console.error('Failed to check badge conditions:', error);
      setState(prev => ({ ...prev, isChecking: false }));
      return [];
    }
  }, [user, loadUserBadges, state.allBadges]);

  // ÊäïÁ®øÊôÇ„ÅÆ„Éê„ÉÉ„Ç∏„ÉÅ„Çß„ÉÉ„ÇØ
  const onToiletPosted = useCallback(
    async (toiletData: any) => {
      if (!user) return;

      try {
        const newBadgeIds = await badgeService.onToiletPosted(user.uid, toiletData);

        if (newBadgeIds.length > 0) {
          await loadUserBadges();

          // ÈÄöÁü•Ë°®Á§∫
          const newBadges = newBadgeIds
            .map(id => state.allBadges.find(badge => badge.id === id))
            .filter((badge): badge is Badge => badge !== undefined);

          for (const badge of newBadges) {
            Alert.alert('üéâ Êñ∞„Åó„ÅÑ„Éê„ÉÉ„Ç∏„ÇíÁç≤ÂæóÔºÅ', `„Äå${badge.name}„Äç\n${badge.description}`, [
              { text: '„Åô„Åî„ÅÑÔºÅ' },
            ]);
          }
        }
      } catch (error) {
        console.error('Failed to handle toilet posted:', error);
      }
    },
    [user, loadUserBadges, state.allBadges],
  );

  // ÊäïÁ•®ÊôÇ„ÅÆ„Éê„ÉÉ„Ç∏„ÉÅ„Çß„ÉÉ„ÇØ
  const onHelpfulVote = useCallback(
    async (targetUserId: string) => {
      if (!user) return;

      try {
        await badgeService.onHelpfulVote(user.uid, targetUserId);
        // „Éê„ÉÉ„Ç∏ÂèñÂæó„Åå„ÅÇ„Å£„ÅüÂèØËÉΩÊÄß„Åå„ÅÇ„Çã„ÅÆ„Åß„ÄÅÂ∞ë„ÅóÈÅÖÂª∂„Åó„Å¶„ÉÅ„Çß„ÉÉ„ÇØ
        setTimeout(() => {
          checkBadgeConditions();
        }, 1000);
      } catch (error) {
        console.error('Failed to handle helpful vote:', error);
      }
    },
    [user, checkBadgeConditions],
  );

  // ÂàùÊúü„Éá„Éº„Çø„ÅÆË™≠„ÅøËæº„Åø
  useEffect(() => {
    if (user) {
      loadUserBadges();
    }
  }, [user, loadUserBadges]);

  // Ë®àÁÆóÂÄ§
  const badgeCount = state.userBadges?.unlockedBadges.length || 0;
  const totalBadges = state.allBadges.length;
  const completionRate = totalBadges > 0 ? (badgeCount / totalBadges) * 100 : 0;

  // „Ç´„ÉÜ„Ç¥„É™Âà•„ÅÆÂèñÂæóÊï∞
  const getBadgeCountByCategory = useCallback(
    (category: string) => {
      const categoryBadges = state.allBadges.filter(badge => badge.category === category);
      const unlockedInCategory = state.unlockedBadgeDetails.filter(
        badge => badge.category === category,
      );
      return {
        unlocked: unlockedInCategory.length,
        total: categoryBadges.length,
      };
    },
    [state.allBadges, state.unlockedBadgeDetails],
  );

  // „É¨„Ç¢Â∫¶Âà•„ÅÆÂèñÂæóÊï∞
  const getBadgeCountByRarity = useCallback(
    (rarity: string) => {
      const rarityBadges = state.allBadges.filter(badge => badge.rarity === rarity);
      const unlockedInRarity = state.unlockedBadgeDetails.filter(badge => badge.rarity === rarity);
      return {
        unlocked: unlockedInRarity.length,
        total: rarityBadges.length,
      };
    },
    [state.allBadges, state.unlockedBadgeDetails],
  );

  // ÁâπÂÆö„ÅÆ„Éê„ÉÉ„Ç∏„ÅåÂèñÂæóÊ∏à„Åø„Åã„ÉÅ„Çß„ÉÉ„ÇØ
  const hasBadge = useCallback(
    (badgeId: string) => state.userBadges?.unlockedBadges.includes(badgeId) || false,
    [state.userBadges],
  );

  return {
    // Áä∂ÊÖã
    userBadges: state.userBadges,
    unlockedBadges: state.unlockedBadgeDetails,
    allBadges: state.allBadges,
    isLoading: state.isLoading,
    isChecking: state.isChecking,

    // Ë®àÁÆóÂÄ§
    badgeCount,
    totalBadges,
    completionRate,

    // „Ç¢„ÇØ„Ç∑„Éß„É≥
    loadUserBadges,
    checkBadgeConditions,
    onToiletPosted,
    onHelpfulVote,
    getBadgeCountByCategory,
    getBadgeCountByRarity,
    hasBadge,
  };
};

import { useState, useCallback, useEffect } from 'react';
import { Alert } from 'react-native';
import { ViewHistory, Favorite, UserActivity, UserStats } from '../types/post';
import { ToiletLocation } from '../types/maps';
import { historyService } from '../services/historyService';
import { useAuth } from '../stores/authStore';

interface UseHistoryState {
  viewHistory: ViewHistory[];
  favorites: Favorite[];
  activities: UserActivity[];
  userStats: UserStats | null;
  isLoading: boolean;
  isUpdating: boolean;
}

export const useHistory = () => {
  const { user } = useAuth();
  const [state, setState] = useState<UseHistoryState>({
    viewHistory: [],
    favorites: [],
    activities: [],
    userStats: null,
    isLoading: false,
    isUpdating: false,
  });

  // Èñ≤Ë¶ßÂ±•Ê≠¥„ÇíË®òÈå≤
  const recordView = useCallback(
    async (toilet: ToiletLocation) => {
      if (!user) return;

      try {
        await historyService.recordView(user.uid, toilet);
        // Â±•Ê≠¥„ÇíÂÜçË™≠„ÅøËæº„ÅøÔºà„Éê„ÉÉ„ÇØ„Ç∞„É©„Ç¶„É≥„Éâ„ÅßÔºâ
        loadViewHistory();
      } catch (error) {
        console.error('Failed to record view:', error);
      }
    },
    [user],
  );

  // Èñ≤Ë¶ßÂ±•Ê≠¥„ÇíË™≠„ÅøËæº„Åø
  const loadViewHistory = useCallback(async () => {
    if (!user) return;

    setState(prev => ({ ...prev, isLoading: true }));

    try {
      const history = await historyService.getViewHistory(user.uid);
      setState(prev => ({
        ...prev,
        viewHistory: history,
        isLoading: false,
      }));
    } catch (error) {
      console.error('Failed to load view history:', error);
      setState(prev => ({ ...prev, isLoading: false }));
    }
  }, [user]);

  // Èñ≤Ë¶ßÂ±•Ê≠¥„Çí„ÇØ„É™„Ç¢
  const clearViewHistory = useCallback(async () => {
    if (!user) return;

    Alert.alert('Â±•Ê≠¥„ÇíÂâäÈô§', 'Èñ≤Ë¶ßÂ±•Ê≠¥„Çí„Åô„Åπ„Å¶ÂâäÈô§„Åó„Åæ„Åô„ÅãÔºü', [
      { text: '„Ç≠„É£„É≥„Çª„É´', style: 'cancel' },
      {
        text: 'ÂâäÈô§',
        style: 'destructive',
        onPress: async () => {
          setState(prev => ({ ...prev, isUpdating: true }));
          try {
            await historyService.clearViewHistory(user.uid);
            setState(prev => ({
              ...prev,
              viewHistory: [],
              isUpdating: false,
            }));
            Alert.alert('ÂÆå‰∫Ü', 'Â±•Ê≠¥„ÇíÂâäÈô§„Åó„Åæ„Åó„Åü');
          } catch (error) {
            console.error('Failed to clear view history:', error);
            Alert.alert('„Ç®„É©„Éº', 'Â±•Ê≠¥„ÅÆÂâäÈô§„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
            setState(prev => ({ ...prev, isUpdating: false }));
          }
        },
      },
    ]);
  }, [user]);

  // „ÅäÊ∞ó„Å´ÂÖ•„Çä„ÇíË™≠„ÅøËæº„Åø
  const loadFavorites = useCallback(async () => {
    if (!user) return;

    setState(prev => ({ ...prev, isLoading: true }));

    try {
      const favorites = await historyService.getFavorites(user.uid);
      setState(prev => ({
        ...prev,
        favorites,
        isLoading: false,
      }));
    } catch (error) {
      console.error('Failed to load favorites:', error);
      setState(prev => ({ ...prev, isLoading: false }));
    }
  }, [user]);

  // „ÅäÊ∞ó„Å´ÂÖ•„Çä„Å´ËøΩÂä†
  const addToFavorites = useCallback(
    async (toilet: ToiletLocation) => {
      if (!user) return;

      setState(prev => ({ ...prev, isUpdating: true }));

      try {
        await historyService.addToFavorites(user.uid, toilet);

        // „ÅäÊ∞ó„Å´ÂÖ•„Çä‰∏ÄË¶ß„ÇíÊõ¥Êñ∞
        await loadFavorites();

        setState(prev => ({ ...prev, isUpdating: false }));
        Alert.alert('ËøΩÂä†ÂÆå‰∫Ü', '„ÅäÊ∞ó„Å´ÂÖ•„Çä„Å´ËøΩÂä†„Åó„Åæ„Åó„Åü');
      } catch (error) {
        console.error('Failed to add to favorites:', error);
        setState(prev => ({ ...prev, isUpdating: false }));

        if (error instanceof Error) {
          Alert.alert('„Ç®„É©„Éº', error.message);
        } else {
          Alert.alert('„Ç®„É©„Éº', '„ÅäÊ∞ó„Å´ÂÖ•„Çä„ÅÆËøΩÂä†„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
        }
      }
    },
    [user, loadFavorites],
  );

  // „ÅäÊ∞ó„Å´ÂÖ•„Çä„Åã„ÇâÂâäÈô§
  const removeFromFavorites = useCallback(
    async (toiletId: string) => {
      if (!user) return;

      setState(prev => ({ ...prev, isUpdating: true }));

      try {
        await historyService.removeFromFavorites(user.uid, toiletId);

        // „ÅäÊ∞ó„Å´ÂÖ•„Çä‰∏ÄË¶ß„ÇíÊõ¥Êñ∞
        setState(prev => ({
          ...prev,
          favorites: prev.favorites.filter(fav => fav.toiletId !== toiletId),
          isUpdating: false,
        }));

        Alert.alert('ÂâäÈô§ÂÆå‰∫Ü', '„ÅäÊ∞ó„Å´ÂÖ•„Çä„Åã„ÇâÂâäÈô§„Åó„Åæ„Åó„Åü');
      } catch (error) {
        console.error('Failed to remove from favorites:', error);
        setState(prev => ({ ...prev, isUpdating: false }));
        Alert.alert('„Ç®„É©„Éº', '„ÅäÊ∞ó„Å´ÂÖ•„Çä„ÅÆÂâäÈô§„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
      }
    },
    [user],
  );

  // „ÅäÊ∞ó„Å´ÂÖ•„Çä„Åã„Å©„ÅÜ„Åã„ÉÅ„Çß„ÉÉ„ÇØ
  const isFavorite = useCallback(
    (toiletId: string): boolean => state.favorites.some(fav => fav.toiletId === toiletId),
    [state.favorites],
  );

  // „ÅäÊ∞ó„Å´ÂÖ•„Çä„ÅÆ„Éà„Ç∞„É´
  const toggleFavorite = useCallback(
    async (toilet: ToiletLocation) => {
      if (isFavorite(toilet.id)) {
        await removeFromFavorites(toilet.id);
      } else {
        await addToFavorites(toilet);
      }
    },
    [isFavorite, removeFromFavorites, addToFavorites],
  );

  // „Ç¢„ÇØ„ÉÜ„Ç£„Éì„ÉÜ„Ç£„ÇíË™≠„ÅøËæº„Åø
  const loadActivities = useCallback(async () => {
    if (!user) return;

    try {
      const activities = await historyService.getUserActivities(user.uid);
      setState(prev => ({ ...prev, activities }));
    } catch (error) {
      console.error('Failed to load activities:', error);
    }
  }, [user]);

  // „É¶„Éº„Ç∂„ÉºÁµ±Ë®à„ÇíË™≠„ÅøËæº„Åø
  const loadUserStats = useCallback(async () => {
    if (!user) return;

    try {
      const stats = await historyService.getUserStats(user.uid);
      setState(prev => ({ ...prev, userStats: stats }));
    } catch (error) {
      console.error('Failed to load user stats:', error);
    }
  }, [user]);

  // „Çà„ÅèË¶ã„Çã„Éà„Ç§„É¨„ÇíÂèñÂæó
  const getFrequentlyViewed = useCallback(
    async (limit: number = 10): Promise<ViewHistory[]> => {
      if (!user) return [];

      try {
        return await historyService.getFrequentlyViewed(user.uid, limit);
      } catch (error) {
        console.error('Failed to get frequently viewed:', error);
        return [];
      }
    },
    [user],
  );

  // ÊúÄËøë„ÅÆ„Ç¢„ÇØ„ÉÜ„Ç£„Éì„ÉÜ„Ç£„ÇíÂèñÂæóÔºàÂÖ®„É¶„Éº„Ç∂„ÉºÔºâ
  const getRecentActivities = useCallback(async (limit: number = 20): Promise<UserActivity[]> => {
    try {
      return await historyService.getRecentActivities(limit);
    } catch (error) {
      console.error('Failed to get recent activities:', error);
      return [];
    }
  }, []);

  // ÂÖ®„Éá„Éº„Çø„ÅÆÂÜçË™≠„ÅøËæº„Åø
  const refresh = useCallback(async () => {
    if (!user) return;

    setState(prev => ({ ...prev, isLoading: true }));

    try {
      await Promise.all([loadViewHistory(), loadFavorites(), loadActivities(), loadUserStats()]);
    } catch (error) {
      console.error('Failed to refresh data:', error);
    } finally {
      setState(prev => ({ ...prev, isLoading: false }));
    }
  }, [user, loadViewHistory, loadFavorites, loadActivities, loadUserStats]);

  // ÂàùÊúü„Éá„Éº„Çø„ÅÆË™≠„ÅøËæº„Åø
  useEffect(() => {
    if (user) {
      refresh();
    }
  }, [user, refresh]);

  // Áµ±Ë®à„ÅÆ‰æøÂà©„É°„ÇΩ„ÉÉ„Éâ
  const getActivityCount = useCallback(
    (days: number = 7): number => {
      const cutoffDate = new Date();
      cutoffDate.setDate(cutoffDate.getDate() - days);

      return state.activities.filter(activity => activity.createdAt >= cutoffDate).length;
    },
    [state.activities],
  );

  const getViewCountToday = useCallback((): number => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    return state.viewHistory.filter(view => view.viewedAt >= today).length;
  }, [state.viewHistory]);

  return {
    // Áä∂ÊÖã
    viewHistory: state.viewHistory,
    favorites: state.favorites,
    activities: state.activities,
    userStats: state.userStats,
    isLoading: state.isLoading,
    isUpdating: state.isUpdating,

    // „Ç¢„ÇØ„Ç∑„Éß„É≥
    recordView,
    loadViewHistory,
    clearViewHistory,
    loadFavorites,
    addToFavorites,
    removeFromFavorites,
    toggleFavorite,
    loadActivities,
    loadUserStats,
    getFrequentlyViewed,
    getRecentActivities,
    refresh,

    // Ë®àÁÆóÂÄ§„Éª„Éò„É´„Éë„Éº
    isFavorite,
    favoriteCount: state.favorites.length,
    historyCount: state.viewHistory.length,
    activityCount: state.activities.length,
    getActivityCount,
    getViewCountToday,
    hasHistory: state.viewHistory.length > 0,
    hasFavorites: state.favorites.length > 0,
  };
};

import { useState, useCallback, useEffect } from 'react';
import { AppState, AppStateStatus } from 'react-native';
import { PushNotification, NotificationSettings } from '../types/post';
import { notificationService } from '../services/notificationService';
import { useAuth } from '../stores/authStore';

interface UseNotificationsState {
  notifications: PushNotification[];
  settings: NotificationSettings | null;
  unreadCount: number;
  isLoading: boolean;
  isInitialized: boolean;
}

export const useNotifications = () => {
  const { user } = useAuth();
  const [state, setState] = useState<UseNotificationsState>({
    notifications: [],
    settings: null,
    unreadCount: 0,
    isLoading: false,
    isInitialized: false,
  });

  // ÈÄöÁü•„Ç∑„Çπ„ÉÜ„É†„ÅÆÂàùÊúüÂåñ
  const initializeNotifications = useCallback(async () => {
    if (!user || state.isInitialized) return;

    try {
      await notificationService.initialize(user.uid);
      setState(prev => ({ ...prev, isInitialized: true }));

      // ÂàùÊúü„Éá„Éº„Çø„ÅÆË™≠„ÅøËæº„Åø
      await Promise.all([loadNotifications(), loadSettings(), loadUnreadCount()]);
    } catch (error) {
      console.error('Failed to initialize notifications:', error);
    }
  }, [user, state.isInitialized]);

  // ÈÄöÁü•Â±•Ê≠¥„ÅÆË™≠„ÅøËæº„Åø
  const loadNotifications = useCallback(async () => {
    if (!user) return;

    setState(prev => ({ ...prev, isLoading: true }));

    try {
      const notifications = await notificationService.getUserNotifications(user.uid);
      setState(prev => ({
        ...prev,
        notifications,
        isLoading: false,
      }));
    } catch (error) {
      console.error('Failed to load notifications:', error);
      setState(prev => ({ ...prev, isLoading: false }));
    }
  }, [user]);

  // ÈÄöÁü•Ë®≠ÂÆö„ÅÆË™≠„ÅøËæº„Åø
  const loadSettings = useCallback(async () => {
    if (!user) return;

    try {
      const settings = await notificationService.getNotificationSettings(user.uid);
      setState(prev => ({ ...prev, settings }));
    } catch (error) {
      console.error('Failed to load notification settings:', error);
    }
  }, [user]);

  // Êú™Ë™≠Êï∞„ÅÆË™≠„ÅøËæº„Åø
  const loadUnreadCount = useCallback(async () => {
    if (!user) return;

    try {
      const unreadCount = await notificationService.getUnreadCount(user.uid);
      setState(prev => ({ ...prev, unreadCount }));
    } catch (error) {
      console.error('Failed to load unread count:', error);
    }
  }, [user]);

  // ÈÄöÁü•Ë®≠ÂÆö„ÅÆÊõ¥Êñ∞
  const updateSettings = useCallback(
    async (updates: Partial<NotificationSettings>) => {
      if (!user) return;

      try {
        await notificationService.updateNotificationSettings(user.uid, updates);
        setState(prev => ({
          ...prev,
          settings: prev.settings ? { ...prev.settings, ...updates } : null,
        }));
      } catch (error) {
        console.error('Failed to update notification settings:', error);
        throw error;
      }
    },
    [user],
  );

  // ÈÄöÁü•„ÇíÊó¢Ë™≠„Å´„Åô„Çã
  const markAsRead = useCallback(async (notificationId: string) => {
    try {
      await notificationService.markAsRead(notificationId);

      setState(prev => ({
        ...prev,
        notifications: prev.notifications.map(notification =>
          notification.id === notificationId ? { ...notification, isRead: true } : notification,
        ),
        unreadCount: Math.max(0, prev.unreadCount - 1),
      }));
    } catch (error) {
      console.error('Failed to mark notification as read:', error);
    }
  }, []);

  // ÂÖ®ÈÄöÁü•„ÇíÊó¢Ë™≠„Å´„Åô„Çã
  const markAllAsRead = useCallback(async () => {
    if (!user) return;

    try {
      await notificationService.markAllAsRead(user.uid);

      setState(prev => ({
        ...prev,
        notifications: prev.notifications.map(notification => ({
          ...notification,
          isRead: true,
        })),
        unreadCount: 0,
      }));
    } catch (error) {
      console.error('Failed to mark all notifications as read:', error);
    }
  }, [user]);

  // ÈÄöÁü•„ÅÆÊâãÂãïÈÄÅ‰ø°Ôºà„Éá„Éê„ÉÉ„Ç∞Áî®Ôºâ
  const sendTestNotification = useCallback(
    async (title: string, body: string) => {
      if (!user) return;

      try {
        await notificationService.sendNotification(user.uid, 'community_news' as any, title, body);

        // ÈÄöÁü•Â±•Ê≠¥„ÇíÂÜçË™≠„ÅøËæº„Åø
        setTimeout(() => {
          loadNotifications();
          loadUnreadCount();
        }, 1000);
      } catch (error) {
        console.error('Failed to send test notification:', error);
      }
    },
    [user, loadNotifications, loadUnreadCount],
  );

  // „Éê„ÉÉ„Ç∏ÂèñÂæóÈÄöÁü•
  const sendBadgeNotification = useCallback(
    async (badgeName: string, badgeId: string) => {
      if (!user) return;

      try {
        await notificationService.sendBadgeUnlockedNotification(user.uid, badgeName, badgeId);

        // ÈÄöÁü•Â±•Ê≠¥„ÇíÊõ¥Êñ∞
        setTimeout(() => {
          loadNotifications();
          loadUnreadCount();
        }, 500);
      } catch (error) {
        console.error('Failed to send badge notification:', error);
      }
    },
    [user, loadNotifications, loadUnreadCount],
  );

  // „ÄåÂΩπ„Å´Á´ã„Å£„Åü„ÄçÈÄöÁü•
  const sendHelpfulVoteNotification = useCallback(
    async (toiletTitle: string, toiletId: string) => {
      if (!user) return;

      try {
        await notificationService.sendHelpfulVoteNotification(user.uid, toiletTitle, toiletId);

        // ÈÄöÁü•Â±•Ê≠¥„ÇíÊõ¥Êñ∞
        setTimeout(() => {
          loadNotifications();
          loadUnreadCount();
        }, 500);
      } catch (error) {
        console.error('Failed to send helpful vote notification:', error);
      }
    },
    [user, loadNotifications, loadUnreadCount],
  );

  // „Ç¢„Éó„É™Áä∂ÊÖãÂ§âÊõ¥„ÅÆÁõ£Ë¶ñ
  useEffect(() => {
    const handleAppStateChange = (nextAppState: AppStateStatus) => {
      if (nextAppState === 'active' && user) {
        // „Ç¢„Éó„É™„Åå„Ç¢„ÇØ„ÉÜ„Ç£„Éñ„Å´„Å™„Å£„Åü„ÇâÊú™Ë™≠Êï∞„ÇíÊõ¥Êñ∞
        loadUnreadCount();
      }
    };

    const subscription = AppState.addEventListener('change', handleAppStateChange);
    return () => subscription?.remove();
  }, [user, loadUnreadCount]);

  // ÂàùÊúüÂåñ
  useEffect(() => {
    if (user && !state.isInitialized) {
      initializeNotifications();
    }
  }, [user, state.isInitialized, initializeNotifications]);

  // ÂÆöÊúüÁöÑ„Å™Êú™Ë™≠Êï∞Êõ¥Êñ∞
  useEffect(() => {
    if (!user) return;

    const interval = setInterval(() => {
      loadUnreadCount();
    }, 60000); // 1ÂàÜ„Åî„Å®

    return () => clearInterval(interval);
  }, [user, loadUnreadCount]);

  return {
    // Áä∂ÊÖã
    notifications: state.notifications,
    settings: state.settings,
    unreadCount: state.unreadCount,
    isLoading: state.isLoading,
    isInitialized: state.isInitialized,

    // „Ç¢„ÇØ„Ç∑„Éß„É≥
    loadNotifications,
    loadSettings,
    updateSettings,
    markAsRead,
    markAllAsRead,
    sendTestNotification,
    sendBadgeNotification,
    sendHelpfulVoteNotification,
    refresh: () => Promise.all([loadNotifications(), loadSettings(), loadUnreadCount()]),
  };
};

import { useState, useCallback, useEffect, useMemo } from 'react';
import { Alert } from 'react-native';
import {
  Report,
  UserRestriction,
  ReportTargetType,
  ReportReason,
  ReportStatus,
  ViolationRecord,
} from '../types/post';
import { reportService } from '../services/reportService';
import { useAuth } from '../stores/authStore';

interface UseReportState {
  reports: Report[];
  userRestrictions: UserRestriction[];
  violationPoints: number;
  isLoading: boolean;
  isSubmitting: boolean;
  error: string | null;
}

export const useReport = () => {
  const { user } = useAuth();
  const [state, setState] = useState<UseReportState>({
    reports: [],
    userRestrictions: [],
    violationPoints: 0,
    isLoading: false,
    isSubmitting: false,
    error: null,
  });

  // ÈÄöÂ†±„ÇíÈÄÅ‰ø°
  const submitReport = useCallback(
    async (
      targetType: ReportTargetType,
      targetId: string,
      reason: ReportReason,
      description?: string,
      evidence?: string[],
    ) => {
      if (!user) {
        Alert.alert('„Ç®„É©„Éº', '„É≠„Ç∞„Ç§„É≥„ÅåÂøÖË¶Å„Åß„Åô');
        return false;
      }

      setState(prev => ({ ...prev, isSubmitting: true, error: null }));

      try {
        await reportService.submitReport(
          user.uid,
          targetType,
          targetId,
          reason,
          description,
          evidence,
        );

        setState(prev => ({ ...prev, isSubmitting: false }));

        Alert.alert('ÈÄöÂ†±ÂÆå‰∫Ü', 'ÈÄöÂ†±„ÇíÂèó„Åë‰ªò„Åë„Åæ„Åó„Åü„ÄÇÁ¢∫Ë™çÂæå„ÄÅÈÅ©Âàá„Å™ÂØæÂøú„ÇíË°å„ÅÑ„Åæ„Åô„ÄÇ', [
          { text: 'OK' },
        ]);

        // „É¶„Éº„Ç∂„Éº„ÅÆÈÄöÂ†±Â±•Ê≠¥„ÇíÊõ¥Êñ∞
        await loadUserReports();

        return true;
      } catch (error) {
        console.error('Failed to submit report:', error);
        setState(prev => ({
          ...prev,
          isSubmitting: false,
          error: error instanceof Error ? error.message : 'ÈÄöÂ†±„ÅÆÈÄÅ‰ø°„Å´Â§±Êïó„Åó„Åæ„Åó„Åü',
        }));

        Alert.alert('„Ç®„É©„Éº', 'ÈÄöÂ†±„ÅÆÈÄÅ‰ø°„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
        return false;
      }
    },
    [user],
  );

  // „É¶„Éº„Ç∂„Éº„ÅÆÈÄöÂ†±Â±•Ê≠¥„ÇíË™≠„ÅøËæº„Åø
  const loadUserReports = useCallback(async () => {
    if (!user) return;

    setState(prev => ({ ...prev, isLoading: true }));

    try {
      const reports = await reportService.getUserReports(user.uid);
      setState(prev => ({
        ...prev,
        reports,
        isLoading: false,
      }));
    } catch (error) {
      console.error('Failed to load user reports:', error);
      setState(prev => ({
        ...prev,
        isLoading: false,
        error: 'ÈÄöÂ†±Â±•Ê≠¥„ÅÆË™≠„ÅøËæº„Åø„Å´Â§±Êïó„Åó„Åæ„Åó„Åü',
      }));
    }
  }, [user]);

  // „É¶„Éº„Ç∂„Éº„ÅÆÂà∂ÈôêÁä∂Ê≥Å„ÇíË™≠„ÅøËæº„Åø
  const loadUserRestrictions = useCallback(async () => {
    if (!user) return;

    try {
      const restrictions = await reportService.getUserRestrictions(user.uid);
      const violationPoints = await reportService.getUserViolationPoints(user.uid);

      setState(prev => ({
        ...prev,
        userRestrictions: restrictions,
        violationPoints,
      }));
    } catch (error) {
      console.error('Failed to load user restrictions:', error);
    }
  }, [user]);

  // „É¶„Éº„Ç∂„Éº„ÅåÁâπÂÆö„ÅÆ„Ç¢„ÇØ„Ç∑„Éß„É≥„ÅßÂà∂Èôê„Åï„Çå„Å¶„ÅÑ„Çã„Åã„ÉÅ„Çß„ÉÉ„ÇØ
  const checkRestriction = useCallback(
    async (
      action: 'post' | 'comment' | 'review' | 'vote',
    ): Promise<{ restricted: boolean; reason?: string; endDate?: Date }> => {
      if (!user) {
        return { restricted: false };
      }

      try {
        return await reportService.isUserRestricted(user.uid, action);
      } catch (error) {
        console.error('Failed to check restriction:', error);
        return { restricted: false };
      }
    },
    [user],
  );

  // Âà∂Èôê„ÉÅ„Çß„ÉÉ„ÇØ‰ªò„Åç„Ç¢„ÇØ„Ç∑„Éß„É≥ÂÆüË°å
  const executeWithRestrictionCheck = useCallback(
    async (
      action: 'post' | 'comment' | 'review' | 'vote',
      callback: () => Promise<void> | void,
      actionName: string = '„Åì„ÅÆÊìç‰Ωú',
    ): Promise<boolean> => {
      const restriction = await checkRestriction(action);

      if (restriction.restricted) {
        const endDateText = restriction.endDate
          ? `Âà∂Èôê„ÅØ${restriction.endDate.toLocaleDateString('ja-JP')}„Åæ„ÅßÊúâÂäπ„Åß„Åô„ÄÇ`
          : '';

        Alert.alert(`${actionName}„ÅåÂà∂Èôê„Åï„Çå„Å¶„ÅÑ„Åæ„Åô`, `${restriction.reason}\n${endDateText}`, [
          { text: 'OK' },
        ]);
        return false;
      }

      try {
        await callback();
        return true;
      } catch (error) {
        console.error('Action failed:', error);
        return false;
      }
    },
    [checkRestriction],
  );

  // „É°„É¢Âåñ„Åï„Çå„ÅüË°®Á§∫Âêç„Éû„ÉÉ„Éî„É≥„Ç∞
  const reasonDisplayNames = useMemo(
    () => ({
      [ReportReason.INAPPROPRIATE_CONTENT]: '‰∏çÈÅ©Âàá„Å™„Ç≥„É≥„ÉÜ„É≥„ÉÑ',
      [ReportReason.SPAM]: '„Çπ„Éë„É†',
      [ReportReason.HARASSMENT]: 'Â´å„Åå„Çâ„Åõ',
      [ReportReason.FAKE_INFORMATION]: 'ËôöÂÅΩÊÉÖÂ†±',
      [ReportReason.COPYRIGHT_VIOLATION]: 'Ëëó‰ΩúÊ®©‰æµÂÆ≥',
      [ReportReason.PRIVACY_VIOLATION]: '„Éó„É©„Ç§„Éê„Ç∑„Éº‰æµÂÆ≥',
      [ReportReason.COMMERCIAL_SPAM]: 'ÂïÜÊ•≠ÁöÑ„Çπ„Éë„É†',
      [ReportReason.HATE_SPEECH]: '„Éò„Ç§„Éà„Çπ„Éî„Éº„ÉÅ',
      [ReportReason.OTHER]: '„Åù„ÅÆ‰ªñ',
    }),
    [],
  );

  const statusDisplayNames = useMemo(
    () => ({
      [ReportStatus.PENDING]: 'ÂØ©ÊüªÂæÖ„Å°',
      [ReportStatus.UNDER_REVIEW]: 'ÂØ©Êüª‰∏≠',
      [ReportStatus.RESOLVED]: 'Ëß£Ê±∫Ê∏à„Åø',
      [ReportStatus.DISMISSED]: 'Âç¥‰∏ã',
      [ReportStatus.AUTO_RESOLVED]: 'Ëá™ÂãïËß£Ê±∫',
    }),
    [],
  );

  const statusColors = useMemo(
    () => ({
      [ReportStatus.PENDING]: '#FF9800',
      [ReportStatus.UNDER_REVIEW]: '#2196F3',
      [ReportStatus.RESOLVED]: '#4CAF50',
      [ReportStatus.DISMISSED]: '#757575',
      [ReportStatus.AUTO_RESOLVED]: '#4CAF50',
    }),
    [],
  );

  // ÈÄöÂ†±ÁêÜÁî±„ÅÆË°®Á§∫Âêç„ÇíÂèñÂæó
  const getReasonDisplayName = useCallback(
    (reason: ReportReason): string => reasonDisplayNames[reason] || reason,
    [reasonDisplayNames],
  );

  // ÈÄöÂ†±„Çπ„ÉÜ„Éº„Çø„Çπ„ÅÆË°®Á§∫Âêç„ÇíÂèñÂæó
  const getStatusDisplayName = useCallback(
    (status: ReportStatus): string => statusDisplayNames[status] || status,
    [statusDisplayNames],
  );

  // „Çπ„ÉÜ„Éº„Çø„Çπ„ÅÆËâ≤„ÇíÂèñÂæó
  const getStatusColor = useCallback(
    (status: ReportStatus): string => statusColors[status] || '#757575',
    [statusColors],
  );

  // ÂÖ®„Éá„Éº„Çø„ÅÆÂÜçË™≠„ÅøËæº„Åø
  const refresh = useCallback(async () => {
    if (!user) return;

    setState(prev => ({ ...prev, isLoading: true, error: null }));

    try {
      await Promise.all([loadUserReports(), loadUserRestrictions()]);
    } catch (error) {
      console.error('Failed to refresh data:', error);
      setState(prev => ({
        ...prev,
        error: '„Éá„Éº„Çø„ÅÆÂÜçË™≠„ÅøËæº„Åø„Å´Â§±Êïó„Åó„Åæ„Åó„Åü',
      }));
    } finally {
      setState(prev => ({ ...prev, isLoading: false }));
    }
  }, [user, loadUserReports, loadUserRestrictions]);

  // ÂàùÊúü„Éá„Éº„Çø„ÅÆË™≠„ÅøËæº„Åø
  useEffect(() => {
    if (user) {
      refresh();
    }
  }, [user, refresh]);

  // „É°„É¢Âåñ„Åï„Çå„ÅüË®àÁÆóÂÄ§
  const hasActiveRestrictions = useMemo(
    () => state.userRestrictions.length > 0,
    [state.userRestrictions.length],
  );

  const getActiveRestrictionTypes = useMemo(
    () => state.userRestrictions.map(r => r.type),
    [state.userRestrictions],
  );

  const isNearViolationLimit = useMemo(
    () => state.violationPoints >= 15, // 20„Éù„Ç§„É≥„Éà„ÅßÊ∞∏‰πÖÂÅúÊ≠¢„Å™„ÅÆ„ÅßË≠¶Âëä
    [state.violationPoints],
  );

  const reportCount = useMemo(() => state.reports.length, [state.reports.length]);
  const activeRestrictionCount = useMemo(
    () => state.userRestrictions.length,
    [state.userRestrictions.length],
  );
  const isHighRiskUser = useMemo(() => state.violationPoints >= 10, [state.violationPoints]);

  return {
    // Áä∂ÊÖã
    reports: state.reports,
    userRestrictions: state.userRestrictions,
    violationPoints: state.violationPoints,
    isLoading: state.isLoading,
    isSubmitting: state.isSubmitting,
    error: state.error,

    // „Ç¢„ÇØ„Ç∑„Éß„É≥
    submitReport,
    loadUserReports,
    loadUserRestrictions,
    checkRestriction,
    executeWithRestrictionCheck,
    refresh,

    // „Éò„É´„Éë„ÉºÈñ¢Êï∞
    getReasonDisplayName,
    getStatusDisplayName,
    getStatusColor,

    // Ë®àÁÆóÂÄ§Ôºà„É°„É¢ÂåñÊ∏à„ÅøÔºâ
    hasActiveRestrictions,
    getActiveRestrictionTypes,
    isNearViolationLimit,
    reportCount,
    activeRestrictionCount,
    isHighRiskUser,
  };
};

import { useState, useCallback, useEffect, useMemo, useRef } from 'react';
import { ToiletLocation } from '../types/maps';
import { SearchFilters, SearchResult, SortOption, SavedSearch, SearchHistory } from '../types/post';
import { searchService } from '../services/searchService';
import { useAuth } from '../stores/authStore';
import { useLocation } from './useLocation';

// Debounce utility function
const useDebounce = <T extends (...args: any[]) => any>(callback: T, delay: number) => {
  const timeoutRef = useRef<NodeJS.Timeout | null>(null);

  return useCallback(
    (...args: any[]) => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
      timeoutRef.current = setTimeout(() => callback(...args), delay);
    },
    [callback, delay],
  );
};

interface UseSearchState {
  results: ToiletLocation[];
  isLoading: boolean;
  isSearching: boolean;
  totalCount: number;
  hasMore: boolean;
  searchTime: number;
  error: string | null;
  savedSearches: SavedSearch[];
  searchHistory: SearchHistory[];
}

const initialFilters: SearchFilters = {
  query: '',
  sortBy: SortOption.RELEVANCE,
};

export const useSearch = () => {
  const { user } = useAuth();
  const { getCurrentLocation } = useLocation();

  const [filters, setFilters] = useState<SearchFilters>(initialFilters);
  const [state, setState] = useState<UseSearchState>({
    results: [],
    isLoading: false,
    isSearching: false,
    totalCount: 0,
    hasMore: false,
    searchTime: 0,
    error: null,
    savedSearches: [],
    searchHistory: [],
  });

  const [currentPage, setCurrentPage] = useState(0);
  const PAGE_SIZE = 20;

  // Ê§úÁ¥¢ÂÆüË°åÔºàÂÜÖÈÉ®Áî®„ÄÅ„Éá„Éê„Ç¶„É≥„Çπ„Å™„ÅóÔºâ
  const executeSearch = useCallback(
    async (searchFilters: SearchFilters, page: number = 0, append: boolean = false) => {
      setState(prev => ({
        ...prev,
        isSearching: true,
        error: null,
        ...(page === 0 && !append ? { results: [] } : {}),
      }));

      try {
        // ÁèæÂú®‰ΩçÁΩÆ„ÇíÂèñÂæó
        let userLocation;
        try {
          userLocation = await getCurrentLocation();
        } catch (error) {
          console.log('Location not available for search');
        }

        // Ê§úÁ¥¢ÂÆüË°å
        const result: SearchResult = await searchService.searchToilets(
          searchFilters,
          userLocation || undefined,
          PAGE_SIZE,
          page * PAGE_SIZE,
        );

        setState(prev => ({
          ...prev,
          results: append ? [...prev.results, ...result.toilets] : result.toilets,
          totalCount: result.totalCount,
          hasMore: result.hasMore,
          searchTime: result.searchTime,
          isSearching: false,
        }));

        // Ê§úÁ¥¢Â±•Ê≠¥„Çí‰øùÂ≠òÔºà„ÇØ„Ç®„É™„Åå„ÅÇ„ÇãÂ†¥Âêà„ÅÆ„ÅøÔºâ
        if (user && searchFilters.query.trim() && !append) {
          await searchService.saveSearchHistory(user.uid, searchFilters, result.totalCount);
          await loadSearchHistory();
        }

        setCurrentPage(page);
      } catch (error) {
        console.error('Search failed:', error);
        setState(prev => ({
          ...prev,
          isSearching: false,
          error: 'Ê§úÁ¥¢„Å´Â§±Êïó„Åó„Åæ„Åó„Åü',
        }));
      }
    },
    [user, getCurrentLocation],
  );

  // „Éá„Éê„Ç¶„É≥„Çπ‰ªò„Åç„ÅÆÊ§úÁ¥¢Èñ¢Êï∞
  const debouncedSearch = useDebounce(executeSearch, 300);

  // „Éï„Ç£„É´„Çø„ÉºÊõ¥Êñ∞ÔºàÊúÄÈÅ©ÂåñÁâàÔºâ
  const updateFilters = useCallback(
    (updates: Partial<SearchFilters>) => {
      setFilters(prevFilters => {
        const newFilters = { ...prevFilters, ...updates };

        // „ÇØ„Ç®„É™„ÅåÂ§âÊõ¥„Åï„Çå„ÅüÂ†¥Âêà„ÅØËá™ÂãïÊ§úÁ¥¢Ôºà„Éá„Éê„Ç¶„É≥„ÇπÔºâ
        if (updates.query !== undefined) {
          debouncedSearch(newFilters, 0, false);
        }

        return newFilters;
      });
    },
    [debouncedSearch],
  );

  // Ê¨°„ÅÆ„Éö„Éº„Ç∏„ÇíË™≠„ÅøËæº„Åø
  const loadMore = useCallback(async () => {
    if (!state.hasMore || state.isSearching) return;

    await executeSearch(filters, currentPage + 1, true);
  }, [state.hasMore, state.isSearching, filters, currentPage, executeSearch]);

  // Ê§úÁ¥¢„Çí„É™„Çª„ÉÉ„Éà
  const resetSearch = useCallback(() => {
    setFilters(initialFilters);
    setState(prev => ({
      ...prev,
      results: [],
      totalCount: 0,
      hasMore: false,
      searchTime: 0,
      error: null,
    }));
    setCurrentPage(0);
  }, []);

  // ‰øùÂ≠ò„Åï„Çå„ÅüÊ§úÁ¥¢Êù°‰ª∂„ÇíË™≠„ÅøËæº„Åø
  const loadSavedSearches = useCallback(async () => {
    if (!user) return;

    try {
      const searches = await searchService.getSavedSearches(user.uid);
      setState(prev => ({ ...prev, savedSearches: searches }));
    } catch (error) {
      console.error('Failed to load saved searches:', error);
    }
  }, [user]);

  // Ê§úÁ¥¢Â±•Ê≠¥„ÇíË™≠„ÅøËæº„Åø
  const loadSearchHistory = useCallback(async () => {
    if (!user) return;

    try {
      const history = await searchService.getSearchHistory(user.uid);
      setState(prev => ({ ...prev, searchHistory: history }));
    } catch (error) {
      console.error('Failed to load search history:', error);
    }
  }, [user]);

  // Ê§úÁ¥¢Êù°‰ª∂„Çí‰øùÂ≠ò
  const saveFavoriteSearch = useCallback(
    async (name: string) => {
      if (!user) return;

      try {
        await searchService.saveFavoriteSearch(user.uid, name, filters);
        await loadSavedSearches();
      } catch (error) {
        console.error('Failed to save favorite search:', error);
        throw error;
      }
    },
    [user, filters, loadSavedSearches],
  );

  // ‰øùÂ≠ò„Åï„Çå„ÅüÊ§úÁ¥¢Êù°‰ª∂„ÇíÂâäÈô§
  const deleteSavedSearch = useCallback(
    async (searchId: string) => {
      try {
        await searchService.deleteSavedSearch(searchId);
        await loadSavedSearches();
      } catch (error) {
        console.error('Failed to delete saved search:', error);
        throw error;
      }
    },
    [loadSavedSearches],
  );

  // ‰øùÂ≠ò„Åï„Çå„ÅüÊ§úÁ¥¢Êù°‰ª∂„ÇíÈÅ©Áî®
  const applySavedSearch = useCallback(
    (savedSearch: SavedSearch) => {
      setFilters(savedSearch.filters);
      executeSearch(savedSearch.filters, 0, false);
    },
    [executeSearch],
  );

  // Ê§úÁ¥¢Â±•Ê≠¥„Åã„ÇâÂÜçÊ§úÁ¥¢
  const applyHistorySearch = useCallback(
    (history: SearchHistory) => {
      setFilters(history.filters);
      executeSearch(history.filters, 0, false);
    },
    [executeSearch],
  );

  // „ÇØ„Ç§„ÉÉ„ÇØÊ§úÁ¥¢Ôºà„Çà„Åè‰Ωø„Çè„Çå„ÇãÊ§úÁ¥¢Êù°‰ª∂Ôºâ
  const quickSearch = useCallback(
    async (type: 'nearby' | 'accessible' | 'highRated' | 'newest') => {
      let quickFilters: SearchFilters;

      switch (type) {
        case 'nearby':
          quickFilters = { ...initialFilters, sortBy: SortOption.DISTANCE, distance: 1 };
          break;
        case 'accessible':
          quickFilters = { ...initialFilters, isAccessible: true, sortBy: SortOption.DISTANCE };
          break;
        case 'highRated':
          quickFilters = { ...initialFilters, rating: 4, sortBy: SortOption.RATING };
          break;
        case 'newest':
          quickFilters = { ...initialFilters, sortBy: SortOption.NEWEST };
          break;
        default:
          quickFilters = initialFilters;
      }

      setFilters(quickFilters);
      await executeSearch(quickFilters, 0, false);
    },
    [executeSearch],
  );

  // ÂàùÊúü„Éá„Éº„Çø„ÅÆË™≠„ÅøËæº„Åø
  useEffect(() => {
    if (user) {
      loadSavedSearches();
      loadSearchHistory();
    }
  }, [user, loadSavedSearches, loadSearchHistory]);

  // „Éï„Ç£„É´„Çø„Éº„ÅÆ‰æøÂà©„É°„ÇΩ„ÉÉ„Éâ
  const clearFilters = useCallback(() => {
    setFilters(initialFilters);
  }, []);

  // „É°„É¢Âåñ„Åï„Çå„ÅüË®àÁÆóÂÄ§
  const hasActiveFilters = useMemo(
    () =>
      filters.toiletType !== undefined ||
      filters.isAccessible !== undefined ||
      filters.hasWashlet !== undefined ||
      filters.rating !== undefined ||
      filters.distance !== undefined ||
      filters.openNow !== undefined ||
      filters.sortBy !== SortOption.RELEVANCE,
    [filters],
  );

  const hasResults = useMemo(() => state.results.length > 0, [state.results.length]);

  const isEmpty = useMemo(
    () => !state.isSearching && state.results.length === 0 && filters.query.trim() !== '',
    [state.isSearching, state.results.length, filters.query],
  );

  return {
    // Áä∂ÊÖã
    results: state.results,
    isLoading: state.isLoading,
    isSearching: state.isSearching,
    totalCount: state.totalCount,
    hasMore: state.hasMore,
    searchTime: state.searchTime,
    error: state.error,
    filters,
    savedSearches: state.savedSearches,
    searchHistory: state.searchHistory,

    // „Ç¢„ÇØ„Ç∑„Éß„É≥
    search: () => executeSearch(filters, 0, false),
    updateFilters,
    loadMore,
    resetSearch,
    clearFilters,
    quickSearch,
    saveFavoriteSearch,
    deleteSavedSearch,
    applySavedSearch,
    applyHistorySearch,
    refresh: () => Promise.all([loadSavedSearches(), loadSearchHistory()]),

    // Ë®àÁÆóÂÄ§Ôºà„É°„É¢ÂåñÊ∏à„ÅøÔºâ
    hasActiveFilters,
    hasResults,
    isEmpty,
  };
};

import { useState, useCallback } from 'react';
import { Alert } from 'react-native';
import {
  ToiletPostForm,
  initialToiletPostForm,
  validateToiletPost,
  validateImages,
  ToiletRatings,
  DetailedToiletEquipment,
  ToiletInfo,
  initialToiletInfo,
} from '../types/post';
import { ToiletType, Coordinate } from '../types/maps';
import { useAuth } from '../stores/authStore';
import { firestoreService } from '../services/firestoreService';
import { useReport } from './useReport';

interface UseToiletPostState {
  form: ToiletPostForm;
  isLoading: boolean;
  errors: string[];
}

export const useToiletPost = () => {
  const { user } = useAuth();
  const { executeWithRestrictionCheck } = useReport();
  const [state, setState] = useState<UseToiletPostState>({
    form: initialToiletPostForm,
    isLoading: false,
    errors: [],
  });

  // „Éï„Ç©„Éº„É†„Éï„Ç£„Éº„É´„Éâ„ÅÆÊõ¥Êñ∞
  const updateForm = useCallback((updates: Partial<ToiletPostForm>) => {
    setState(prev => ({
      ...prev,
      form: { ...prev.form, ...updates },
      errors: [], // „Ç®„É©„Éº„Çí„ÇØ„É™„Ç¢
    }));
  }, []);

  // ÊñΩË®≠Âêç„ÅÆÊõ¥Êñ∞
  const updateFacilityTitle = useCallback(
    (facilityTitle: string) => {
      updateForm({ facilityTitle });
    },
    [updateForm],
  );

  // ÊñΩË®≠Ë™¨Êòé„ÅÆÊõ¥Êñ∞
  const updateFacilityDescription = useCallback(
    (facilityDescription: string) => {
      updateForm({ facilityDescription });
    },
    [updateForm],
  );

  // „Éà„Ç§„É¨„Çø„Ç§„Éó„ÅÆÊõ¥Êñ∞
  const updateType = useCallback(
    (type: ToiletType) => {
      updateForm({ type });
    },
    [updateForm],
  );

  // Ë§áÊï∞„Éà„Ç§„É¨ÁÆ°ÁêÜ
  const addToilet = useCallback(() => {
    setState(prev => {
      const newToiletId = `toilet_${Date.now()}`;
      const newToilet: ToiletInfo = {
        ...initialToiletInfo,
        id: newToiletId,
        title: `„Éà„Ç§„É¨${prev.form.toilets.length + 1}`,
      };

      return {
        ...prev,
        form: {
          ...prev.form,
          toilets: [...prev.form.toilets, newToilet],
        },
        errors: [],
      };
    });
  }, []);

  const removeToilet = useCallback((toiletId: string) => {
    setState(prev => ({
      ...prev,
      form: {
        ...prev.form,
        toilets: prev.form.toilets.filter(toilet => toilet.id !== toiletId),
      },
      errors: [],
    }));
  }, []);

  const updateToilet = useCallback((toiletId: string, updates: Partial<ToiletInfo>) => {
    setState(prev => ({
      ...prev,
      form: {
        ...prev.form,
        toilets: prev.form.toilets.map(toilet =>
          toilet.id === toiletId ? { ...toilet, ...updates } : toilet,
        ),
      },
      errors: [],
    }));
  }, []);

  // ‰ΩçÁΩÆÊÉÖÂ†±„ÅÆÊõ¥Êñ∞
  const updateLocation = useCallback(
    (location: Coordinate) => {
      updateForm({ location });
    },
    [updateForm],
  );

  // ÁâπÂÆö„Éà„Ç§„É¨„ÅÆË®≠ÂÇôÊÉÖÂ†±„ÇíÊõ¥Êñ∞
  const updateToiletFacility = useCallback(
    (toiletIndex: number, facilityKey: string, value: boolean) => {
      setState(prev => {
        const updatedToilets = [...prev.form.toilets];
        if (updatedToilets[toiletIndex]) {
          updatedToilets[toiletIndex] = {
            ...updatedToilets[toiletIndex],
            facilities: {
              ...updatedToilets[toiletIndex].facilities,
              [facilityKey]: value,
            },
          };
        }
        return {
          ...prev,
          form: {
            ...prev.form,
            toilets: updatedToilets,
          },
          errors: [],
        };
      });
    },
    [],
  );

  // Âñ∂Ê•≠ÊôÇÈñì„ÅÆÊõ¥Êñ∞
  const updateOpeningHours = useCallback((updates: Partial<ToiletPostForm['openingHours']>) => {
    setState(prev => ({
      ...prev,
      form: {
        ...prev.form,
        openingHours: {
          ...prev.form.openingHours,
          ...updates,
        },
      },
      errors: [],
    }));
  }, []);

  // ËøΩÂä†ÊÉÖÂ†±„ÅÆÊõ¥Êñ∞
  const updateAdditionalInfo = useCallback(
    (additionalInfo: string) => {
      updateForm({ additionalInfo });
    },
    [updateForm],
  );

  // ÊñΩË®≠ÁîªÂÉè„ÅÆËøΩÂä†
  const addImage = useCallback((imagePath: string) => {
    setState(prev => {
      const newImages = [...prev.form.facilityImages, imagePath];
      const validation = validateImages(newImages);

      if (!validation.isValid) {
        Alert.alert('„Ç®„É©„Éº', validation.errors.join('\n'));
        return prev;
      }

      return {
        ...prev,
        form: {
          ...prev.form,
          facilityImages: newImages,
        },
        errors: [],
      };
    });
  }, []);

  // ÊñΩË®≠ÁîªÂÉè„ÅÆÂâäÈô§
  const removeImage = useCallback((index: number) => {
    setState(prev => ({
      ...prev,
      form: {
        ...prev.form,
        facilityImages: prev.form.facilityImages.filter((_, i) => i !== index),
      },
      errors: [],
    }));
  }, []);

  // ÂÄãÂà•„Éà„Ç§„É¨ÁîªÂÉè„ÅÆËøΩÂä†
  const addToiletImage = useCallback((toiletId: string, imagePath: string) => {
    setState(prev => {
      const toiletIndex = prev.form.toilets.findIndex(t => t.id === toiletId);
      if (toiletIndex === -1) return prev;

      const toilet = prev.form.toilets[toiletIndex];
      const newImages = [...toilet.images, imagePath];
      const validation = validateImages(newImages);

      if (!validation.isValid) {
        Alert.alert('„Ç®„É©„Éº', validation.errors.join('\n'));
        return prev;
      }

      return {
        ...prev,
        form: {
          ...prev.form,
          toilets: prev.form.toilets.map(t =>
            t.id === toiletId ? { ...t, images: newImages } : t,
          ),
        },
        errors: [],
      };
    });
  }, []);

  // ÂÄãÂà•„Éà„Ç§„É¨ÁîªÂÉè„ÅÆÂâäÈô§
  const removeToiletImage = useCallback((toiletId: string, index: number) => {
    setState(prev => ({
      ...prev,
      form: {
        ...prev.form,
        toilets: prev.form.toilets.map(t =>
          t.id === toiletId ? { ...t, images: t.images.filter((_, i) => i !== index) } : t,
        ),
      },
      errors: [],
    }));
  }, []);

  // „Éê„É™„Éá„Éº„Ç∑„Éß„É≥ÂÆüË°å
  const validateForm = useCallback(() => {
    const validation = validateToiletPost(state.form);
    const facilityImageValidation = validateImages(state.form.facilityImages);

    // ÂêÑ„Éà„Ç§„É¨„ÅÆÁîªÂÉè„ÇÇ„Éê„É™„Éá„Éº„Ç∑„Éß„É≥
    const toiletImageErrors: string[] = [];
    state.form.toilets.forEach((toilet, index) => {
      const toiletValidation = validateImages(toilet.images);
      if (!toiletValidation.isValid) {
        toiletImageErrors.push(
          ...toiletValidation.errors.map(error => `„Éà„Ç§„É¨${index + 1}: ${error}`),
        );
      }
    });

    const allErrors = [
      ...validation.errors,
      ...facilityImageValidation.errors,
      ...toiletImageErrors,
    ];

    setState(prev => ({
      ...prev,
      errors: allErrors,
    }));

    return allErrors.length === 0;
  }, [state.form]);

  // „Éï„Ç©„Éº„É†ÈÄÅ‰ø°ÔºàÂà∂Èôê„ÉÅ„Çß„ÉÉ„ÇØ‰ªò„ÅçÔºâ
  const submitForm = useCallback(async () => {
    if (!user) {
      Alert.alert('„Ç®„É©„Éº', '„É≠„Ç∞„Ç§„É≥„ÅåÂøÖË¶Å„Åß„Åô');
      return false;
    }

    // Âà∂Èôê„ÉÅ„Çß„ÉÉ„ÇØ‰ªò„Åç„ÅßÂÆüË°å
    return await executeWithRestrictionCheck(
      'post',
      async () => {
        // „Éê„É™„Éá„Éº„Ç∑„Éß„É≥
        if (!validateForm()) {
          Alert.alert('ÂÖ•Âäõ„Ç®„É©„Éº', state.errors.join('\n'));
          throw new Error('Validation failed');
        }

        setState(prev => ({ ...prev, isLoading: true }));

        try {
          // Firestore„Å∏„ÅÆ‰øùÂ≠òÂá¶ÁêÜ
          const toiletId = await firestoreService.createToilet(state.form, user.uid);

          console.log('Toilet created successfully:', toiletId);
          Alert.alert('ÊàêÂäü', '„Éà„Ç§„É¨ÊÉÖÂ†±„ÅåÊäïÁ®ø„Åï„Çå„Åæ„Åó„ÅüÔºÅ');

          // „Éï„Ç©„Éº„É†„Çí„É™„Çª„ÉÉ„Éà
          setState(prev => ({
            ...prev,
            form: initialToiletPostForm,
            isLoading: false,
            errors: [],
          }));
        } catch (error) {
          console.error('Submit error:', error);
          Alert.alert(
            '„Ç®„É©„Éº',
            error instanceof Error ? error.message : 'ÊäïÁ®ø„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇ„ÇÇ„ÅÜ‰∏ÄÂ∫¶„ÅäË©¶„Åó„Åè„Å†„Åï„ÅÑ„ÄÇ',
          );
          setState(prev => ({ ...prev, isLoading: false }));
          throw error;
        }
      },
      '„Éà„Ç§„É¨„ÅÆÊäïÁ®ø',
    );
  }, [user, validateForm, state.errors, state.form, executeWithRestrictionCheck]);

  // „Éï„Ç©„Éº„É†„ÅÆ„É™„Çª„ÉÉ„Éà
  const resetForm = useCallback(() => {
    setState({
      form: initialToiletPostForm,
      isLoading: false,
      errors: [],
    });
  }, []);

  // ÁâπÂÆö„Éà„Ç§„É¨„ÅÆË©ï‰æ°Ëª∏„ÇíÊõ¥Êñ∞
  const updateToiletRating = useCallback(
    (toiletIndex: number, ratingKey: keyof ToiletRatings, value: number) => {
      setState(prev => {
        const updatedToilets = [...prev.form.toilets];
        if (updatedToilets[toiletIndex]) {
          updatedToilets[toiletIndex] = {
            ...updatedToilets[toiletIndex],
            ratings: {
              ...updatedToilets[toiletIndex].ratings,
              [ratingKey]: value,
            },
          };
        }
        return {
          ...prev,
          form: {
            ...prev.form,
            toilets: updatedToilets,
          },
          errors: [],
        };
      });
    },
    [],
  );

  // ÁâπÂÆö„Éà„Ç§„É¨„ÅÆË©≥Á¥∞Ë®≠ÂÇô„ÇíÊõ¥Êñ∞
  const updateToiletDetailedEquipment = useCallback(
    (toiletIndex: number, updates: Partial<DetailedToiletEquipment>) => {
      setState(prev => {
        const updatedToilets = [...prev.form.toilets];
        if (updatedToilets[toiletIndex]) {
          updatedToilets[toiletIndex] = {
            ...updatedToilets[toiletIndex],
            detailedEquipment: {
              ...updatedToilets[toiletIndex].detailedEquipment,
              ...updates,
            },
          };
        }
        return {
          ...prev,
          form: {
            ...prev.form,
            toilets: updatedToilets,
          },
          errors: [],
        };
      });
    },
    [],
  );

  // ÁâπÂÆö„Éà„Ç§„É¨„ÅÆÁî∑ÊÄßÁî®Ë®≠ÂÇôÊõ¥Êñ∞
  const updateToiletMaleEquipment = useCallback(
    (
      toiletIndex: number,
      updates: Partial<NonNullable<DetailedToiletEquipment['maleEquipment']>>,
    ) => {
      setState(prev => {
        const updatedToilets = [...prev.form.toilets];
        if (updatedToilets[toiletIndex]) {
          const currentEquipment = updatedToilets[toiletIndex].detailedEquipment;
          updatedToilets[toiletIndex] = {
            ...updatedToilets[toiletIndex],
            detailedEquipment: {
              ...currentEquipment,
              maleEquipment: currentEquipment.maleEquipment
                ? {
                    ...currentEquipment.maleEquipment,
                    ...updates,
                  }
                : {
                    urinals: 0,
                    westernToilets: 0,
                    ...updates,
                  },
            },
          };
        }
        return {
          ...prev,
          form: {
            ...prev.form,
            toilets: updatedToilets,
          },
          errors: [],
        };
      });
    },
    [],
  );

  // ÁâπÂÆö„Éà„Ç§„É¨„ÅÆÂ•≥ÊÄßÁî®Ë®≠ÂÇôÊõ¥Êñ∞
  const updateToiletFemaleEquipment = useCallback(
    (
      toiletIndex: number,
      updates: Partial<NonNullable<DetailedToiletEquipment['femaleEquipment']>>,
    ) => {
      setState(prev => {
        const updatedToilets = [...prev.form.toilets];
        if (updatedToilets[toiletIndex]) {
          const currentEquipment = updatedToilets[toiletIndex].detailedEquipment;
          updatedToilets[toiletIndex] = {
            ...updatedToilets[toiletIndex],
            detailedEquipment: {
              ...currentEquipment,
              femaleEquipment: currentEquipment.femaleEquipment
                ? {
                    ...currentEquipment.femaleEquipment,
                    ...updates,
                  }
                : {
                    japaneseToilets: 0,
                    westernToilets: 0,
                    ...updates,
                  },
            },
          };
        }
        return {
          ...prev,
          form: {
            ...prev.form,
            toilets: updatedToilets,
          },
          errors: [],
        };
      });
    },
    [],
  );

  // ÁâπÂÆö„Éà„Ç§„É¨„ÅÆÂÖ±Áî®Ë®≠ÂÇôÊõ¥Êñ∞
  const updateToiletSharedEquipment = useCallback(
    (
      toiletIndex: number,
      updates: Partial<NonNullable<DetailedToiletEquipment['sharedEquipment']>>,
    ) => {
      setState(prev => {
        const updatedToilets = [...prev.form.toilets];
        if (updatedToilets[toiletIndex]) {
          const currentEquipment = updatedToilets[toiletIndex].detailedEquipment;
          updatedToilets[toiletIndex] = {
            ...updatedToilets[toiletIndex],
            detailedEquipment: {
              ...currentEquipment,
              sharedEquipment: currentEquipment.sharedEquipment
                ? {
                    ...currentEquipment.sharedEquipment,
                    ...updates,
                  }
                : {
                    japaneseToilets: 0,
                    westernToilets: 0,
                    ...updates,
                  },
            },
          };
        }
        return {
          ...prev,
          form: {
            ...prev.form,
            toilets: updatedToilets,
          },
          errors: [],
        };
      });
    },
    [],
  );

  // ÁâπÂÆö„Éà„Ç§„É¨„ÅÆËøΩÂä†Ê©üËÉΩÊõ¥Êñ∞
  const updateToiletAdditionalFeatures = useCallback(
    (
      toiletIndex: number,
      featureKey: keyof DetailedToiletEquipment['additionalFeatures'],
      value: boolean,
    ) => {
      setState(prev => {
        const updatedToilets = [...prev.form.toilets];
        if (updatedToilets[toiletIndex]) {
          updatedToilets[toiletIndex] = {
            ...updatedToilets[toiletIndex],
            detailedEquipment: {
              ...updatedToilets[toiletIndex].detailedEquipment,
              additionalFeatures: {
                ...updatedToilets[toiletIndex].detailedEquipment.additionalFeatures,
                [featureKey]: value,
              },
            },
          };
        }
        return {
          ...prev,
          form: {
            ...prev.form,
            toilets: updatedToilets,
          },
          errors: [],
        };
      });
    },
    [],
  );

  // „Ç®„É©„Éº„ÅÆ„ÇØ„É™„Ç¢
  const clearErrors = useCallback(() => {
    setState(prev => ({ ...prev, errors: [] }));
  }, []);

  return {
    // Áä∂ÊÖã
    form: state.form,
    isLoading: state.isLoading,
    errors: state.errors,
    isValid: state.errors.length === 0,

    // „Ç¢„ÇØ„Ç∑„Éß„É≥
    updateFacilityTitle,
    updateFacilityDescription,
    updateType,
    updateLocation,
    updateOpeningHours,
    updateAdditionalInfo,
    addToilet,
    removeToilet,
    updateToilet,
    addImage,
    removeImage,
    addToiletImage,
    removeToiletImage,
    validateForm,
    submitForm,
    resetForm,
    clearErrors,

    // Êñ∞„Åó„ÅÑ„Éà„Ç§„É¨Âõ∫Êúâ„ÅÆ„Ç¢„ÇØ„Ç∑„Éß„É≥
    updateToiletFacility,
    updateToiletRating,
    updateToiletDetailedEquipment,
    updateToiletMaleEquipment,
    updateToiletFemaleEquipment,
    updateToiletSharedEquipment,
    updateToiletAdditionalFeatures,

    // ÂæåÊñπ‰∫íÊèõÊÄß„ÅÆ„Åü„ÇÅ„ÅÆÈñ¢Êï∞ÔºàÊúÄÂàù„ÅÆ„Éà„Ç§„É¨„Å´ÂØæ„Åô„ÇãÊìç‰ΩúÔºâ
    updateTitle: useCallback((title: string) => {
      if (state.form.toilets.length > 0 && state.form.toilets[0]) {
        updateToilet(state.form.toilets[0].id, { title });
      }
    }, [state.form.toilets, updateToilet]),
    updateDescription: useCallback((description: string) => {
      if (state.form.toilets.length > 0 && state.form.toilets[0]) {
        updateToilet(state.form.toilets[0].id, { description });
      }
    }, [state.form.toilets, updateToilet]),
    updateAccessibility: useCallback((isAccessible: boolean) => {
      if (state.form.toilets.length > 0 && state.form.toilets[0]) {
        updateToilet(state.form.toilets[0].id, { isAccessible });
      }
    }, [state.form.toilets, updateToilet]),
    updateFacility: (facilityKey: string, value: boolean) =>
      updateToiletFacility(0, facilityKey, value),
    updateRating: (ratingKey: keyof ToiletRatings, value: number) =>
      updateToiletRating(0, ratingKey, value),
    updateDetailedEquipment: (updates: Partial<DetailedToiletEquipment>) =>
      updateToiletDetailedEquipment(0, updates),
    updateMaleEquipment: (
      updates: Partial<NonNullable<DetailedToiletEquipment['maleEquipment']>>,
    ) => updateToiletMaleEquipment(0, updates),
    updateFemaleEquipment: (
      updates: Partial<NonNullable<DetailedToiletEquipment['femaleEquipment']>>,
    ) => updateToiletFemaleEquipment(0, updates),
    updateSharedEquipment: (
      updates: Partial<NonNullable<DetailedToiletEquipment['sharedEquipment']>>,
    ) => updateToiletSharedEquipment(0, updates),
    updateAdditionalFeatures: (
      featureKey: keyof DetailedToiletEquipment['additionalFeatures'],
      value: boolean,
    ) => updateToiletAdditionalFeatures(0, featureKey, value),
  };
};

