  console.log('AppNavigator: Ë™çË®ºÁä∂ÊÖã', { isAuthenticated, isLoading });

  // „Ç¢„Éó„É™Ëµ∑ÂãïÊôÇ„Å´Ë™çË®ºÁä∂ÊÖã„ÇíÂàùÊúüÂåñ
  useEffect(() => {
    console.log('AppNavigator: useEffect „ÅßÂàùÊúüÂåñ„ÇíÈñãÂßã');
