// „Ç´„ÉÜ„Ç¥„É™Ë°®Á§∫Âêç
const getCategoryDisplayName = (category: BadgeCategory): string => {
  const categoryNames = {
    [BadgeCategory.POSTING]: 'ÊäïÁ®ø',
    [BadgeCategory.REVIEWING]: '„É¨„Éì„É•„Éº',
    [BadgeCategory.EXPLORATION]: 'Êé¢Á¥¢',
    [BadgeCategory.COMMUNITY]: '„Ç≥„Éü„É•„Éã„ÉÜ„Ç£',
    [BadgeCategory.SPECIAL]: 'ÁâπÂà•',
  };
  return categoryNames[category];
};

// „É¨„Ç¢Â∫¶Ë°®Á§∫Âêç
const getRarityDisplayName = (rarity: BadgeRarity): string => {
  const rarityNames = {
    [BadgeRarity.COMMON]: '„Ç≥„É¢„É≥',
    [BadgeRarity.UNCOMMON]: '„Ç¢„É≥„Ç≥„É¢„É≥',
    [BadgeRarity.RARE]: '„É¨„Ç¢',
    [BadgeRarity.EPIC]: '„Ç®„Éî„ÉÉ„ÇØ',
    [BadgeRarity.LEGENDARY]: '„É¨„Ç∏„Çß„É≥„ÉÄ„É™„Éº',
  };
  return rarityNames[rarity];
};

// ÂèñÂæóÊù°‰ª∂„ÅÆ„ÉÜ„Ç≠„Çπ„Éà
const getConditionText = (badge: Badge): string => {
  const { condition } = badge;

  switch (condition.type) {
    case 'count':
      return `${getTargetDisplayName(condition.target)}„Çí${condition.value}ÂõûÈÅîÊàê`;
    case 'rating':
      return `${getTargetDisplayName(condition.target)}„Åå${condition.value}‰ª•‰∏ä`;
    case 'streak':
      return `${condition.value}Êó•ÈñìÈÄ£Á∂ö„Åß${getTargetDisplayName(condition.target)}`;
    case 'special':
      return badge.description;
    default:
      return badge.description;
  }
};

// „Çø„Éº„Ç≤„ÉÉ„ÉàË°®Á§∫Âêç
const getTargetDisplayName = (target: string): string => {
  const targetNames: Record<string, string> = {
    toilets_posted: '„Éà„Ç§„É¨ÊäïÁ®ø',
    helpful_votes_given: '„ÄåÂΩπ„Å´Á´ã„Å£„Åü„ÄçÊäïÁ•®',
    helpful_votes_received: '„ÄåÂΩπ„Å´Á´ã„Å£„Åü„Äç„ÇíÂèó„Åë„Çã',
    unique_areas_posted: 'Áï∞„Å™„ÇãÂú∞Âüü„Åß„ÅÆÊäïÁ®ø',
    toilet_types_posted: 'Áï∞„Å™„Çã„Çø„Ç§„Éó„ÅÆ„Éà„Ç§„É¨ÊäïÁ®ø',
    accessible_toilets_posted: '„Éê„É™„Ç¢„Éï„É™„Éº„Éà„Ç§„É¨ÊäïÁ®ø',
    daily_posts: 'ÊØéÊó•ÊäïÁ®ø',
    helpful_ratio: 'ÂΩπ„Å´Á´ã„Å£„ÅüÊØîÁéá',
    avg_cleanliness_rating: 'Âπ≥ÂùáÊ∏ÖÊΩîÂ∫¶Ë©ï‰æ°',
  };
  return targetNames[target] || target;
};

