import React, { useCallback, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  FlatList,
  TouchableOpacity,
  SafeAreaView,
  RefreshControl,
  ActivityIndicator,
} from 'react-native';
import Icon from 'react-native-vector-icons/Ionicons';
import { PushNotification, NotificationType } from '../../types/post';
import { useNotifications } from '../../hooks/useNotifications';

interface NotificationItemProps {
  notification: PushNotification;
  onPress: (notification: PushNotification) => void;
}

const NotificationItem: React.FC<NotificationItemProps> = ({ notification, onPress }) => {
  const getNotificationIcon = (type: NotificationType): string => {
    switch (type) {
      case NotificationType.BADGE_UNLOCKED:
        return 'trophy';
      case NotificationType.HELPFUL_VOTE:
        return 'thumbs-up';
      case NotificationType.NEW_TOILET_NEARBY:
        return 'location';
      case NotificationType.TOILET_UPDATED:
        return 'refresh';
      case NotificationType.MAINTENANCE_ALERT:
        return 'warning';
      case NotificationType.COMMUNITY_NEWS:
        return 'newspaper';
      case NotificationType.WEEKLY_SUMMARY:
        return 'stats-chart';
      case NotificationType.ACHIEVEMENT_REMINDER:
        return 'alarm';
      default:
        return 'notifications';
    }
  };

  const getNotificationColor = (type: NotificationType): string => {
    switch (type) {
      case NotificationType.BADGE_UNLOCKED:
        return '#FFD700';
      case NotificationType.HELPFUL_VOTE:
        return '#4CAF50';
      case NotificationType.NEW_TOILET_NEARBY:
        return '#2196F3';
      case NotificationType.TOILET_UPDATED:
        return '#FF9800';
      case NotificationType.MAINTENANCE_ALERT:
        return '#f44336';
      case NotificationType.COMMUNITY_NEWS:
        return '#9C27B0';
      case NotificationType.WEEKLY_SUMMARY:
        return '#607D8B';
      case NotificationType.ACHIEVEMENT_REMINDER:
        return '#3F51B5';
      default:
        return '#666';
    }
  };

  const formatDate = (date: Date): string => {
    const now = new Date();
    const diffMs = now.getTime() - date.getTime();
    const diffHours = Math.floor(diffMs / (1000 * 60 * 60));
    const diffDays = Math.floor(diffHours / 24);

    if (diffHours < 1) {
      const diffMinutes = Math.floor(diffMs / (1000 * 60));
      return diffMinutes < 1 ? '„Åü„Å£„Åü‰ªä' : `${diffMinutes}ÂàÜÂâç`;
    } else if (diffHours < 24) {
      return `${diffHours}ÊôÇÈñìÂâç`;
    } else if (diffDays < 7) {
      return `${diffDays}Êó•Ââç`;
    } else {
      return date.toLocaleDateString('ja-JP', {
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
      });
    }
  };

  return (
    <TouchableOpacity
      style={[styles.notificationItem, !notification.isRead && styles.unreadItem]}
      onPress={() => onPress(notification)}
      activeOpacity={0.7}
    >
      {/* ÈÄöÁü•„Ç¢„Ç§„Ç≥„É≥ */}
      <View
        style={[
          styles.iconContainer,
          { backgroundColor: `${getNotificationColor(notification.type)}20` },
        ]}
      >
        <Icon
          name={getNotificationIcon(notification.type)}
          size={24}
          color={getNotificationColor(notification.type)}
        />
      </View>

      {/* ÈÄöÁü•ÂÜÖÂÆπ */}
      <View style={styles.contentContainer}>
        <Text style={[styles.title, !notification.isRead && styles.unreadTitle]}>
          {notification.title}
        </Text>

        <Text style={styles.body} numberOfLines={2}>
          {notification.body}
        </Text>

        <Text style={styles.timestamp}>{formatDate(notification.createdAt)}</Text>
      </View>

      {/* Êú™Ë™≠„Ç§„É≥„Ç∏„Ç±„Éº„Çø„Éº */}
      {!notification.isRead && <View style={styles.unreadIndicator} />}
    </TouchableOpacity>
  );
};

