import auth from '@react-native-firebase/auth';
import { GoogleSignin } from '@react-native-google-signin/google-signin';

export interface User {
  uid: string;
  email: string | null;
  displayName: string | null;
  photoURL: string | null;
}

class AuthService {
  constructor() {
    this.configureGoogleSignIn();
  }

  private configureGoogleSignIn() {
    GoogleSignin.configure({
      webClientId: '787078900732-m6hflbubinifr6rn24gaqvisj15qmaae.apps.googleusercontent.com',
    });
  }

  /**
   * Google„Çµ„Ç§„É≥„Ç§„É≥„ÇíÂÆüË°å
   */
  async signInWithGoogle(): Promise<User> {
    try {
      // Google „Çµ„Ç§„É≥„Ç§„É≥ÁîªÈù¢„ÇíË°®Á§∫
      console.log('Checking Google Play Services...');
      await GoogleSignin.hasPlayServices({ showPlayServicesUpdateDialog: true });

      console.log('Starting Google Sign In...');
      const response = await GoogleSignin.signIn();
      console.log('Google Sign In response:', response);

      const idToken = response.data?.idToken || (response as any).idToken;
      if (!idToken) {
        throw new Error('ID„Éà„Éº„ÇØ„É≥„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
      }

      // GoogleË™çË®ºÊÉÖÂ†±„Çí‰ΩúÊàê
      console.log('Creating Firebase credential...');
      const googleCredential = auth.GoogleAuthProvider.credential(idToken);

      // Firebase „Å´„Çµ„Ç§„É≥„Ç§„É≥
      console.log('Signing in with Firebase...');
      const userCredential = await auth().signInWithCredential(googleCredential);

      return this.formatUser(userCredential.user);
    } catch (error: any) {
      console.error('Google sign in error:', error);
      console.error('Error code:', error.code);
      console.error('Error message:', error.message);

      // „Ç®„É©„Éº„Ç≥„Éº„ÉâÂà•„ÅÆË©≥Á¥∞„É°„ÉÉ„Çª„Éº„Ç∏
      if (error.code === 'statusCodes.SIGN_IN_CANCELLED') {
        throw new Error('„Çµ„Ç§„É≥„Ç§„É≥„Åå„Ç≠„É£„É≥„Çª„É´„Åï„Çå„Åæ„Åó„Åü');
      } else if (error.code === 'statusCodes.IN_PROGRESS') {
        throw new Error('„Çµ„Ç§„É≥„Ç§„É≥„ÅåÈÄ≤Ë°å‰∏≠„Åß„Åô');
      } else if (error.code === 'statusCodes.PLAY_SERVICES_NOT_AVAILABLE') {
        throw new Error('Google Play Services „ÅåÂà©Áî®„Åß„Åç„Åæ„Åõ„Çì');
      } else {
        throw new Error(`Google„Çµ„Ç§„É≥„Ç§„É≥„Å´Â§±Êïó„Åó„Åæ„Åó„Åü: ${error.message}`);
      }
    }
  }

  /**
   * „Çµ„Ç§„É≥„Ç¢„Ç¶„Éà
   */
  async signOut(): Promise<void> {
    try {
      // Firebase „Åã„Çâ„Çµ„Ç§„É≥„Ç¢„Ç¶„Éà
      await auth().signOut();

      // Google „Åã„Çâ„ÇÇ„Çµ„Ç§„É≥„Ç¢„Ç¶„Éà
      await GoogleSignin.signOut();
    } catch (error) {
      console.error('Sign out error:', error);
      throw new Error('„Çµ„Ç§„É≥„Ç¢„Ç¶„Éà„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    }
  }

  /**
   * Ë™çË®ºÁä∂ÊÖãÂ§âÊõ¥„ÅÆÁõ£Ë¶ñ
   */
  onAuthStateChanged(callback: (user: User | null) => void): () => void {
    try {
      return auth().onAuthStateChanged(firebaseUser => {
        try {
          if (firebaseUser) {
            callback(this.formatUser(firebaseUser));
          } else {
            callback(null);
          }
        } catch (error) {
          console.log('authService: onAuthStateChanged „Ç≥„Éº„É´„Éê„ÉÉ„ÇØ„Åß„Ç®„É©„Éº:', error);
          callback(null);
        }
      });
    } catch (error) {
      console.log('authService: onAuthStateChanged „Åß„Ç®„É©„Éº:', error);
      // „ÉÄ„Éü„ÉºÈñ¢Êï∞„ÇíËøî„Åô
      return () => {};
    }
  }

  /**
   * ÁèæÂú®„ÅÆ„É¶„Éº„Ç∂„Éº„ÇíÂèñÂæó
   */
  getCurrentUser(): User | null {
    try {
      const firebaseUser = auth().currentUser;
      return firebaseUser ? this.formatUser(firebaseUser) : null;
    } catch (error) {
      console.log('authService: getCurrentUser „Åß„Ç®„É©„Éº:', error);
      return null;
    }
  }

  /**
   * Firebase User„Ç™„Éñ„Ç∏„Çß„ÇØ„Éà„ÇíÊ®ôÊ∫ñÂΩ¢Âºè„Å´Â§âÊèõ
   */
  private formatUser(firebaseUser: any): User {
    return {
      uid: firebaseUser.uid,
      email: firebaseUser.email,
      displayName: firebaseUser.displayName,
      photoURL: firebaseUser.photoURL,
    };
  }

  /**
   * „Ç¢„Ç´„Ç¶„É≥„ÉàÂâäÈô§
   */
  async deleteAccount(): Promise<void> {
    try {
      const user = auth().currentUser;
      if (user) {
        await user.delete();
        await GoogleSignin.signOut();
      }
    } catch (error) {
      console.error('Delete account error:', error);
      throw new Error('„Ç¢„Ç´„Ç¶„É≥„ÉàÂâäÈô§„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    }
  }

  /**
   * „É¶„Éº„Ç∂„Éº„Åå„Çµ„Ç§„É≥„Ç§„É≥„Åó„Å¶„ÅÑ„Çã„Åã„ÉÅ„Çß„ÉÉ„ÇØ
   */
  isSignedIn(): boolean {
    return auth().currentUser !== null;
  }
}

export const authService = new AuthService();

import firestore from '@react-native-firebase/firestore';
import { Badge, UserBadges, BadgeCategory, BadgeRarity } from '../types/post';
import { BADGES } from '../data/badges';

export class BadgeService {
  private userBadgesCollection = firestore().collection('user_badges');
  private userStatsCollection = firestore().collection('user_stats');

  /**
   * „É¶„Éº„Ç∂„Éº„ÅÆ„Éê„ÉÉ„Ç∏ÊÉÖÂ†±„ÇíÂèñÂæó
   */
  async getUserBadges(userId: string): Promise<UserBadges> {
    try {
      const doc = await this.userBadgesCollection.doc(userId).get();

      if (!doc.exists) {
        // Êñ∞Ë¶è„É¶„Éº„Ç∂„Éº„ÅÆÂ†¥Âêà„ÄÅÂàùÊúü„Éá„Éº„Çø„Çí‰ΩúÊàê
        const initialData: UserBadges = {
          userId,
          unlockedBadges: [],
          progress: {},
          lastChecked: new Date(),
        };

        await this.userBadgesCollection.doc(userId).set({
          ...initialData,
          lastChecked: firestore.Timestamp.now(),
        });

        return initialData;
      }

      const data = doc.data()!;
      return {
        userId: data.userId,
        unlockedBadges: data.unlockedBadges || [],
        progress: data.progress || {},
        lastChecked: data.lastChecked?.toDate() || new Date(),
      };
    } catch (error) {
      console.error('Failed to get user badges:', error);
      throw new Error('„Éê„ÉÉ„Ç∏ÊÉÖÂ†±„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    }
  }

  /**
   * „É¶„Éº„Ç∂„Éº„ÅÆÁµ±Ë®àÊÉÖÂ†±„ÇíÂèñÂæó
   */
  async getUserStats(userId: string): Promise<Record<string, number>> {
    try {
      const doc = await this.userStatsCollection.doc(userId).get();

      if (!doc.exists) {
        return {};
      }

      return doc.data() || {};
    } catch (error) {
      console.error('Failed to get user stats:', error);
      return {};
    }
  }

  /**
   * „É¶„Éº„Ç∂„Éº„ÅÆÁµ±Ë®àÊÉÖÂ†±„ÇíÊõ¥Êñ∞
   */
  async updateUserStats(userId: string, stats: Record<string, number>): Promise<void> {
    try {
      await this.userStatsCollection.doc(userId).set(stats, { merge: true });
    } catch (error) {
      console.error('Failed to update user stats:', error);
      throw new Error('Áµ±Ë®àÊÉÖÂ†±„ÅÆÊõ¥Êñ∞„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    }
  }

  /**
   * „Éê„ÉÉ„Ç∏ÂèñÂæóÊù°‰ª∂„Çí„ÉÅ„Çß„ÉÉ„ÇØ
   */
  async checkBadgeConditions(userId: string): Promise<string[]> {
    try {
      const [userBadges, userStats] = await Promise.all([
        this.getUserBadges(userId),
        this.getUserStats(userId),
      ]);

      const newBadges: string[] = [];

      for (const badge of BADGES) {
        // Êó¢„Å´ÂèñÂæóÊ∏à„Åø„ÅÆ„Éê„ÉÉ„Ç∏„ÅØ„Çπ„Ç≠„ÉÉ„Éó
        if (userBadges.unlockedBadges.includes(badge.id)) {
          continue;
        }

        const isUnlocked = this.evaluateBadgeCondition(badge, userStats, userBadges);

        if (isUnlocked) {
          newBadges.push(badge.id);
        }
      }

      // Êñ∞„Åó„ÅÑ„Éê„ÉÉ„Ç∏„Åå„ÅÇ„ÇãÂ†¥Âêà„ÄÅ„Éê„ÉÉ„Ç∏ÊÉÖÂ†±„ÇíÊõ¥Êñ∞
      if (newBadges.length > 0) {
        await this.unlockBadges(userId, newBadges);
      }

      return newBadges;
    } catch (error) {
      console.error('Failed to check badge conditions:', error);
      return [];
    }
  }

  /**
   * „Éê„ÉÉ„Ç∏„ÅÆÂèñÂæóÊù°‰ª∂„ÇíË©ï‰æ°
   */
  private evaluateBadgeCondition(
    badge: Badge,
    userStats: Record<string, number>,
    userBadges: UserBadges,
  ): boolean {
    const { condition } = badge;
    const currentValue = userStats[condition.target] || 0;

    switch (condition.type) {
      case 'count':
        return currentValue >= condition.value;

      case 'rating':
        // ÊúÄÂ∞èÊäïÁ®øÊï∞„ÉÅ„Çß„ÉÉ„ÇØÔºàÂìÅË≥™Á≥ª„Éê„ÉÉ„Ç∏Ôºâ
        if (condition.target === 'helpful_ratio' || condition.target.includes('avg_')) {
          const minPosts = userStats.toilets_posted || 0;
          if (minPosts < 10) return false;
        }
        return currentValue >= condition.value;

      case 'streak':
        // ÈÄ£Á∂öË®òÈå≤Á≥ªÔºàÂÆüË£Ö„ÅØÁ∞°Áï•ÂåñÔºâ
        return currentValue >= condition.value;

      case 'special':
        // ÁâπÂà•Êù°‰ª∂ÔºàÊâãÂãï‰ªò‰∏é„ÇÑÁâπÊÆä„É≠„Ç∏„ÉÉ„ÇØÔºâ
        return this.evaluateSpecialCondition(condition.target, condition.value, userStats);

      default:
        return false;
    }
  }

  /**
   * ÁâπÂà•Êù°‰ª∂„ÅÆË©ï‰æ°
   */
  private evaluateSpecialCondition(
    target: string,
    value: number,
    userStats: Record<string, number>,
  ): boolean {
    switch (target) {
      case 'registration_date':
        // „Ç¢„Éº„É™„Éº„Ç¢„ÉÄ„Éó„Çø„ÉºÂà§ÂÆöÔºà„É¶„Éº„Ç∂„ÉºID„Éô„Éº„ÇπÁ≠âÔºâ
        return (userStats.user_number || 999999) <= value;

      case 'monthly_ranking':
        // ÊúàÈñì„É©„É≥„Ç≠„É≥„Ç∞1‰Ωç
        return (userStats.monthly_rank || 999) === value;

      case 'perfect_rating_post':
        // „Éë„Éº„Éï„Çß„ÇØ„ÉàË©ï‰æ°„ÅÆÊäïÁ®øÊï∞
        return (userStats.perfect_posts || 0) >= value;

      case 'manual_award':
        // ÊâãÂãï‰ªò‰∏é„Éï„É©„Ç∞
        return (userStats.legend_awarded || 0) >= value;

      default:
        return false;
    }
  }

  /**
   * „Éê„ÉÉ„Ç∏„ÇíÂèñÂæóÊ∏à„Åø„Å´„Éû„Éº„ÇØ
   */
  async unlockBadges(userId: string, badgeIds: string[]): Promise<void> {
    try {
      const userBadges = await this.getUserBadges(userId);

      const updatedBadges = {
        ...userBadges,
        unlockedBadges: [...userBadges.unlockedBadges, ...badgeIds],
        lastChecked: firestore.Timestamp.now(),
      };

      await this.userBadgesCollection.doc(userId).set(updatedBadges, { merge: true });
    } catch (error) {
      console.error('Failed to unlock badges:', error);
      throw new Error('„Éê„ÉÉ„Ç∏„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    }
  }

  /**
   * „É¶„Éº„Ç∂„Éº„ÅÆÂèñÂæóÊ∏à„Åø„Éê„ÉÉ„Ç∏Ë©≥Á¥∞„ÇíÂèñÂæó
   */
  async getUserBadgeDetails(userId: string): Promise<Badge[]> {
    try {
      const userBadges = await this.getUserBadges(userId);

      return userBadges.unlockedBadges
        .map(badgeId => BADGES.find(badge => badge.id === badgeId))
        .filter((badge): badge is Badge => badge !== undefined)
        .map(badge => ({
          ...badge,
          unlockedAt: new Date(), // ÂÆüÈöõ„ÅÆÂÆüË£Ö„Åß„ÅØÂèñÂæóÊó•ÊôÇ„Çí‰øùÂ≠ò
        }));
    } catch (error) {
      console.error('Failed to get user badge details:', error);
      return [];
    }
  }

  /**
   * „É¶„Éº„Ç∂„Éº„ÅÆ„Éê„ÉÉ„Ç∏ÈÄ≤Êçó„ÇíÊõ¥Êñ∞
   */
  async updateBadgeProgress(userId: string, target: string, value: number): Promise<void> {
    try {
      await this.updateUserStats(userId, { [target]: value });

      // „Éê„ÉÉ„Ç∏Êù°‰ª∂„Çí„ÉÅ„Çß„ÉÉ„ÇØ„Åó„Å¶Êñ∞„Åó„ÅÑ„Éê„ÉÉ„Ç∏„Åå„ÅÇ„Çã„ÅãÁ¢∫Ë™ç
      await this.checkBadgeConditions(userId);
    } catch (error) {
      console.error('Failed to update badge progress:', error);
    }
  }

  /**
   * ÊäïÁ®øÊôÇ„ÅÆÁµ±Ë®àÊõ¥Êñ∞
   */
  async onToiletPosted(userId: string, toiletData: any): Promise<string[]> {
    try {
      const userStats = await this.getUserStats(userId);

      const updatedStats: Record<string, any> = {
        ...userStats,
        toilets_posted: (userStats.toilets_posted || 0) + 1,
        accessible_toilets_posted: toiletData.isAccessible
          ? (userStats.accessible_toilets_posted || 0) + 1
          : userStats.accessible_toilets_posted || 0,
      };

      // Âú∞Âüü„Å®„Çø„Ç§„Éó„ÅÆËøΩË∑°
      const postedAreas = userStats.posted_areas && typeof userStats.posted_areas === 'string' ? JSON.parse(userStats.posted_areas) : [];
      const postedTypes = userStats.posted_types && typeof userStats.posted_types === 'string' ? JSON.parse(userStats.posted_types) : [];

      if (toiletData.area && !postedAreas.includes(toiletData.area)) {
        postedAreas.push(toiletData.area);
        updatedStats.unique_areas_posted = postedAreas.length;
        updatedStats.posted_areas = JSON.stringify(postedAreas);
      }

      if (!postedTypes.includes(toiletData.type)) {
        postedTypes.push(toiletData.type);
        updatedStats.toilet_types_posted = postedTypes.length;
        updatedStats.posted_types = JSON.stringify(postedTypes);
      }

      await this.updateUserStats(userId, updatedStats);

      // „Éê„ÉÉ„Ç∏Êù°‰ª∂„ÉÅ„Çß„ÉÉ„ÇØ
      return await this.checkBadgeConditions(userId);
    } catch (error) {
      console.error('Failed to handle toilet posted event:', error);
      return [];
    }
  }

  /**
   * ÊäïÁ•®ÊôÇ„ÅÆÁµ±Ë®àÊõ¥Êñ∞
   */
  async onHelpfulVote(voterId: string, targetUserId: string): Promise<void> {
    try {
      // ÊäïÁ•®ËÄÖ„ÅÆÁµ±Ë®àÊõ¥Êñ∞
      const voterStats = await this.getUserStats(voterId);
      await this.updateUserStats(voterId, {
        ...voterStats,
        helpful_votes_given: (voterStats.helpful_votes_given || 0) + 1,
      });

      // ÊäïÁ®øËÄÖ„ÅÆÁµ±Ë®àÊõ¥Êñ∞
      const targetStats = await this.getUserStats(targetUserId);
      await this.updateUserStats(targetUserId, {
        ...targetStats,
        helpful_votes_received: (targetStats.helpful_votes_received || 0) + 1,
      });

      // ‰∏°ËÄÖ„ÅÆ„Éê„ÉÉ„Ç∏Êù°‰ª∂„ÉÅ„Çß„ÉÉ„ÇØ
      await Promise.all([
        this.checkBadgeConditions(voterId),
        this.checkBadgeConditions(targetUserId),
      ]);
    } catch (error) {
      console.error('Failed to handle helpful vote event:', error);
    }
  }

  /**
   * ÂÖ®„Éê„ÉÉ„Ç∏„ÅÆÂèñÂæó
   */
  getAllBadges(): Badge[] {
    return BADGES;
  }

  /**
   * „Ç´„ÉÜ„Ç¥„É™Âà•„Éê„ÉÉ„Ç∏„ÅÆÂèñÂæó
   */
  getBadgesByCategory(category: BadgeCategory): Badge[] {
    return BADGES.filter(badge => badge.category === category);
  }

  /**
   * „É¨„Ç¢Â∫¶Âà•„Éê„ÉÉ„Ç∏„ÅÆÂèñÂæó
   */
  getBadgesByRarity(rarity: BadgeRarity): Badge[] {
    return BADGES.filter(badge => badge.rarity === rarity);
  }
}

// „Ç∑„É≥„Ç∞„É´„Éà„É≥„Ç§„É≥„Çπ„Çø„É≥„Çπ
export const badgeService = new BadgeService();

import firestore from '@react-native-firebase/firestore';
import storage from '@react-native-firebase/storage';
import { ToiletPostForm } from '../types/post';
import { ToiletLocation } from '../types/maps';

export class FirestoreService {
  private toiletsCollection = firestore().collection('toilets');
  private imagesRef = storage().ref('toilet-images');

  /**
   * ÁîªÂÉè„ÇíFirebase Storage„Å´„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ
   */
  async uploadImages(images: string[], toiletId: string): Promise<string[]> {
    const uploadPromises = images.map(async (imagePath, index) => {
      try {
        const imageRef = this.imagesRef.child(`${toiletId}/${index}.jpg`);
        await imageRef.putFile(imagePath);
        const downloadURL = await imageRef.getDownloadURL();
        return downloadURL;
      } catch (error) {
        console.error(`Failed to upload image ${index}:`, error);
        throw error;
      }
    });

    return Promise.all(uploadPromises);
  }

  /**
   * „Éà„Ç§„É¨ÊÉÖÂ†±„ÇíFirestore„Å´‰øùÂ≠òÔºàË§áÊï∞„Éà„Ç§„É¨ÂØæÂøúÔºâ
   */
  async createToilet(form: ToiletPostForm, userId: string): Promise<string> {
    try {
      // ÊñΩË®≠„Éâ„Ç≠„É•„É°„É≥„Éà„Çí‰ΩúÊàê
      const facilityRef = this.toiletsCollection.doc();
      const facilityId = facilityRef.id;

      // ÊñΩË®≠ÁîªÂÉè„Çí„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ
      let facilityImageUrls: string[] = [];
      if (form.facilityImages.length > 0) {
        facilityImageUrls = await this.uploadImages(form.facilityImages, `${facilityId}/facility`);
      }

      // ÊñΩË®≠„Éá„Éº„ÇøÔºàÊúÄÂàù„ÅÆ„Éà„Ç§„É¨„ÅÆÊÉÖÂ†±„Çí‰ΩøÁî®Ôºâ
      const firstToilet = form.toilets[0];
      if (!firstToilet) {
        throw new Error('Â∞ë„Å™„Åè„Å®„ÇÇ1„Å§„ÅÆ„Éà„Ç§„É¨ÊÉÖÂ†±„ÅåÂøÖË¶Å„Åß„Åô');
      }

      // ÂêÑ„Éà„Ç§„É¨„ÅÆÁîªÂÉè„Çí„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ
      let toiletImageUrls: string[] = [];
      if (firstToilet.images.length > 0) {
        toiletImageUrls = await this.uploadImages(firstToilet.images, `${facilityId}/toilet_0`);
      }

      // Firestore„Å´‰øùÂ≠ò„Åô„Çã„Éá„Éº„ÇøÔºàÊó¢Â≠ò„ÅÆÊßãÈÄ†„ÇíÁ∂≠ÊåÅÔºâ
      const toiletData = {
        id: facilityId,
        title: form.facilityTitle,
        description: form.facilityDescription,
        type: form.type,
        isAccessible: firstToilet.isAccessible,
        location: {
          latitude: form.location!.latitude,
          longitude: form.location!.longitude,
        },
        facilities: firstToilet.facilities,
        openingHours: form.openingHours,
        additionalInfo: form.additionalInfo,
        detailedEquipment: firstToilet.detailedEquipment,
        ratings: firstToilet.ratings,
        images: [...facilityImageUrls, ...toiletImageUrls],
        createdBy: userId,
        createdAt: firestore.Timestamp.now(),
        updatedAt: firestore.Timestamp.now(),
        rating: firstToilet.ratings.overall || 0,
        reviewCount: 1,
        isActive: true,
        // Ë§áÊï∞„Éà„Ç§„É¨ÊÉÖÂ†±„ÇíËøΩÂä†„Éï„Ç£„Éº„É´„Éâ„Å®„Åó„Å¶‰øùÂ≠ò
        toilets: form.toilets.map((toilet, index) => ({
          ...toilet,
          images: index === 0 ? toiletImageUrls : [], // ÁèæÂú®„ÅØÊúÄÂàù„ÅÆ„Éà„Ç§„É¨„ÅÆÁîªÂÉè„ÅÆ„Åø„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ
        })),
      };

      await facilityRef.set(toiletData);
      return facilityId;
    } catch (error: any) {
      console.error('Failed to create toilet:', error);
      console.error('Error code:', error.code);
      console.error('Error message:', error.message);
      throw new Error(`„Éà„Ç§„É¨„ÅÆÊäïÁ®ø„Å´Â§±Êïó„Åó„Åæ„Åó„Åü: ${error.message}`);
    }
  }

  /**
   * „Éà„Ç§„É¨ÊÉÖÂ†±„ÇíÊõ¥Êñ∞
   */
  async updateToilet(toiletId: string, updates: Partial<ToiletLocation>): Promise<void> {
    try {
      await this.toiletsCollection.doc(toiletId).update({
        ...updates,
        updatedAt: firestore.Timestamp.now(),
      });
    } catch (error) {
      console.error('Failed to update toilet:', error);
      throw new Error('„Éà„Ç§„É¨ÊÉÖÂ†±„ÅÆÊõ¥Êñ∞„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    }
  }

  /**
   * Ëøë„Åè„ÅÆ„Éà„Ç§„É¨„ÇíÊ§úÁ¥¢
   */
  async getNearbyToilets(
    latitude: number,
    longitude: number,
    radiusKm: number = 5,
  ): Promise<ToiletLocation[]> {
    try {
      console.log(`‰ΩçÁΩÆÊÉÖÂ†±„Åß„Éà„Ç§„É¨„ÇíÊ§úÁ¥¢‰∏≠: lat=${latitude}, lng=${longitude}, radius=${radiusKm}km`);

      // ÂÖ•ÂäõÂÄ§„ÅÆÊ§úË®º
      if (!latitude || !longitude || isNaN(latitude) || isNaN(longitude)) {
        console.error('ÁÑ°Âäπ„Å™‰ΩçÁΩÆÊÉÖÂ†±:', { latitude, longitude });
        throw new Error('ÁÑ°Âäπ„Å™‰ΩçÁΩÆÊÉÖÂ†±„ÅåÊèê‰æõ„Åï„Çå„Åæ„Åó„Åü');
      }

      // Firestore„Åß„ÅØÂú∞ÁêÜÁöÑ„ÇØ„Ç®„É™„ÅåÂà∂Èôê„Åï„Çå„Å¶„ÅÑ„Çã„Åü„ÇÅ„ÄÅ
      // „Çà„ÇäÂØõÂÆπ„Å™ÁØÑÂõ≤Ê§úÁ¥¢„ÇíÂÆüË£ÖÔºàÊ§úÁ¥¢ÁØÑÂõ≤„Çí2ÂÄç„Å´Êã°Â§ßÔºâ
      const latDelta = (radiusKm * 2) / 111.32; // Á∑ØÂ∫¶1Â∫¶ ‚âà 111.32km
      const lngDelta = (radiusKm * 2) / (111.32 * Math.cos((latitude * Math.PI) / 180));

      console.log(`Ê§úÁ¥¢ÁØÑÂõ≤: lat=${latitude-latDelta}~${latitude+latDelta}, lng=${longitude-lngDelta}~${longitude+lngDelta}`);

      // „Åæ„ÅöFirestore„Å∏„ÅÆÊé•Á∂ö„Çí„ÉÜ„Çπ„Éà
      const testQuery = await this.toiletsCollection.limit(1).get();
      console.log('FirestoreÊé•Á∂ö„ÉÜ„Çπ„ÉàÊàêÂäü:', testQuery.size);

      // Ë§áÂêà„Ç§„É≥„Éá„ÉÉ„ÇØ„Çπ„ÇíÈÅø„Åë„Çã„Åü„ÇÅ„ÄÅÂçò‰∏ÄÊù°‰ª∂„Åß„ÇØ„Ç®„É™„Åó„Å¶Âæå„Åß„Éï„Ç£„É´„Çø„É™„É≥„Ç∞
      const snapshot = await this.toiletsCollection
        .where('isActive', '==', true)
        .limit(100)
        .get();

      console.log(`Firestore„ÇØ„Ç®„É™ÁµêÊûú: ${snapshot.size}‰ª∂„ÅÆ„Éâ„Ç≠„É•„É°„É≥„Éà`);

      const toilets: ToiletLocation[] = [];

      snapshot.forEach(doc => {
        try {
          const data = doc.data();
          console.log(`Âá¶ÁêÜ‰∏≠„ÅÆ„Éâ„Ç≠„É•„É°„É≥„Éà: ${doc.id}`, data);

          // „Éá„Éº„Çø„ÅÆÂ≠òÂú®Á¢∫Ë™ç
          if (!data.location || !data.location.latitude || !data.location.longitude) {
            console.warn(`„Éâ„Ç≠„É•„É°„É≥„Éà ${doc.id} „Å´‰ΩçÁΩÆÊÉÖÂ†±„Åå„ÅÇ„Çä„Åæ„Åõ„Çì`);
            return;
          }

          // ‰ΩçÁΩÆÊÉÖÂ†±„ÅÆÁØÑÂõ≤„ÉÅ„Çß„ÉÉ„ÇØÔºà„ÇØ„É©„Ç§„Ç¢„É≥„Éà„Çµ„Ç§„ÉâÔºâ
          const withinLatRange = data.location.latitude >= latitude - latDelta && 
                                data.location.latitude <= latitude + latDelta;
          const withinLngRange = data.location.longitude >= longitude - lngDelta && 
                                data.location.longitude <= longitude + lngDelta;

          console.log(`‰ΩçÁΩÆ„ÉÅ„Çß„ÉÉ„ÇØ: ${data.title}`);
          console.log(`  Á∑ØÂ∫¶: ${data.location.latitude} (ÁØÑÂõ≤: ${latitude - latDelta} ~ ${latitude + latDelta}) = ${withinLatRange}`);
          console.log(`  ÁµåÂ∫¶: ${data.location.longitude} (ÁØÑÂõ≤: ${longitude - lngDelta} ~ ${longitude + lngDelta}) = ${withinLngRange}`);

          if (withinLatRange && withinLngRange) {
            toilets.push({
              id: data.id || doc.id,
              title: data.title || '„Çø„Ç§„Éà„É´„Å™„Åó',
              description: data.description || '',
              latitude: data.location.latitude,
              longitude: data.location.longitude,
              type: data.type || 'public',
              isAccessible: data.isAccessible || false,
              rating: data.rating || 0,
              reviewCount: data.reviewCount || 0,
              createdBy: data.createdBy || '',
              createdAt: data.createdAt ? data.createdAt.toDate() : new Date(),
              updatedAt: data.updatedAt ? data.updatedAt.toDate() : new Date(),
            });
          }
        } catch (docError: any) {
          console.error(`„Éâ„Ç≠„É•„É°„É≥„Éà ${doc.id} „ÅÆÂá¶ÁêÜ„Åß„Ç®„É©„Éº:`, docError);
        }
      });

      console.log(`ÁµêÊûú: ${toilets.length}‰ª∂„ÅÆ„Éà„Ç§„É¨„ÅåË¶ã„Å§„Åã„Çä„Åæ„Åó„Åü`);
      return toilets;
    } catch (error: any) {
      console.error('Failed to get nearby toilets:', error);
      console.error('Error code:', error.code);
      console.error('Error message:', error.message);
      console.error('Error stack:', error.stack);

      // Firestore„ÅÆÊé•Á∂ö„Ç®„É©„Éº„Åã„Å©„ÅÜ„Åã„ÇíÂà§ÂÆö
      if (error.code === 'unavailable' || error.code === 'permission-denied') {
        throw new Error('Firestore„Å∏„ÅÆÊé•Á∂ö„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇ„Éç„ÉÉ„Éà„ÉØ„Éº„ÇØÊé•Á∂ö„Å®FirebaseË®≠ÂÆö„ÇíÁ¢∫Ë™ç„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ');
      } else if (error.code === 'unauthenticated') {
        throw new Error('Ë™çË®º„ÅåÂøÖË¶Å„Åß„Åô„ÄÇ„É≠„Ç∞„Ç§„É≥„Åó„Å¶„Åã„ÇâÂÜçÂ∫¶„ÅäË©¶„Åó„Åè„Å†„Åï„ÅÑ„ÄÇ');
      } else {
        throw new Error(`Ëøë„Åè„ÅÆ„Éà„Ç§„É¨„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü: ${error.message}`);
      }
    }
  }

  /**
   * ÁâπÂÆö„ÅÆ„Éà„Ç§„É¨ÊÉÖÂ†±„ÇíÂèñÂæó
   */
  async getToilet(toiletId: string): Promise<ToiletLocation | null> {
    try {
      const doc = await this.toiletsCollection.doc(toiletId).get();

      if (!doc.exists) {
        return null;
      }

      const data = doc.data()!;
      return {
        id: data.id,
        title: data.title,
        description: data.description,
        latitude: data.location.latitude,
        longitude: data.location.longitude,
        type: data.type,
        isAccessible: data.isAccessible,
        rating: data.rating,
        reviewCount: data.reviewCount,
        createdBy: data.createdBy,
        createdAt: data.createdAt.toDate(),
        updatedAt: data.updatedAt.toDate(),
      };
    } catch (error: any) {
      console.error('Failed to get toilet:', error);
      console.error('Error code:', error.code);
      console.error('Error message:', error.message);
      throw new Error(`„Éà„Ç§„É¨ÊÉÖÂ†±„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü: ${error.message}`);
