  /**
   * „ÅäÊ∞ó„Å´ÂÖ•„Çä„Åã„Å©„ÅÜ„Åã„ÉÅ„Çß„ÉÉ„ÇØ
   */
  async isFavorite(userId: string, toiletId: string): Promise<boolean> {
    try {
      const snapshot = await this.favoritesCollection
        .where('userId', '==', userId)
