  /**
   * „É¶„Éº„Ç∂„Éº„Ç¢„ÇØ„ÉÜ„Ç£„Éì„ÉÜ„Ç£„ÇíË®òÈå≤
   */
  async recordActivity(
    userId: string,
    type: ActivityType,
    targetId: string,
    targetTitle: string,
    description: string,
  ): Promise<void> {
    try {
      const activityData: UserActivity = {
        id: this.activitiesCollection.doc().id,
        userId,
        type,
        targetId,
        targetTitle,
        description,
        createdAt: new Date(),
      };

      await this.activitiesCollection.add({
        ...activityData,
        createdAt: firestore.Timestamp.now(),
      });

      // Âè§„ÅÑ„Ç¢„ÇØ„ÉÜ„Ç£„Éì„ÉÜ„Ç£„ÇíÂâäÈô§ÔºàÊúÄÊñ∞200‰ª∂„ÅÆ„Åø‰øùÊåÅÔºâ
      const allActivities = await this.activitiesCollection
        .where('userId', '==', userId)
        .orderBy('createdAt', 'desc')
        .get();

      if (allActivities.docs.length > 200) {
        const batch = firestore().batch();
        // 200‰ª∂„Çà„ÇäÂè§„ÅÑ„ÇÇ„ÅÆ„ÇíÂâäÈô§
        allActivities.docs.slice(200).forEach((doc: any) => {
          batch.delete(doc.ref);
