      const activities: UserActivity[] = [];
      snapshot.forEach(doc => {
        const data = doc.data();
        activities.push({
          id: doc.id,
          userId: data.userId,
          type: data.type,
          targetId: data.targetId,
          targetTitle: data.targetTitle,
          description: data.description,
          createdAt: data.createdAt.toDate(),
        });
      });

      return activities;
    } catch (error) {
      console.error('Failed to get recent activities:', error);
      return [];
    }
  }

  /**
   * „Çà„ÅèË¶ã„Çã„Éà„Ç§„É¨„ÇíÂèñÂæó
   */
  async getFrequentlyViewed(userId: string, limit: number = 10): Promise<ViewHistory[]> {
    try {
      const snapshot = await this.viewHistoryCollection
        .where('userId', '==', userId)
        .orderBy('viewCount', 'desc')
        .limit(limit)
        .get();

      const history: ViewHistory[] = [];
      snapshot.forEach(doc => {
        const data = doc.data();
        history.push({
          id: doc.id,
          userId: data.userId,
          toiletId: data.toiletId,
          toiletTitle: data.toiletTitle,
          toiletType: data.toiletType,
          viewedAt: data.viewedAt.toDate(),
          viewCount: data.viewCount || 1,
        });
      });

      return history;
    } catch (error) {
      console.error('Failed to get frequently viewed:', error);
      return [];
    }
  }
}

// „Ç∑„É≥„Ç∞„É´„Éà„É≥„Ç§„É≥„Çπ„Çø„É≥„Çπ
export const historyService = new HistoryService();

import messaging from '@react-native-firebase/messaging';
import firestore from '@react-native-firebase/firestore';
import { Platform, PermissionsAndroid } from 'react-native';
import { PushNotification, NotificationSettings, PushToken, NotificationType } from '../types/post';

export class NotificationService {
  private notificationsCollection = firestore().collection('push_notifications');
  private settingsCollection = firestore().collection('notification_settings');
  private tokensCollection = firestore().collection('push_tokens');

  /**
   * „Éó„ÉÉ„Ç∑„É•ÈÄöÁü•„ÅÆÂàùÊúüÂåñ
   */
  async initialize(userId: string): Promise<void> {
    try {
      // ÈÄöÁü•Ê®©Èôê„ÅÆÁ¢∫Ë™ç„Éª„É™„ÇØ„Ç®„Çπ„Éà
      const authStatus = await messaging().requestPermission();
      const enabled =
        authStatus === messaging.AuthorizationStatus.AUTHORIZED ||
        authStatus === messaging.AuthorizationStatus.PROVISIONAL;

      if (!enabled) {
        console.log('Push notification permission denied');
        return;
      }

      // Android 13‰ª•Èôç„ÅÆËøΩÂä†Ê®©ÈôêÁ¢∫Ë™ç
      if (Platform.OS === 'android' && Platform.Version >= 33) {
        const granted = await PermissionsAndroid.request(
          PermissionsAndroid.PERMISSIONS.POST_NOTIFICATIONS,
        );
        if (granted !== PermissionsAndroid.RESULTS.GRANTED) {
          console.log('Android notification permission denied');
          return;
        }
      }

      // FCM„Éà„Éº„ÇØ„É≥„ÅÆÂèñÂæó„Éª‰øùÂ≠ò
      const token = await messaging().getToken();
      await this.savePushToken(userId, token);

      // „Éà„Éº„ÇØ„É≥Êõ¥Êñ∞„ÅÆÁõ£Ë¶ñ
      messaging().onTokenRefresh(async newToken => {
        await this.savePushToken(userId, newToken);
      });

      // „Éï„Ç©„Ç¢„Ç∞„É©„Ç¶„É≥„ÉâÈÄöÁü•„ÅÆË®≠ÂÆö
      messaging().onMessage(async remoteMessage => {
        await this.handleForegroundMessage(remoteMessage);
      });

      // „Éê„ÉÉ„ÇØ„Ç∞„É©„Ç¶„É≥„ÉâÈÄöÁü•„ÅÆË®≠ÂÆöÔºàindex.js„ÅßË®≠ÂÆöÊ∏à„Åø„ÅÆ„ÅØ„ÅöÔºâ
      messaging().onNotificationOpenedApp(remoteMessage => {
        this.handleNotificationOpen(remoteMessage);
      });

      // „Ç¢„Éó„É™Ëµ∑ÂãïÊôÇ„ÅÆÈÄöÁü•„ÉÅ„Çß„ÉÉ„ÇØ
      messaging()
        .getInitialNotification()
        .then(remoteMessage => {
          if (remoteMessage) {
            this.handleNotificationOpen(remoteMessage);
          }
        });
    } catch (error) {
      console.error('Failed to initialize notifications:', error);
    }
  }

  /**
   * „Éó„ÉÉ„Ç∑„É•„Éà„Éº„ÇØ„É≥„ÅÆ‰øùÂ≠ò
   */
  async savePushToken(userId: string, token: string): Promise<void> {
    try {
      const tokenData: PushToken = {
        userId,
        token,
        platform: Platform.OS as 'ios' | 'android',
        isActive: true,
        lastUsed: new Date(),
        createdAt: new Date(),
      };

      // Êó¢Â≠ò„ÅÆ„Éà„Éº„ÇØ„É≥„ÇíÁÑ°ÂäπÂåñ
      await this.tokensCollection
        .where('userId', '==', userId)
        .where('platform', '==', Platform.OS)
        .get()
        .then(snapshot => {
          const batch = firestore().batch();
          snapshot.docs.forEach(doc => {
            batch.update(doc.ref, { isActive: false });
          });
          return batch.commit();
        });

      // Êñ∞„Åó„ÅÑ„Éà„Éº„ÇØ„É≥„Çí‰øùÂ≠ò
      await this.tokensCollection.add({
        ...tokenData,
        lastUsed: firestore.Timestamp.now(),
        createdAt: firestore.Timestamp.now(),
      });
    } catch (error) {
      console.error('Failed to save push token:', error);
    }
  }

  /**
   * ÈÄöÁü•Ë®≠ÂÆö„ÅÆÂèñÂæó
   */
  async getNotificationSettings(userId: string): Promise<NotificationSettings> {
    try {
      const doc = await this.settingsCollection.doc(userId).get();

      if (!doc.exists) {
        // „Éá„Éï„Ç©„É´„ÉàË®≠ÂÆö„Çí‰ΩúÊàê
        const defaultSettings: NotificationSettings = {
          userId,
          enabled: true,
          badgeNotifications: true,
          helpfulVoteNotifications: true,
          nearbyToiletNotifications: true,
          updateNotifications: true,
          newsNotifications: true,
          summaryNotifications: false,
          reminderNotifications: true,
          quietHoursEnabled: false,
          quietHoursStart: '22:00',
          quietHoursEnd: '08:00',
          updatedAt: new Date(),
        };

        await this.settingsCollection.doc(userId).set({
          ...defaultSettings,
          updatedAt: firestore.Timestamp.now(),
        });

        return defaultSettings;
      }

      const data = doc.data()!;
      return {
        ...data,
        updatedAt: data.updatedAt?.toDate() || new Date(),
      } as NotificationSettings;
    } catch (error) {
      console.error('Failed to get notification settings:', error);
      throw new Error('ÈÄöÁü•Ë®≠ÂÆö„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    }
  }

  /**
   * ÈÄöÁü•Ë®≠ÂÆö„ÅÆÊõ¥Êñ∞
   */
  async updateNotificationSettings(
    userId: string,
    updates: Partial<NotificationSettings>,
  ): Promise<void> {
    try {
      await this.settingsCollection.doc(userId).update({
        ...updates,
        updatedAt: firestore.Timestamp.now(),
      });
    } catch (error) {
      console.error('Failed to update notification settings:', error);
      throw new Error('ÈÄöÁü•Ë®≠ÂÆö„ÅÆÊõ¥Êñ∞„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    }
  }

  /**
   * ÈÄöÁü•„ÅÆÈÄÅ‰ø°Ôºà„Çµ„Éº„Éê„Éº„Çµ„Ç§„ÉâÁî®Ôºâ
   */
  async sendNotification(
    userId: string,
    type: NotificationType,
    title: string,
    body: string,
    data?: Record<string, any>,
  ): Promise<void> {
    try {
      // ÈÄöÁü•Ë®≠ÂÆö„Çí„ÉÅ„Çß„ÉÉ„ÇØ
      const settings = await this.getNotificationSettings(userId);
      if (!settings.enabled || !this.shouldSendNotification(type, settings)) {
        return;
      }

      // „Çµ„Ç§„É¨„É≥„ÉàÊôÇÈñì„ÅÆ„ÉÅ„Çß„ÉÉ„ÇØ
      if (this.isInQuietHours(settings)) {
        return;
      }

      // ÈÄöÁü•„Éá„Éº„Çø„Çí‰øùÂ≠ò
      const notification: PushNotification = {
        id: firestore().collection('push_notifications').doc().id,
        userId,
        type,
        title,
        body,
        data,
        isRead: false,
        createdAt: new Date(),
      };

      await this.notificationsCollection.add({
        ...notification,
        createdAt: firestore.Timestamp.now(),
      });

      // ÂÆüÈöõ„ÅÆ„Éó„ÉÉ„Ç∑„É•ÈÄöÁü•ÈÄÅ‰ø°ÔºàCloud Functions„ÅßÂá¶ÁêÜÔºâ
      // „Åì„Åì„Åß„ÅØ„É≠„Éº„Ç´„É´ÈÄöÁü•„Å®„Åó„Å¶Ë°®Á§∫
      this.showLocalNotification(title, body, data);
    } catch (error) {
      console.error('Failed to send notification:', error);
    }
  }

  /**
   * „É¶„Éº„Ç∂„Éº„ÅÆÈÄöÁü•Â±•Ê≠¥„ÇíÂèñÂæó
   */
  async getUserNotifications(userId: string, limit: number = 20): Promise<PushNotification[]> {
    try {
      const snapshot = await this.notificationsCollection
        .where('userId', '==', userId)
        .orderBy('createdAt', 'desc')
        .limit(limit)
        .get();

      const notifications: PushNotification[] = [];
      snapshot.forEach(doc => {
        const data = doc.data();
        notifications.push({
          id: doc.id,
          userId: data.userId,
          type: data.type,
          title: data.title,
          body: data.body,
          data: data.data,
          isRead: data.isRead,
          createdAt: data.createdAt.toDate(),
          scheduledAt: data.scheduledAt?.toDate(),
        });
      });

      return notifications;
    } catch (error) {
      console.error('Failed to get user notifications:', error);
      return [];
    }
  }

  /**
   * ÈÄöÁü•„ÇíÊó¢Ë™≠„Å´„Åô„Çã
