  /**
   * Êú™Ë™≠ÈÄöÁü•Êï∞„ÅÆÂèñÂæó
   */
  async getUnreadCount(userId: string): Promise<number> {
    try {
      const snapshot = await this.notificationsCollection
        .where('userId', '==', userId)
        .where('isRead', '==', false)
