  /**
   * ÈÄöÂ†±„ÇíÈÄÅ‰ø°
   */
  async submitReport(
    reporterId: string,
    targetType: ReportTargetType,
    targetId: string,
    reason: ReportReason,
    description?: string,
    evidence?: string[],
  ): Promise<void> {
    try {
      // Âêå„ÅòÂØæË±°„Å´ÂØæ„Åô„ÇãÈáçË§áÈÄöÂ†±„Çí„ÉÅ„Çß„ÉÉ„ÇØ
      const existingReport = await this.reportsCollection
        .where('reporterId', '==', reporterId)
        .where('targetType', '==', targetType)
        .where('targetId', '==', targetId)
        .where('status', 'in', [ReportStatus.PENDING, ReportStatus.UNDER_REVIEW])
        .get();

      if (!existingReport.empty) {
        throw new Error('„Åì„ÅÆÂØæË±°„ÅØÊó¢„Å´ÈÄöÂ†±Ê∏à„Åø„Åß„Åô');
      }

      const reportData: Report = {
        id: this.reportsCollection.doc().id,
        reporterId,
        targetType,
        targetId,
        reason,
        description,
        evidence,
        status: ReportStatus.PENDING,
        createdAt: new Date(),
      };

      await this.reportsCollection.add({
        ...reportData,
        createdAt: firestore.Timestamp.now(),
      });

      // Ëá™ÂãïÊ§úÂá∫„É≠„Ç∏„ÉÉ„ÇØ„ÅÆÂÆüË°å
      await this.processAutoDetection(targetType, targetId, reason);
    } catch (error) {
      console.error('Failed to submit report:', error);
      throw error;
    }
  }

  /**
   * ÈÄöÂ†±‰∏ÄË¶ß„ÇíÂèñÂæóÔºàÁÆ°ÁêÜËÄÖÁî®Ôºâ
   */
  async getReports(status?: ReportStatus, limit: number = 50): Promise<Report[]> {
    try {
      let query = this.reportsCollection.orderBy('createdAt', 'desc');

      if (status) {
        query = query.where('status', '==', status);
      }

      const snapshot = await query.limit(limit).get();

      const reports: Report[] = [];
      snapshot.forEach(doc => {
        const data = doc.data();
        reports.push({
          id: doc.id,
          reporterId: data.reporterId,
          targetType: data.targetType,
          targetId: data.targetId,
          reason: data.reason,
          description: data.description,
          evidence: data.evidence,
          status: data.status,
          createdAt: data.createdAt.toDate(),
          reviewedAt: data.reviewedAt?.toDate(),
          reviewerId: data.reviewerId,
          resolution: data.resolution,
        });
      });

      return reports;
    } catch (error) {
      console.error('Failed to get reports:', error);
      return [];
    }
  }

