  /**
   * ÈÅïÂèçË®òÈå≤„ÇíËøΩÂä†
   */
  async addViolationRecord(
    userId: string,
    type: ViolationType,
    severity: ViolationSeverity,
    description: string,
    evidence?: string[],
    reportId?: string,
    autoDetected: boolean = false,
  ): Promise<void> {
    try {
      const points = this.getViolationPoints(severity);
      const expiresAt = new Date();
      expiresAt.setDate(expiresAt.getDate() + this.defaultConfig.pointExpirationDays);

      const violationData: ViolationRecord = {
        id: this.violationsCollection.doc().id,
        userId,
        type,
        severity,
        description,
        evidence,
        reportId,
        autoDetected,
        points,
        createdAt: new Date(),
        expiresAt,
      };

      await this.violationsCollection.add({
        ...violationData,
        createdAt: firestore.Timestamp.now(),
        expiresAt: firestore.Timestamp.fromDate(expiresAt),
      });

      // Ëá™ÂãïÂà∂ÈôêÂà§ÂÆö
      await this.checkAndApplyRestrictions(userId);
    } catch (error) {
      console.error('Failed to add violation record:', error);
      throw error;
    }
  }

  /**
   * „É¶„Éº„Ç∂„Éº„ÅÆÈÅïÂèç„Éù„Ç§„É≥„Éà„ÇíË®àÁÆó
   */
  async getUserViolationPoints(userId: string): Promise<number> {
    try {
      const snapshot = await this.violationsCollection
        .where('userId', '==', userId)
        .where('expiresAt', '>', firestore.Timestamp.now())
        .get();

      let totalPoints = 0;
      snapshot.forEach(doc => {
        const data = doc.data();
        totalPoints += data.points || 0;
      });

      return totalPoints;
    } catch (error) {
      console.error('Failed to get user violation points:', error);
      return 0;
    }
  }

  /**
   * „É¶„Éº„Ç∂„Éº„ÅÆÂà∂ÈôêÁä∂Ê≥Å„ÇíÂèñÂæó
   */
  async getUserRestrictions(userId: string): Promise<UserRestriction[]> {
    try {
      const snapshot = await this.restrictionsCollection
        .where('userId', '==', userId)
        .where('isActive', '==', true)
        .orderBy('startDate', 'desc')
        .get();

      const restrictions: UserRestriction[] = [];
      snapshot.forEach(doc => {
        const data = doc.data();
        restrictions.push({
          id: doc.id,
          userId: data.userId,
          type: data.type,
          reason: data.reason,
          startDate: data.startDate.toDate(),
          endDate: data.endDate?.toDate(),
          isActive: data.isActive,
          createdBy: data.createdBy,
          details: data.details,
        });
      });

      // ÊúüÈôêÂàá„Çå„ÅÆÂà∂Èôê„ÇíÁÑ°ÂäπÂåñ
      for (const restriction of restrictions) {
        if (restriction.endDate && restriction.endDate < new Date()) {
          await this.deactivateRestriction(restriction.id);
        }
      }

      return restrictions.filter(r => !r.endDate || r.endDate > new Date());
    } catch (error) {
      console.error('Failed to get user restrictions:', error);
      return [];
    }
  }

  /**
   * Âà∂Èôê„ÇíÈÅ©Áî®
   */
  private async applyRestriction(
    userId: string,
    type: RestrictionType,
    reason: string,
    durationDays?: number,
    details?: Record<string, any>,
  ): Promise<void> {
    try {
      const startDate = new Date();
      const endDate = durationDays
        ? new Date(startDate.getTime() + durationDays * 24 * 60 * 60 * 1000)
        : undefined;

      const restrictionData: UserRestriction = {
        id: this.restrictionsCollection.doc().id,
        userId,
        type,
        reason,
        startDate,
        endDate,
        isActive: true,
        createdBy: 'system',
        details,
      };

      await this.restrictionsCollection.add({
        ...restrictionData,
        startDate: firestore.Timestamp.fromDate(startDate),
        endDate: endDate ? firestore.Timestamp.fromDate(endDate) : null,
      });

      // TODO: „Éó„ÉÉ„Ç∑„É•ÈÄöÁü•„Åß„É¶„Éº„Ç∂„Éº„Å´Âà∂Èôê„ÇíÈÄöÁü•
    } catch (error) {
      console.error('Failed to apply restriction:', error);
    }
  }

  /**
   * Âà∂Èôê„ÇíÁÑ°ÂäπÂåñ
   */
  private async deactivateRestriction(restrictionId: string): Promise<void> {
    try {
      await this.restrictionsCollection.doc(restrictionId).update({
        isActive: false,
      });
    } catch (error) {
      console.error('Failed to deactivate restriction:', error);
    }
  }

  /**
   * Ëá™ÂãïÂà∂ÈôêÂà§ÂÆö„Å®ÈÅ©Áî®
   */
  private async checkAndApplyRestrictions(userId: string): Promise<void> {
    try {
      const violationPoints = await this.getUserViolationPoints(userId);

      // Ë®≠ÂÆö„Åï„Çå„ÅüÂà∂Èôê„Éù„Ç§„É≥„Éà„ÇíÈôçÈ†Ü„Åß„ÉÅ„Çß„ÉÉ„ÇØ
      const restrictionPoints = Object.keys(this.defaultConfig.restrictions)
        .map(Number)
        .sort((a, b) => b - a);

      for (const points of restrictionPoints) {
        if (violationPoints >= points) {
          const config = this.defaultConfig.restrictions[points];

          // Êó¢„Å´Âêå„Åò„Çø„Ç§„Éó„ÅÆÂà∂Èôê„ÅåÈÅ©Áî®„Åï„Çå„Å¶„ÅÑ„Çã„Åã„ÉÅ„Çß„ÉÉ„ÇØ
          const existingRestrictions = await this.getUserRestrictions(userId);
          const hasExistingRestriction = existingRestrictions.some(r => r.type === config.type);

          if (!hasExistingRestriction) {
            await this.applyRestriction(userId, config.type, config.message, config.durationDays, {
              violationPoints,
              triggerPoints: points,
            });
          }
          break;
        }
      }
    } catch (error) {
      console.error('Failed to check and apply restrictions:', error);
    }
  }

  /**
   * Ëá™ÂãïÊ§úÂá∫Âá¶ÁêÜ
   */
  private async processAutoDetection(
    targetType: ReportTargetType,
    targetId: string,
    reason: ReportReason,
  ): Promise<void> {
    try {
      // Âêå„ÅòÂØæË±°„Å∏„ÅÆÈÄöÂ†±Êï∞„Çí„Ç´„Ç¶„É≥„Éà
      const reportCount = await this.getReportCountForTarget(targetType, targetId);

      // ÈÄöÂ†±Êï∞„Åå‰∏ÄÂÆö‰ª•‰∏ä„ÅÆÂ†¥Âêà„ÄÅËá™ÂãïÁöÑ„Å´ÈÅïÂèç„Å®„Åø„Å™„Åô
      if (reportCount >= 3) {
        // ÂØæË±°„ÅÆ„Ç™„Éº„Éä„Éº„ÇíÁâπÂÆö„Åó„Å¶ÈÅïÂèçË®òÈå≤„ÇíËøΩÂä†
        const ownerId = await this.getTargetOwnerId(targetType, targetId);
        if (ownerId) {
          let violationType: ViolationType;
          let severity: ViolationSeverity;

          switch (reason) {
            case ReportReason.SPAM:
            case ReportReason.COMMERCIAL_SPAM:
              violationType = ViolationType.SPAM_POSTING;
              severity = ViolationSeverity.MEDIUM;
              break;
            case ReportReason.INAPPROPRIATE_CONTENT:
              violationType = ViolationType.INAPPROPRIATE_CONTENT;
              severity = ViolationSeverity.HIGH;
              break;
            case ReportReason.HARASSMENT:
            case ReportReason.HATE_SPEECH:
              violationType = ViolationType.HARASSMENT;
              severity = ViolationSeverity.HIGH;
              break;
            case ReportReason.FAKE_INFORMATION:
              violationType = ViolationType.FAKE_INFORMATION;
              severity = ViolationSeverity.MEDIUM;
              break;
            default:
              violationType = ViolationType.INAPPROPRIATE_CONTENT;
              severity = ViolationSeverity.LOW;
          }

          await this.addViolationRecord(
            ownerId,
            violationType,
            severity,
            `Ë§áÊï∞„ÅÆÈÄöÂ†±„Å´„Çà„ÇäËá™ÂãïÊ§úÂá∫: ${reason}`,
            undefined,
            undefined,
            true,
          );
        }
      }
    } catch (error) {
      console.error('Failed to process auto detection:', error);
    }
  }

  /**
   * ÂØæË±°„Å∏„ÅÆÈÄöÂ†±Êï∞„ÇíÂèñÂæó
   */
  private async getReportCountForTarget(
    targetType: ReportTargetType,
    targetId: string,
  ): Promise<number> {
    try {
      const snapshot = await this.reportsCollection
        .where('targetType', '==', targetType)
        .where('targetId', '==', targetId)
        .where('status', '!=', ReportStatus.DISMISSED)
        .get();

      return snapshot.size;
    } catch (error) {
      console.error('Failed to get report count:', error);
      return 0;
    }
  }

  /**
   * ÂØæË±°„ÅÆ„Ç™„Éº„Éä„ÉºID„ÇíÂèñÂæó
   */
  private async getTargetOwnerId(
    targetType: ReportTargetType,
    targetId: string,
  ): Promise<string | null> {
    try {
      let collection: string;
      switch (targetType) {
        case ReportTargetType.TOILET:
          collection = 'toilets';
          break;
        case ReportTargetType.REVIEW:
          collection = 'reviews';
          break;
        case ReportTargetType.COMMENT:
          collection = 'comments';
          break;
        case ReportTargetType.USER:
          return targetId; // „É¶„Éº„Ç∂„ÉºËá™Ë∫´
        default:
          return null;
      }

      const doc = await firestore().collection(collection).doc(targetId).get();

      if (doc.exists()) {
        const data = doc.data()!;
        return data.userId || data.authorId || null;
      }

      return null;
    } catch (error) {
      console.error('Failed to get target owner ID:', error);
      return null;
    }
  }

  /**
   * ÈÅïÂèç„Éù„Ç§„É≥„Éà„ÇíÂèñÂæó
   */
  private getViolationPoints(severity: ViolationSeverity): number {
    switch (severity) {
      case ViolationSeverity.LOW:
        return 1;
      case ViolationSeverity.MEDIUM:
        return 3;
      case ViolationSeverity.HIGH:
        return 6;
      case ViolationSeverity.CRITICAL:
        return 12;
      default:
        return 1;
    }
  }

  /**
   * „É¶„Éº„Ç∂„Éº„ÅåÂà∂Èôê„Åï„Çå„Å¶„ÅÑ„Çã„Åã„ÉÅ„Çß„ÉÉ„ÇØ
   */
  async isUserRestricted(
    userId: string,
    action: 'post' | 'comment' | 'review' | 'vote',
  ): Promise<{ restricted: boolean; reason?: string; endDate?: Date }> {
    try {
      const restrictions = await this.getUserRestrictions(userId);

      for (const restriction of restrictions) {
        let isApplicable = false;

        switch (action) {
          case 'post':
            isApplicable = [
              RestrictionType.POST_RESTRICTION,
              RestrictionType.TEMPORARY_BAN,
              RestrictionType.PERMANENT_BAN,
            ].includes(restriction.type);
            break;
          case 'comment':
            isApplicable = [
              RestrictionType.COMMENT_RESTRICTION,
              RestrictionType.TEMPORARY_BAN,
              RestrictionType.PERMANENT_BAN,
            ].includes(restriction.type);
            break;
          case 'review':
            isApplicable = [
              RestrictionType.REVIEW_RESTRICTION,
              RestrictionType.TEMPORARY_BAN,
              RestrictionType.PERMANENT_BAN,
            ].includes(restriction.type);
            break;
          case 'vote':
            isApplicable = [
              RestrictionType.VOTE_RESTRICTION,
              RestrictionType.TEMPORARY_BAN,
              RestrictionType.PERMANENT_BAN,
            ].includes(restriction.type);
            break;
        }

        if (isApplicable) {
          return {
            restricted: true,
            reason: restriction.reason,
            endDate: restriction.endDate,
          };
        }
      }

      return { restricted: false };
    } catch (error) {
      console.error('Failed to check user restriction:', error);
      return { restricted: false };
    }
  }
}

// „Ç∑„É≥„Ç∞„É´„Éà„É≥„Ç§„É≥„Çπ„Çø„É≥„Çπ
export const reportService = new ReportService();

import firestore from '@react-native-firebase/firestore';
import { ToiletLocation } from '../types/maps';
import { SearchFilters, SearchResult, SortOption, SavedSearch, SearchHistory } from '../types/post';

export class SearchService {
  private toiletsCollection = firestore().collection('toilets');
  private savedSearchesCollection = firestore().collection('saved_searches');
  private searchHistoryCollection = firestore().collection('search_history');

  /**
   * „Éà„Ç§„É¨„ÇíÊ§úÁ¥¢
   */
  async searchToilets(
    filters: SearchFilters,
    userLocation?: { latitude: number; longitude: number },
    limit: number = 20,
    offset: number = 0,
  ): Promise<SearchResult> {
    const startTime = Date.now();

    try {
      let query = this.toiletsCollection.where('isActive', '==', true);

      // „ÉÜ„Ç≠„Çπ„ÉàÊ§úÁ¥¢Ôºà„Çø„Ç§„Éà„É´„Å®Ë™¨ÊòéÔºâ
      if (filters.query.trim()) {
        // Firestore„ÅÆÂà∂Èôê„Å´„Çà„Çä„ÄÅ„ÇØ„É©„Ç§„Ç¢„É≥„Éà„Çµ„Ç§„Éâ„Åß„Éï„Ç£„É´„Çø„É™„É≥„Ç∞
        // ÂÆüÈöõ„ÅÆÂÆüË£Ö„Åß„ÅØ Algolia „ÇÑ ElasticSearch „ÇíÊé®Â•®
        const allToilets = await this.getAllActiveToilets();
        const filteredToilets = this.filterByText(allToilets, filters.query);
        const finalResults = this.applyFilters(filteredToilets, filters, userLocation);
        const sortedResults = this.sortResults(finalResults, filters.sortBy, userLocation);

        const paginatedResults = sortedResults.slice(offset, offset + limit);

        return {
          toilets: paginatedResults,
          totalCount: sortedResults.length,
          hasMore: offset + limit < sortedResults.length,
          searchTime: Date.now() - startTime,
        };
      }

      // „Éï„Ç£„É´„Çø„ÉºÊù°‰ª∂„ÅÆÈÅ©Áî®
      if (filters.toiletType) {
        query = query.where('type', '==', filters.toiletType);
      }

      if (filters.isAccessible !== undefined) {
        query = query.where('isAccessible', '==', filters.isAccessible);
      }

      if (filters.rating !== undefined) {
        query = query.where('rating', '>=', filters.rating);
      }

      // „Éá„Éº„ÇøÂèñÂæó
      let snapshot;
      switch (filters.sortBy) {
        case SortOption.RATING:
          snapshot = await query
            .orderBy('rating', 'desc')
            .limit(limit + offset)
            .get();
          break;
        case SortOption.NEWEST:
          snapshot = await query
            .orderBy('createdAt', 'desc')
            .limit(limit + offset)
            .get();
          break;
        case SortOption.HELPFUL:
          snapshot = await query
            .orderBy('reviewCount', 'desc')
            .limit(limit + offset)
            .get();
          break;
        default:
          snapshot = await query.limit(limit + offset).get();
      }

      const allResults: ToiletLocation[] = [];
      snapshot.forEach(doc => {
        const data = doc.data();
        allResults.push({
          id: data.id,
          title: data.title,
          description: data.description,
          latitude: data.location.latitude,
          longitude: data.location.longitude,
          type: data.type,
          isAccessible: data.isAccessible,
          rating: data.rating,
          reviewCount: data.reviewCount,
          createdBy: data.createdBy,
          createdAt: data.createdAt.toDate(),
          updatedAt: data.updatedAt.toDate(),
        });
      });

      // ËøΩÂä†„Éï„Ç£„É´„Çø„É™„É≥„Ç∞
      const filteredResults = this.applyAdditionalFilters(allResults, filters, userLocation);

      // Ë∑ùÈõ¢„ÇΩ„Éº„Éà„ÅÆÂ†¥Âêà„ÅØ„ÇØ„É©„Ç§„Ç¢„É≥„Éà„Çµ„Ç§„Éâ„ÅßÂá¶ÁêÜ
      const sortedResults =
        filters.sortBy === SortOption.DISTANCE && userLocation
          ? this.sortByDistance(filteredResults, userLocation)
          : filteredResults;

      const paginatedResults = sortedResults.slice(offset, offset + limit);

      return {
        toilets: paginatedResults,
        totalCount: sortedResults.length,
        hasMore: offset + limit < sortedResults.length,
        searchTime: Date.now() - startTime,
      };
    } catch (error) {
      console.error('Failed to search toilets:', error);
      return {
        toilets: [],
        totalCount: 0,
        hasMore: false,
        searchTime: Date.now() - startTime,
      };
    }
  }

  /**
   * ÂÖ®„Å¶„ÅÆ„Ç¢„ÇØ„ÉÜ„Ç£„Éñ„Å™„Éà„Ç§„É¨„ÇíÂèñÂæóÔºà„ÉÜ„Ç≠„Çπ„ÉàÊ§úÁ¥¢Áî®Ôºâ
   */
  private async getAllActiveToilets(): Promise<ToiletLocation[]> {
    const snapshot = await this.toiletsCollection.where('isActive', '==', true).get();

    const toilets: ToiletLocation[] = [];
    snapshot.forEach(doc => {
      const data = doc.data();
      toilets.push({
        id: data.id,
        title: data.title,
        description: data.description,
        latitude: data.location.latitude,
        longitude: data.location.longitude,
        type: data.type,
        isAccessible: data.isAccessible,
        rating: data.rating,
        reviewCount: data.reviewCount,
        createdBy: data.createdBy,
        createdAt: data.createdAt.toDate(),
        updatedAt: data.updatedAt.toDate(),
      });
    });

    return toilets;
  }

  /**
   * „ÉÜ„Ç≠„Çπ„Éà„Å´„Çà„Çã„Éï„Ç£„É´„Çø„É™„É≥„Ç∞
   */
  private filterByText(toilets: ToiletLocation[], query: string): ToiletLocation[] {
    const lowerQuery = query.toLowerCase();
    return toilets.filter(
      toilet =>
        toilet.title.toLowerCase().includes(lowerQuery) ||
        (toilet.description && toilet.description.toLowerCase().includes(lowerQuery)),
    );
  }

  /**
   * „Éï„Ç£„É´„Çø„ÉºÊù°‰ª∂„ÅÆÈÅ©Áî®
   */
  private applyFilters(
    toilets: ToiletLocation[],
    filters: SearchFilters,
    userLocation?: { latitude: number; longitude: number },
  ): ToiletLocation[] {
    return toilets.filter(toilet => {
      // „Éà„Ç§„É¨„Çø„Ç§„Éó
      if (filters.toiletType && toilet.type !== filters.toiletType) {
        return false;
      }

      // „Éê„É™„Ç¢„Éï„É™„Éº
      if (filters.isAccessible !== undefined && toilet.isAccessible !== filters.isAccessible) {
        return false;
      }

      // Ë©ï‰æ°
      if (filters.rating !== undefined && toilet.rating && toilet.rating < filters.rating) {
        return false;
      }

      // Ë∑ùÈõ¢
      if (filters.distance && userLocation) {
        const distance = this.calculateDistance(
          userLocation.latitude,
          userLocation.longitude,
          toilet.latitude,
          toilet.longitude,
        );
        if (distance > filters.distance) {
          return false;
        }
      }

      return true;
    });
  }

  /**
   * ËøΩÂä†„Éï„Ç£„É´„Çø„Éº„ÅÆÈÅ©Áî®
   */
  private applyAdditionalFilters(
    toilets: ToiletLocation[],
    filters: SearchFilters,
    userLocation?: { latitude: number; longitude: number },
  ): ToiletLocation[] {
    return toilets.filter(toilet => {
      // Ë∑ùÈõ¢„Éï„Ç£„É´„Çø„Éº
      if (filters.distance && userLocation) {
        const distance = this.calculateDistance(
          userLocation.latitude,
          userLocation.longitude,
          toilet.latitude,
          toilet.longitude,
        );
        if (distance > filters.distance) {
          return false;
        }
      }

      // Âñ∂Ê•≠ÊôÇÈñì„Éï„Ç£„É´„Çø„ÉºÔºàÁ∞°Áï•ÂåñÔºâ
      if (filters.openNow) {
        // ÂÆüÈöõ„ÅÆÂÆüË£Ö„Åß„ÅØÂñ∂Ê•≠ÊôÇÈñì„Éá„Éº„Çø„Çí‰ΩøÁî®
        return true; // Â∏∏„Å´Âñ∂Ê•≠‰∏≠„Å®„Åø„Å™„Åô
      }

      return true;
    });
  }

  /**
   * ÁµêÊûú„ÅÆ„ÇΩ„Éº„Éà
   */
  private sortResults(
    toilets: ToiletLocation[],
    sortBy: SortOption,
    userLocation?: { latitude: number; longitude: number },
  ): ToiletLocation[] {
    switch (sortBy) {
      case SortOption.DISTANCE:
        return userLocation ? this.sortByDistance(toilets, userLocation) : toilets;

      case SortOption.RATING:
        return toilets.sort((a, b) => (b.rating || 0) - (a.rating || 0));

      case SortOption.NEWEST:
        return toilets.sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());

      case SortOption.HELPFUL:
        return toilets.sort((a, b) => (b.reviewCount || 0) - (a.reviewCount || 0));

      case SortOption.RELEVANCE:
      default:
        return toilets; // Èñ¢ÈÄ£Â∫¶„ÇΩ„Éº„Éà„ÅØÊ§úÁ¥¢„Ç®„É≥„Ç∏„É≥„Å´‰æùÂ≠ò
    }
  }

  /**
   * Ë∑ùÈõ¢„Åß„ÇΩ„Éº„Éà
   */
  private sortByDistance(
    toilets: ToiletLocation[],
    userLocation: { latitude: number; longitude: number },
  ): ToiletLocation[] {
    return toilets.sort((a, b) => {
      const distanceA = this.calculateDistance(
        userLocation.latitude,
        userLocation.longitude,
        a.latitude,
        a.longitude,
      );
      const distanceB = this.calculateDistance(
        userLocation.latitude,
        userLocation.longitude,
        b.latitude,
        b.longitude,
      );
      return distanceA - distanceB;
    });
  }

  /**
   * Ë∑ùÈõ¢Ë®àÁÆóÔºà„Éè„Éº„Éê„Ç§„É≥ÂÖ¨ÂºèÔºâ
   */
  private calculateDistance(lat1: number, lon1: number, lat2: number, lon2: number): number {
    const R = 6371; // Âú∞ÁêÉ„ÅÆÂçäÂæÑ (km)
    const dLat = this.toRadians(lat2 - lat1);
    const dLon = this.toRadians(lon2 - lon1);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(this.toRadians(lat1)) *
        Math.cos(this.toRadians(lat2)) *
        Math.sin(dLon / 2) *
        Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  }

  private toRadians(degrees: number): number {
    return degrees * (Math.PI / 180);
  }

  /**
   * Ê§úÁ¥¢Â±•Ê≠¥„Çí‰øùÂ≠ò
   */
  async saveSearchHistory(
    userId: string,
    filters: SearchFilters,
    resultCount: number,
  ): Promise<void> {
    try {
      const historyData = {
        id: this.searchHistoryCollection.doc().id,
        userId,
        query: filters.query,
        filters,
        resultCount,
        searchedAt: firestore.Timestamp.now(),
      };

      await this.searchHistoryCollection.add(historyData);

      // Âè§„ÅÑÂ±•Ê≠¥„ÇíÂâäÈô§ÔºàÊúÄÊñ∞50‰ª∂„ÅÆ„Åø‰øùÊåÅÔºâ
      const allHistory = await this.searchHistoryCollection
        .where('userId', '==', userId)
        .orderBy('searchedAt', 'desc')
        .get();

      if (allHistory.docs.length > 50) {
        const batch = firestore().batch();
        allHistory.docs.slice(50).forEach((doc: any) => {
          batch.delete(doc.ref);
        });
        await batch.commit();
      }
    } catch (error) {
      console.error('Failed to save search history:', error);
    }
  }

  /**
   * Ê§úÁ¥¢Â±•Ê≠¥„ÇíÂèñÂæó
   */
  async getSearchHistory(userId: string, limit: number = 10): Promise<SearchHistory[]> {
    try {
      const snapshot = await this.searchHistoryCollection
        .where('userId', '==', userId)
        .orderBy('searchedAt', 'desc')
        .limit(limit)
        .get();

      const history: SearchHistory[] = [];
      snapshot.forEach(doc => {
        const data = doc.data();
        history.push({
          id: data.id,
          userId: data.userId,
          query: data.query,
          filters: data.filters,
          resultCount: data.resultCount,
          searchedAt: data.searchedAt.toDate(),
        });
      });

      return history;
    } catch (error) {
      console.error('Failed to get search history:', error);
      return [];
    }
  }

  /**
   * ‰øùÂ≠ò„Åï„Çå„ÅüÊ§úÁ¥¢Êù°‰ª∂„Çí‰ΩúÊàê
   */
  async saveFavoriteSearch(userId: string, name: string, filters: SearchFilters): Promise<void> {
    try {
      const savedSearchData = {
        id: this.savedSearchesCollection.doc().id,
        userId,
        name,
        filters,
        createdAt: firestore.Timestamp.now(),
        lastUsed: firestore.Timestamp.now(),
      };

      await this.savedSearchesCollection.add(savedSearchData);
    } catch (error) {
      console.error('Failed to save favorite search:', error);
      throw new Error('Ê§úÁ¥¢Êù°‰ª∂„ÅÆ‰øùÂ≠ò„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    }
  }

  /**
   * ‰øùÂ≠ò„Åï„Çå„ÅüÊ§úÁ¥¢Êù°‰ª∂„ÇíÂèñÂæó
   */
  async getSavedSearches(userId: string): Promise<SavedSearch[]> {
    try {
      const snapshot = await this.savedSearchesCollection
        .where('userId', '==', userId)
        .orderBy('lastUsed', 'desc')
        .get();

      const searches: SavedSearch[] = [];
      snapshot.forEach(doc => {
        const data = doc.data();
        searches.push({
          id: data.id,
          userId: data.userId,
          name: data.name,
          filters: data.filters,
          createdAt: data.createdAt.toDate(),
          lastUsed: data.lastUsed.toDate(),
        });
      });

      return searches;
    } catch (error) {
      console.error('Failed to get saved searches:', error);
      return [];
    }
  }

  /**
   * ‰øùÂ≠ò„Åï„Çå„ÅüÊ§úÁ¥¢Êù°‰ª∂„ÇíÂâäÈô§
   */
  async deleteSavedSearch(searchId: string): Promise<void> {
    try {
      await this.savedSearchesCollection.doc(searchId).delete();
    } catch (error) {
      console.error('Failed to delete saved search:', error);
      throw new Error('Ê§úÁ¥¢Êù°‰ª∂„ÅÆÂâäÈô§„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    }
  }
}

