import { create } from 'zustand';
import { authService, User } from '../services/authService';

interface AuthState {
  // Áä∂ÊÖã
  user: User | null;
  isLoading: boolean;
  isInitialized: boolean;
  error: string | null;

  // „Ç¢„ÇØ„Ç∑„Éß„É≥
  signInWithGoogle: () => Promise<void>;
  signOut: () => Promise<void>;
  deleteAccount: () => Promise<void>;
  clearError: () => void;
  initialize: () => void;
}

export const useAuthStore = create<AuthState>((set, get) => ({
  // ÂàùÊúüÁä∂ÊÖã
  user: null,
  isLoading: false,
  isInitialized: false,
  error: null,

  // Google„Çµ„Ç§„É≥„Ç§„É≥
  signInWithGoogle: async () => {
    set({ isLoading: true, error: null });
    try {
      const user = await authService.signInWithGoogle();
      set({ user, isLoading: false });
    } catch (error) {
      set({
        error: error instanceof Error ? error.message : '„Çµ„Ç§„É≥„Ç§„É≥„Å´Â§±Êïó„Åó„Åæ„Åó„Åü',
        isLoading: false,
      });
    }
  },

  // „Çµ„Ç§„É≥„Ç¢„Ç¶„Éà
  signOut: async () => {
    set({ isLoading: true, error: null });
    try {
      await authService.signOut();
      set({ user: null, isLoading: false });
    } catch (error) {
      set({
        error: error instanceof Error ? error.message : '„Çµ„Ç§„É≥„Ç¢„Ç¶„Éà„Å´Â§±Êïó„Åó„Åæ„Åó„Åü',
        isLoading: false,
      });
    }
  },

  // „Ç¢„Ç´„Ç¶„É≥„ÉàÂâäÈô§
  deleteAccount: async () => {
    set({ isLoading: true, error: null });
    try {
      await authService.deleteAccount();
      set({ user: null, isLoading: false });
    } catch (error) {
      set({
        error: error instanceof Error ? error.message : '„Ç¢„Ç´„Ç¶„É≥„ÉàÂâäÈô§„Å´Â§±Êïó„Åó„Åæ„Åó„Åü',
        isLoading: false,
      });
    }
  },

  // „Ç®„É©„Éº„ÇØ„É™„Ç¢
  clearError: () => {
    set({ error: null });
  },

  // Ë™çË®ºÁä∂ÊÖã„ÅÆÂàùÊúüÂåñ
  initialize: () => {
    try {
      console.log('authStore: ÂàùÊúüÂåñÈñãÂßã');
      // ÁèæÂú®„ÅÆ„É¶„Éº„Ç∂„Éº„ÇíË®≠ÂÆöÔºà„Ç®„É©„Éº„Éè„É≥„Éâ„É™„É≥„Ç∞‰ªò„ÅçÔºâ
      let currentUser = null;
      try {
        currentUser = authService.getCurrentUser();
      } catch (error) {
        console.log('authStore: getCurrentUser „Åß„Ç®„É©„ÉºÔºà„Çπ„Ç≠„ÉÉ„ÉóÔºâ:', error);
      }

      set({ user: currentUser, isInitialized: true });
      console.log('authStore: ÂàùÊúüÂåñÂÆå‰∫Ü', { user: currentUser });

      // Ë™çË®ºÁä∂ÊÖãÂ§âÊõ¥„ÅÆÁõ£Ë¶ñ„ÇíÈñãÂßãÔºà„Ç®„É©„Éº„Éè„É≥„Éâ„É™„É≥„Ç∞‰ªò„ÅçÔºâ
      try {
        authService.onAuthStateChanged(user => {
          console.log('authStore: Ë™çË®ºÁä∂ÊÖãÂ§âÊõ¥:', user);
          set({ user });
        });
      } catch (error) {
        console.log('authStore: onAuthStateChanged „Åß„Ç®„É©„ÉºÔºà„Çπ„Ç≠„ÉÉ„ÉóÔºâ:', error);
      }
    } catch (error) {
      console.error('authStore: ÂàùÊúüÂåñ„Åß„Ç®„É©„Éº:', error);
      // „Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Å¶„ÇÇÂàùÊúüÂåñÂÆå‰∫Ü„Å®„Åô„Çã
      set({ user: null, isInitialized: true });
    }
  },
}));

// Ë™çË®ºÁä∂ÊÖã„ÅÆ‰æøÂà©„Å™„Çª„É¨„ÇØ„Çø„Éº
export const useAuth = () => {
  const { user, isLoading, error, isInitialized } = useAuthStore();
  return {
    user,
    isLoading: !isInitialized || isLoading, // ÂàùÊúüÂåñ‰∏≠„ÇÇ„É≠„Éº„Éá„Ç£„É≥„Ç∞Êâ±„ÅÑ
    error,
    isAuthenticated: !!user,
  };
};

export const useAuthActions = () => {
  const { signInWithGoogle, signOut, deleteAccount, clearError } = useAuthStore();
  return {
    signInWithGoogle,
    signOut,
    deleteAccount,
    clearError,
  };
};

