// „Éê„É™„Éá„Éº„Ç∑„Éß„É≥Èñ¢Êï∞
export const validateToiletPost = (
  form: ToiletPostForm,
): { isValid: boolean; errors: string[] } => {
  const errors: string[] = [];

  // ÊñΩË®≠ÊÉÖÂ†±„ÅÆÂøÖÈ†à„Éï„Ç£„Éº„É´„Éâ„ÅÆ„ÉÅ„Çß„ÉÉ„ÇØ
  if (!form.facilityTitle.trim()) {
    errors.push('ÊñΩË®≠Âêç„ÅØÂøÖÈ†à„Åß„Åô');
  }

  if (form.facilityTitle.length > 100) {
    errors.push('ÊñΩË®≠Âêç„ÅØ100ÊñáÂ≠ó‰ª•ÂÜÖ„ÅßÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
  }

  if (!form.location) {
    errors.push('‰ΩçÁΩÆÊÉÖÂ†±„ÇíË®≠ÂÆö„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
  }

  if (form.facilityDescription.length > 500) {
    errors.push('ÊñΩË®≠Ë™¨Êòé„ÅØ500ÊñáÂ≠ó‰ª•ÂÜÖ„ÅßÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
  }

  if (form.additionalInfo.length > 300) {
    errors.push('ËøΩÂä†ÊÉÖÂ†±„ÅØ300ÊñáÂ≠ó‰ª•ÂÜÖ„ÅßÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
  }

  // Âñ∂Ê•≠ÊôÇÈñì„ÅÆ„ÉÅ„Çß„ÉÉ„ÇØ
  if (!form.openingHours.is24Hours) {
    if (!form.openingHours.openTime || !form.openingHours.closeTime) {
      errors.push('Âñ∂Ê•≠ÊôÇÈñì„ÇíË®≠ÂÆö„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
    }
  }

  // „Éà„Ç§„É¨ÊÉÖÂ†±„ÅÆ„ÉÅ„Çß„ÉÉ„ÇØ
  if (form.toilets.length === 0) {
    errors.push('Â∞ë„Å™„Åè„Å®„ÇÇ1„Å§„ÅÆ„Éà„Ç§„É¨ÊÉÖÂ†±„ÇíËøΩÂä†„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
  }

  form.toilets.forEach((toilet, index) => {
    if (!toilet.title.trim()) {
      errors.push(`„Éà„Ç§„É¨${index + 1}: „Çø„Ç§„Éà„É´„ÅØÂøÖÈ†à„Åß„Åô`);
    }

    if (toilet.title.length > 50) {
      errors.push(`„Éà„Ç§„É¨${index + 1}: „Çø„Ç§„Éà„É´„ÅØ50ÊñáÂ≠ó‰ª•ÂÜÖ„ÅßÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ`);
    }

    if (toilet.description.length > 500) {
      errors.push(`„Éà„Ç§„É¨${index + 1}: Ë™¨Êòé„ÅØ500ÊñáÂ≠ó‰ª•ÂÜÖ„ÅßÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ`);
    }
  });

  return {
    isValid: errors.length === 0,
    errors,
  };
};

// ÁîªÂÉè„ÅÆ„Éê„É™„Éá„Éº„Ç∑„Éß„É≥
export const validateImages = (images: string[]): { isValid: boolean; errors: string[] } => {
  const errors: string[] = [];

  if (images.length > 5) {
    errors.push('ÁîªÂÉè„ÅØ5Êûö„Åæ„ÅßÊäïÁ®øÂèØËÉΩ„Åß„Åô');
  }

  return {
    isValid: errors.length === 0,
    errors,
  };
};

